if (folderCount > 1)
{MessageBox.Show($"åœ¨æœ¬åœ°è§¦å‘å™¨æ‰¾åˆ° {folderCount} ä¸ª ç©ºå¤©å§¬ MissFisher æˆ– é£Žç­çŒ« Gaelicat\r\nè¯·ç§»é™¤å¤šä½™çš„è§¦å‘å™¨", "é”™è¯¯", MessageBoxButtons.OK, MessageBoxIcon.Error);}
trv.Refresh();
void Search(TreeNode node)
{foreach (TreeNode child in node.Nodes)
{if (child.Tag is Folder f)
{if (f.Name.Contains("é£Žç­çŒ« ") || f.Name.Contains("ç©ºå¤©å§¬ ") || f.Name.Contains("MissFisher"))
{folderCount++; // å¢žåŠ è®¡æ•°
}
Search(child);
}
else if (child.Tag is Repository r)
{Search(child);
}
}}
