using static Triggernometry.Interpreter.StaticHelpers;
using static Triggernometry.RealPlugin;
bool initialized = false;
int i = 1;
for (i = 1; i <= 5; i++)
{try
{MissFisherExtension.Init();
initialized = true;
break;
}
catch
{Interpreter.StaticHelpers.Log(
DebugLevelEnum.Warning,
$"空天姬扩展初始化失败，正在重试第 {i} / 5 次……"
);
if (i == 5)
throw;
}
Thread.Sleep(i * 1000);}
public static class MissFisherExtension
{private static readonly string envlang = "${_ffxivlanguage}";
private static readonly int _lang = int.TryParse(
GetDictVariable(true, "空天姬配置")?.GetValue("界面语言")?.ToString(),
out int l
)
? l
: (
envlang == "CN" ? 1
: envlang == "JP" ? 3
: 2
);
private static readonly string Name = "MissFisherExtension";
public static PostNamazu.PostNamazu NamazuPlugin =>
(PostNamazu.PostNamazu)RealPlugin.InstanceHook(null, "PostNamazu.PostNamazu").pluginObj;
public static ExternalProcessMemory Memory => NamazuPlugin?.Memory;
public static SigScanner Scanner => NamazuPlugin?.SigScanner;
private static PostNamazuUi _postNamazuUi;
public static bool IsCN => !Triggernometry.FFXIV.Entity.GetMyself().Name.Contains(" ");
public static PostNamazuUi PostNamazuUi
{get
{if (_postNamazuUi?.Parent as TabPage == null)
{_postNamazuUi = GetPostNamazuUi();
}
return _postNamazuUi;
}
}
public static bool IsReady =>
Triggernometry.Interpreter.StaticHelpers.XivProcess != null && Memory != null;
public static bool IsValidPtr(IntPtr? ptr) => ptr.HasValue && ptr.Value != IntPtr.Zero;
private static PostNamazuUi GetPostNamazuUi()
{TabPage tp = RealPlugin.InstanceHook(null, "PostNamazu.PostNamazu").TabPage;
return tp.Controls.OfType<PostNamazuUi>().FirstOrDefault();
}
public static void OnXivProcUpdate()
{NamazuUiLog($"空天姬初始化：开始扫描内存~");
ScanSignatures();
}
public static void RegisterCallback(
string callBackName,
Action<string> callBackAction,
params IntPtr?[] ptrs
)
{if (ptrs == null || ptrs.Length == 0 || ptrs.Any(p => !IsValidPtr(p)))
{Log(
RealPlugin.DebugLevelEnum.Error,
$"注册回调 {callBackName} 失败：指针无效 (ptr == IntPtr.Zero)"
);
return;
}
Triggernometry.RealPlugin.plug.RegisterNamedCallback(
callBackName,
new Action<object, string>((_, cmd) => callBackAction(cmd)),
null,
registrant: Name
);
}
public static void Init()
{OnXivProcUpdate();
Triggernometry.Interpreter.StaticHelpers.RegisterXivProcessUpdatedAction(
Name,
new System.Action(OnXivProcUpdate)
);
RegisterCallback("KTJChangeBaitAuto", FishingHelper.ChangeBaitAuto, ExecuteCommandPtr);
RegisterCallback("KTJChangeBaitById", FishingHelper.ChangeBaitById, ExecuteCommandPtr);
RegisterCallback(
"KTJChangeSwimBaitByIndex",
FishingHelper.ChangeSwimBaitByIndex,
ExecuteCommandPtr
);
RegisterCallback(
"KTJGetCurrentBait",
FishingHelper.GetCurrentBait,
PlayerStatePtr,
FishManagerPtr
);
RegisterCallback(
"KTJGetCurrentWeather",
FishingHelper.GetCurrentWeather,
WeatherManagerPtr
);
RegisterCallback("KTJUseItem", FishingHelper.UseItem, ActionManagerPtr, UseActionPtr);
RegisterCallback(
"KTJObtainOceanFishing",
FishingHelper.ObtainOceanFishing,
EventFrameworkPtr,
GetInstanceContentDirectorPtr
);
if (!IsCN)
{
RegisterCallback("KTJObtainWKS", FishingHelper.ObtainWKS, WKSManager);
}
if (FishingHelper.CanChangeBait())
{if (string.IsNullOrEmpty(GetScalarVariable(false, "空天姬控制海钓提醒")))
Echo(
_lang == 1
? $"已激活: 空天姬内存方法\r\n{Name} v0.18 By Nyy"
: $"Activated: MissFisherMemoryComponents\r\n{Name} v0.18 By Nyy"
);
}
else
{Echo($"");
Echo(_lang == 1 ? $"{Name} 初始化失败！" : $"{Name} initialization failed!");
}
}
private static void CheckBeforeExecution()
{if (!IsReady)
throw new Exception("KTJCheckBeforeExecution 没有对应的 FFXIV 进程");
}
public static IntPtr ExecuteCommandPtr;
public static IntPtr PlayerStatePtr;
public static IntPtr WeatherManagerPtr;
public static IntPtr ActionManagerPtr;
public static IntPtr GameMainPtr;
public static IntPtr UseActionPtr;
public static IntPtr EventFrameworkPtr;
public static IntPtr FishManagerPtr;
public static IntPtr WKSManager;
public static IntPtr GetInstanceContentDirectorPtr;
public static void ScanSignatures()
{GameMainPtr = ScanSignatureWithFallback(
nameof(GameMainPtr),
"48 8D 0D * * * * 0F 28 F2 48 89 44 24 ??"
);
ExecuteCommandPtr = ScanSignatureWithFallback(
nameof(ExecuteCommandPtr),
"E8 * * * * 48 83 C4 ?? C3 CC CC CC CC CC CC CC CC CC CC CC CC 48 83 EC ?? 45 0F B6 C0",
"E8 * * * * 48 8B 06 48 8B CE FF 50 ? E9 ? ? ? ? 49 8B CC"
);
PlayerStatePtr = Scanner.ScanText(
"48 8D 0D * * * * E8 ?? ?? ?? ?? 84 C0 75 06 F6 43 18 02",
nameof(PlayerStatePtr)
);
WeatherManagerPtr = Scanner.ScanText(
"48 8D 0D * * * * 44 0F B7 45",
nameof(WeatherManagerPtr)
);
var eventFrameworkPtrPtr = ScanSignatureWithFallback(
nameof(EventFrameworkPtr),
"4C 39 2D * * * * 74 14"
);
EventFrameworkPtr = Memory.Read<IntPtr>(eventFrameworkPtrPtr);
FishManagerPtr = Memory.Read<IntPtr>(EventFrameworkPtr + 0x70);
GetInstanceContentDirectorPtr = ScanSignatureWithFallback(
nameof(GetInstanceContentDirectorPtr),
"E8 * * * * 0F B6 98"
);
ActionManagerPtr = ScanSignatureWithFallback(
nameof(ActionManagerPtr),
"48 8D 0D * * * * F3 0F 10 13",
"48 8D 0D * * * * 0F B7 46 18 "
);
UseActionPtr = ScanSignatureWithFallback(
nameof(UseActionPtr),
"E8 * * * * 41 89 9E ?? ?? ?? ?? EB 0B",
"E8 * * * * E9 ?? ?? ?? ?? 4C 89 64 24 ?? 49 8D 4F 20",
"E8 * * * * 33 C0 48 8B 5C 24 ?? 48 8B 6C 24 ?? 48 8B 74 24 ?? 48 8B 7C 24 ?? 48 83 C4 50"
);
if (!IsCN)
{var wksManagerPtrPtr = Scanner.GetStaticAddressFromSig(
"48 8B 7C 24 ?? 48 89 35 ?? ?? ?? ??",
8,
nameof(WKSManager)
);
WKSManager = Memory.Read<IntPtr>(wksManagerPtrPtr);
}
}
private static IntPtr ScanSignatureWithFallback(string name, params string[] patterns)
{Exception ex = null;
foreach (var pattern in patterns)
{try
{return Scanner.ScanText(pattern, name);
}
catch (Exception e)
{ex = e;
}
}
RealPlugin.plug.FilteredAddToLog(
RealPlugin.DebugLevelEnum.Warning,
$"ScanSignatureWithFallback: `{name}` 提供的 {patterns.Length} 条备用Sig均扫描失败/返回空指针。"
);
RealPlugin.plug.FilteredAddToLog(RealPlugin.DebugLevelEnum.Error, ex?.Message ?? "");
return IntPtr.Zero;
}
private static class FishingHelper
{public static bool CanChangeBait()
{return !(ExecuteCommandPtr == IntPtr.Zero);
}
public static void ChangeBaitAuto(string cmd)
{CheckBeforeExecution();
if (uint.TryParse(cmd, out var swimIndex) && swimIndex is >= 0 and < 3)
{ChangeSwimBaitByIndex(cmd);
}
else
{ChangeBaitById(cmd);
}
}
public static void ChangeBaitById(string cmd)
{CheckBeforeExecution();
if (uint.TryParse(cmd, out var baitId))
{NamazuUiLog($"KTJChangeBait: baitId = {baitId}");
Memory.CallInjected64<IntPtr>(ExecuteCommandPtr, 701, 4, (int)baitId, 0, 0);
}
}
public static void ChangeSwimBaitByIndex(string cmd)
{CheckBeforeExecution();
if (uint.TryParse(cmd, out var swimIndex) && swimIndex is >= 0 and < 3)
{NamazuUiLog($"KTJChangeSwimBait: swimIndex = {swimIndex}");
Memory.CallInjected64<IntPtr>(ExecuteCommandPtr, 701, 25, (int)swimIndex, 0, 0);
}
}
public static void GetCurrentBait(string cmd)
{CheckBeforeExecution();
var currentBaitId = Memory.Read<uint>(
PlayerStatePtr + MissFisherVersionHelper.PlayerStateCurrentBait()
);
var currentSelectedSwimIndex = Memory.Read<byte>(
FishManagerPtr + MissFisherVersionHelper.FishingManagerCurrentSelectedSwimBait()
);
var swimBaits = new[]
{Memory.Read<uint>(
FishManagerPtr + MissFisherVersionHelper.FishingManagerSwimBaitId1()
),
Memory.Read<uint>(
FishManagerPtr + MissFisherVersionHelper.FishingManagerSwimBaitId2()
),
Memory.Read<uint>(
FishManagerPtr + MissFisherVersionHelper.FishingManagerSwimBaitId3()
),
};
if (currentSelectedSwimIndex < 3)
{var switBait = swimBaits[currentSelectedSwimIndex];
if (switBait != 0)
{currentBaitId = switBait;
}
}
if (!IsCN)
{
var currentTerritoryIntendedUseId = Memory.Read<byte>(
GameMainPtr + MissFisherVersionHelper.GameMainCurrentTerritoryIntendedUseId()
);
if (currentTerritoryIntendedUseId == 60)
{var wksBaitId = Memory.Read<uint>(
WKSManager + MissFisherVersionHelper.WKSManagerCurrentCosmicBaitOffset()
);
if (wksBaitId != 0)
{currentBaitId = wksBaitId;
}
}
}
Triggernometry.Interpreter.StaticHelpers.SetScalarVariable(
isPersistent: false,
"空天姬控制钓饵",
currentBaitId
);
}
public static void GetCurrentWeather(string cmd)
{CheckBeforeExecution();
var currentWeather = Memory.Read<byte>(WeatherManagerPtr + 0x64);
Triggernometry.Interpreter.StaticHelpers.SetScalarVariable(
isPersistent: false,
"空天姬天气ID",
currentWeather
);
}
public static void UseItem(string cmd)
{CheckBeforeExecution();
if (!uint.TryParse(cmd, out var itemId))
return;
var assemblyLock = Memory.Executor.AssemblyLock;
var flag = false;
try
{Monitor.Enter(assemblyLock, ref flag);
if (
Memory.CallInjected64<bool>(
UseActionPtr,
ActionManagerPtr,
(int)2,
itemId,
(ulong)0xE000_0000,
(uint)0xFFFF,
(int)0,
(uint)0,
0
)
)
{NamazuUiLog($"KTJUseItem: itemId = {itemId}");
}
}
finally
{if (flag)
Monitor.Exit(assemblyLock);
}
}
public static void ObtainOceanFishing(string cmd)
{CheckBeforeExecution();
var directorPtr = Memory.CallInjected64<IntPtr>(
GetInstanceContentDirectorPtr,
EventFrameworkPtr
);
if (directorPtr == IntPtr.Zero)
{Interpreter.StaticHelpers.Log(
DebugLevelEnum.Error,
$"{Name}: ContentDirector获取失败，现在真在副本中吗？"
);
return;
}
var directorType = Memory.Read<ushort>(
directorPtr + MissFisherVersionHelper.InstanceContentDirectorType()
);
if (directorType != 0x10)
{Interpreter.StaticHelpers.Log(
DebugLevelEnum.Error,
$"{Name}: 现在真在海钓吗？({directorType})"
);
return;
}
var status = Memory.Read<uint>(
directorPtr + MissFisherVersionHelper.InstanceContentOceanFishingStatus()
);
var currentRoute = Memory.Read<uint>(
directorPtr + MissFisherVersionHelper.InstanceContentOceanFishingCurrentRoute()
);
var currentZone = Memory.Read<uint>(
directorPtr + MissFisherVersionHelper.InstanceContentOceanFishingCurrentZone()
);
var spectralCurrentActive = Memory.Read<bool>(
directorPtr + MissFisherVersionHelper.InstanceContentOceanSpectralCurrentActive()
);
var weatherId = Memory.Read<uint>(
directorPtr + MissFisherVersionHelper.InstanceContentOceanFishingWeatherId()
);
var contentTimeLeft = Memory.Read<float>(
directorPtr + MissFisherVersionHelper.InstanceContentOceanFishingContentTimeLeft()
);
var timeOffset = Memory.Read<uint>(
directorPtr + MissFisherVersionHelper.InstanceContentOceanFishingTimeOffset()
);
var timeLeft = contentTimeLeft - timeOffset;
var entsAt = DateTimeOffset.UtcNow.ToUnixTimeMilliseconds() + (int)timeLeft * 1000;
var playerAllPoint = Memory.Read<ushort>(
directorPtr + MissFisherVersionHelper.InstanceContentOceanPlayerAllPoint()
);
Triggernometry.Interpreter.StaticHelpers.SetScalarVariable(
isPersistent: false,
"空天姬控制海钓状态",
status
);
Triggernometry.Interpreter.StaticHelpers.SetScalarVariable(
isPersistent: false,
"空天姬控制海钓当前路线",
currentRoute
);
Triggernometry.Interpreter.StaticHelpers.SetScalarVariable(
isPersistent: false,
"空天姬控制海钓当前海域",
currentZone
);
Triggernometry.Interpreter.StaticHelpers.SetScalarVariable(
isPersistent: false,
"空天姬控制海钓是否幻海中",
spectralCurrentActive
);
Triggernometry.Interpreter.StaticHelpers.SetScalarVariable(
isPersistent: false,
"空天姬控制海钓当前天气ID",
weatherId
);
Triggernometry.Interpreter.StaticHelpers.SetScalarVariable(
isPersistent: false,
"空天姬控制海钓当前海域结束时间",
entsAt
);
Triggernometry.Interpreter.StaticHelpers.SetScalarVariable(
isPersistent: false,
"空天姬控制海钓结束时总分数",
playerAllPoint
);
return;
}
public static void ObtainWKS(string cmd)
{var currentMissionUnitId = Memory.Read<ushort>(
WKSManager + MissFisherVersionHelper.WKSManagerCurrentMissionUnitIdOffset()
);
Triggernometry.Interpreter.StaticHelpers.SetScalarVariable(
isPersistent: false,
"空天姬控制宇宙当前任务ID",
currentMissionUnitId == 0 ? null : currentMissionUnitId
);
}
}
public static void Echo(string msg) =>
RealPlugin.plug.InvokeNamedCallback("command", $"/e {msg}");
public static void NamazuUiLog(string msg) => PostNamazuUi?.Log($"{Name}: {msg}");}
public static class MissFisherVersionHelper
{public static bool IsCN => !Triggernometry.FFXIV.Entity.GetMyself().Name.Contains(" ");
public static int GameMainCurrentTerritoryIntendedUseId() => 0x40A4;
public static int PlayerStateCurrentBait() => IsCN ? 0x4A4 : 0x4AC;
public static int FishingManagerCurrentSelectedSwimBait() => IsCN ? 0x234 : 0x23C;
public static int FishingManagerSwimBaitId1() => IsCN ? 0x238 : 0x240;
public static int FishingManagerSwimBaitId2() => IsCN ? 0x23C : 0x244;
public static int FishingManagerSwimBaitId3() => IsCN ? 0x240 : 0x248;
public static int InstanceContentDirectorType() => IsCN ? 0xE1E : 0xE26;
public static int InstanceContentOceanFishingCurrentRoute() => IsCN ? 0x1E08 : 0x1E10;
public static int InstanceContentOceanFishingStatus() => IsCN ? 0x1E0C : 0x1E14;
public static int InstanceContentOceanFishingCurrentZone() => IsCN ? 0x1E10 : 0x1E18;
public static int InstanceContentOceanSpectralCurrentActive() => IsCN ? 0x1E20 : 0x1E28;
public static int InstanceContentOceanFishingWeatherId() => IsCN ? 0x1E1C : 0x1E24;
public static int InstanceContentOceanFishingContentTimeLeft() => IsCN ? 0xD40 : 0xD48;
public static int InstanceContentOceanFishingTimeOffset() => IsCN ? 0x1E18 : 0x1E20;
public static int InstanceContentOceanPlayerAllPoint() => IsCN ? 0x225C + 0x4 : 0x2264 + 0x4;
public static int WKSManagerCurrentMissionUnitIdOffset() => 0xC60;
public static int WKSManagerCurrentCosmicBaitOffset() => 0xC9C;}

