if (string.IsNullOrEmpty(GetScalarVariable(false, "空天姬控制海钓提醒")))
RealPlugin.plug.InvokeNamedCallback(
"command",
I18n.GetTranslationByIndex("方法激活默语", MissFisher.lang)
