Storage["KTJGetTranslationByIndex"] = new Func<string, int, string>(
(key, index) =>
{
string originalText = I18n.GetTranslationByIndex(key, index);
string processedText = originalText.Replace("￥", "$");
return processedText;
}
);
Storage["KTJGetStatusTag"] = new Func<int, string>(
(i) =>
{int _l =
int.TryParse(
GetDictVariable(true, "空天姬配置")?.GetValue("界面语言")?.ToString(),
out var l
)
&& l != 0
? l
: MissFisher.lang;
if (!int.TryParse(GetScalarVariable(false, "空天姬前置ID"), out var sid))
{return I18n.GetTranslationByIndex("未知状态文本", _l);
}
return I18n.GetTranslationByIndex(
!MissFisher.IsAprilFoolsDay()
? sid switch
{1 => "平钓文本",
2 => "撒饵文本",
3 => "耐心文本",
4 => "以小钓大文本",
5 => "游动饵以小钓大文本",
6 => "以小钓大II文本",
_ => "未知状态文本",
}
: sid switch
{1 => "平喵文本",
2 => "喵饵文本",
3 => "挠心文本",
4 => "以小肥大文本",
5 => "喵动饵以小肥大文本",
6 => "以小肥大II文本",
_ => "未知状态文本",
},
_l
);
}
);
Storage["KTJGetPresetBait"] = new Func<string, string>(
(preset) =>
{int l =
int.TryParse(
GetDictVariable(true, "空天姬配置")?.GetValue("界面语言")?.ToString(),
out var _l
)
&& _l != 0
? _l
: MissFisher.lang;
;
VariableList 钓饵名 = GetListVariable(true, "空天姬钓饵名") ?? new VariableList();
VariableList 钓饵ID = GetListVariable(true, "空天姬钓饵ID") ?? new VariableList();
int 单语言空间 = int.Parse("${env:单语言空间}");
int 单语言空间乘二 = 单语言空间 * 2;
int 水产品名起始索引 = 单语言空间 * (l - 1);
if (string.IsNullOrEmpty(preset))
{return "";
}
string pattern = @"（(.*?)）";
MatchCollection matches = Regex.Matches(preset, pattern, RegexOptions.Singleline);
List<string> result = new();
foreach (Match match in matches)
{if (match.Groups.Count > 1)
{string word = match.Groups[1].Value;
result.Add(word);
}
}
string[] 钓饵参数组 = result.ToArray();
List<string> 结果列表ID = new();
List<string> 结果列表鱼名 = new();
foreach (string 钓饵参数 in 钓饵参数组)
{if (钓饵名.IndexOf(钓饵参数) > 0)
{int 索引 = 钓饵名.IndexOf(钓饵参数);
if (索引 > 单语言空间 && 索引 < 单语言空间乘二)
{索引 -= 单语言空间;
}
else if (索引 > 单语言空间乘二)
{索引 -= 单语言空间乘二;
}
Variable 值 = 钓饵ID.Peek(索引);
结果列表ID.Add(值.ToString());
}
}
foreach (string 部分值 in 结果列表ID)
{if (int.TryParse(部分值, out int 数字))
{int 索引a = 钓饵ID.IndexOf(部分值);
if (索引a > 0)
{Variable 值 = 钓饵名.Peek(索引a + 水产品名起始索引);
结果列表鱼名.Add(值.ToString());
}
}
}
List<string> 结果列表去重复 = new(结果列表鱼名.Distinct().ToList());
List<string> 结果列表去重复加前缀 = new();
foreach (string 去重复名 in 结果列表去重复)
{结果列表去重复加前缀.Add("" + 去重复名);
}
string 所需钓饵 = string.Join(l != 2 ? "、" : ", ", 结果列表去重复加前缀);
if (string.IsNullOrEmpty(所需钓饵))
{return "";
}
else
{return I18n.GetTranslationByIndex("消息预设所需钓饵", l) + "\r" + 所需钓饵;
}
return "";
}
);
Storage["KTJGetFishEyesCordialResult"] = new Func<
long,
int,
long,
long,
int,
int,
int,
int,
int,
int,
string 
>(
(
当前时间戳,
强心剂可用,
强心剂上次使用时间,
强心剂冷却时长,
强心剂回复量,
当前GP,
自然回复量,
GP上限,
所需GP,
模式
) =>
{
bool 可用强心剂 = 强心剂可用 == 1;
long 剩余冷却时间 = 可用强心剂
? 1 
: Math.Max(强心剂上次使用时间 + 强心剂冷却时长 - 当前时间戳, 1);
int 冷却期间自然回复GP = 可用强心剂 ? 0 : (int)(剩余冷却时间 / 3000) * 自然回复量;
int GP目标值 = Math.Min(所需GP, GP上限 + 强心剂回复量);
bool 超出GP上限 = GP目标值 > GP上限;
bool 所需GP超过最大可获得GP = 所需GP > GP上限 + 强心剂回复量;
bool 强心剂使用后不溢出 = GP上限 >= 当前GP + 强心剂回复量 + 冷却期间自然回复GP;
int GP缺口 = Math.Max(所需GP - 当前GP, 0);
int 冷却后最大缺口 = Math.Max(GP上限 - (当前GP + 强心剂回复量 + 冷却期间自然回复GP), 0);
long 冷却后缺口自然回复时间 = (long)(冷却后最大缺口 * 3000.0 / 自然回复量);
long 最大GP缺口自然回复时间 = (long)(GP缺口 * 3000.0 / 自然回复量);
if (模式 == 0)
{return Math.Min(
超出GP上限 ? long.MaxValue : 最大GP缺口自然回复时间,
剩余冷却时间
+ Math.Max(
冷却后缺口自然回复时间,
(所需GP超过最大可获得GP && 强心剂使用后不溢出 ? 强心剂冷却时长 : 0)
)
)
.ToString();
}
else
{return (强心剂使用后不溢出 ? 剩余冷却时间 : 0).ToString();
}
}
);
public class MissFisher
{public static string language = "${_ffxivlanguage}";
public static bool isSnapshot = "${f:contain(Snapshot):${_triggerpath}}" == "1";
public static readonly int lang = int.TryParse(
GetDictVariable(true, "空天姬配置")?.GetValue("界面语言")?.ToString(),
out var l
)
? (l < 1 || l > 3 ? 获取游戏语言() : l)
: 获取游戏语言();
static int 获取游戏语言()
{return language == "CN" ? 1
: language == "JP" ? 3
: 2;
