bool 是国家公祭日 =
当前日期.Month == 国家公祭日.Month && 当前日期.Day == 国家公祭日.Day && _lang == 1;
bool 是愚人节 = 当前日期.Month == 4 && 当前日期.Day == 1;
int 周年数 = 当前日期.Year - 周年起始日期.Year;
if (当前日期 < 周年起始日期.AddYears(周年数))
{周年数--;
}
string 周年 = 转换为汉字(周年数);
if (当前日期.Month == 周年起始日期.Month && 当前日期.Day == 周年起始日期.Day)
{if (_lang == 1)
延时更新提示语($"空 天 姬 {周年数} 周 年 啦 ！");
else if (_lang == 3)
延时更新提示语($"「空天姫」の{周年数}周年記念！");
else
延时更新提示语($"MissFisher's {周年数}{GetOrdinalSuffix(周年数)} Anniversary!");
}
