void 初始化预设关联控件内容()
{字典 = GetDictVariable(true, "空天姬预设");
说明 =
_lang == 1
? 字典.GetValue("说明")?.ToString() ?? ""
: (
_lang == 3
? "プリセットの補完への貢献をしてくださった @Sectumsempra @Pa-paya! @Latihas の皆様に感謝します！\r\nプリセットテーブル："
: "Thanks to @Sectumsempra @Pa-paya! @Latihas and others for their contributions to the preset improvements!\r\nPreset Sheet:"
)
+ "https://docs.google.com/spreadsheets/d/1-1Xy7xlG_BRU2jJNkbjTL5Ijj8pGgNm6rHncHYiHguo/edit?usp=sharing";
赞助 = 字典.GetValue("赞助")?.ToString() ?? "";
预设 = 字典
.Values.Keys.Where(item =>
!string.IsNullOrEmpty(item)
&& !char.IsDigit(item[0])
&& !item.StartsWith("空天姬")
)
.Select(item => item.ToLower())
.ToList();
预设名2 = GetListVariable(true, "空天姬预设名2") ?? new VariableList();
预设名3 = GetListVariable(true, "空天姬预设名3") ?? new VariableList();
预设名4 = GetListVariable(true, "空天姬预设名4") ?? new VariableList();
设置提示(
输入鱼名,
获取翻译("输入提示")
+ Environment.NewLine
+ 说明.Replace("\\r\\n", Environment.NewLine)
);
更新输入鸣谢文本();
if (!string.IsNullOrEmpty(_lastN))
延时更新提示语(获取翻译("发现新版本1"));
}
void 设置控件关系()
{
空天姬主界面.Controls.Add(表格);
表格.ColumnCount = 4;
表格.RowCount = 7;
Graphics graphics = Graphics.FromHwnd(IntPtr.Zero);
float dpiX = graphics.DpiX;
float dpiY = graphics.DpiY;
graphics.Dispose();
float scaleFactor = dpiY / 96f;
int 行高像素 = (int)(38 * scaleFactor);
int 列宽像素 = (int)(76 * scaleFactor);
for (int i = 0; i < 表格.RowCount; i++)
{表格.RowStyles.Add(new RowStyle(SizeType.Absolute, 行高像素));
}
for (int j = 0; j < 表格.ColumnCount; j++)
{表格.ColumnStyles.Add(new ColumnStyle(SizeType.Absolute, 列宽像素));
}
表格.Controls.Add(按钮启动, 0, 3);
表格.SetRowSpan(按钮启动, 2);
表格.SetColumnSpan(按钮启动, 2);
表格.Controls.Add(下拉模式, 2, 3);
表格.SetColumnSpan(下拉模式, 2);
表格.Controls.Add(按钮配置, 3, 1);
表格.Controls.Add(按钮功能, 3, 0);
表格.Controls.Add(按钮航线, 3, 2);
表格.Controls.Add(输入鸣谢, 0, 0);
表格.SetColumnSpan(输入鸣谢, 3);
表格.SetRowSpan(输入鸣谢, 3);
表格.Controls.Add(输入鱼名, 2, 4);
表格.SetColumnSpan(输入鱼名, 2);
表格.Controls.Add(文本诗句, 0, 5);
表格.SetColumnSpan(文本诗句, 4);
表格.SetRowSpan(文本诗句, 2);
}
void 设置控件样式()
{
foreach (Control c in 表格.Controls)
{
c.Font = 字体;
c.ForeColor = 设置主题颜色(白色);
c.AutoSize = true;
c.Dock = DockStyle.Fill;
if (c is Button b)
{b.Font = 加粗;
b.FlatStyle = FlatStyle.Flat;
b.BackColor = 设置主题颜色(按钮蓝色);
b.FlatAppearance.BorderColor = 设置主题颜色(按钮黑蓝色);
}
else if (c is TextBox t)
{t.Font = 加粗;
t.BorderStyle = BorderStyle.FixedSingle;
t.BackColor = 设置主题颜色(输入框灰蓝色);
}
else if (下拉模式.SelectedIndex == -1)
{恢复下拉模式选项();
}
}
文本诗句.Font =
_lang == 1 ? 楷体
: _lang == 3 ? 明朝体
: 罗马体;
文本诗句.ForeColor = 设置主题颜色(标签暗金色);
文本诗句.TextAlign = ContentAlignment.MiddleCenter;
按钮启动.Font = 加粗;
按钮配置.BackColor = 设置主题颜色(按钮红色);
按钮配置.ForeColor = 设置主题颜色(按钮字体黄色);
按钮功能.BackColor = 设置主题颜色(按钮同步蓝色);
按钮功能.ForeColor = 设置主题颜色(按钮字体黄色);
按钮航线.BackColor = 设置主题颜色(灰色);
按钮航线.ForeColor = 设置主题颜色(深灰色);
输入鸣谢.Font = 小号加粗;
输入鸣谢.ForeColor = 设置主题颜色(暗金色);
输入鸣谢.BackColor = 设置主题颜色(输入框深蓝色);
输入鱼名.Font = 字体;
输入鱼名.ForeColor = 设置主题颜色(灰色);
下拉模式.DropDownStyle = ComboBoxStyle.DropDownList;
下拉模式.DrawMode = DrawMode.OwnerDrawFixed;
下拉模式.BackColor = 设置主题颜色(输入框深蓝色);
下拉模式.ForeColor = 设置主题颜色(下拉框淡金色);
下拉模式.Items.Clear();
下拉模式.Items.AddRange(
new[]
{获取翻译("模式文本1"),
获取翻译("模式文本2"),
获取翻译("模式文本3"),
获取翻译("模式文本4"),
获取翻译("模式文本5"),
获取翻译("模式文本6"),
获取翻译("模式文本7"),
获取翻译("模式文本8"),
获取翻译("模式文本9"),
获取翻译("模式文本10"),
获取翻译("模式文本11"),
获取翻译("模式文本12"),
}
);
按钮功能.Text = 获取翻译("生成按钮文本");
设置提示(按钮功能, 获取翻译("生成按钮提示"));
按钮配置.Text = 获取翻译("配置按钮文本");
设置提示(按钮配置, 获取翻译("配置按钮提示"));
按钮航线.Text = 获取翻译("航线按钮文本");
设置提示(按钮航线, 获取翻译("航线按钮提示"));
按钮启动.Text = 获取翻译("启动按钮初始文本");
设置提示(按钮启动, 获取翻译("启动按钮初始文本") + "\r\n右键切换悬浮窗状态");
if (输入鱼名.Text == 输入提示)
{var nr = 获取翻译("输入初始提示");
输入鱼名.Text = nr;
输入提示 = nr;
}
设置提示(
输入鱼名,
获取翻译("输入提示")
+ Environment.NewLine
+ 说明.Replace("\\r\\n", Environment.NewLine)
);
设置提示(输入鸣谢, 获取翻译("赞助窗口提示"));
添加下拉前缀(下拉模式);
下拉模式.DrawItem -= 绘制下拉框;
下拉模式.DrawItem += 绘制下拉框;
恢复下拉模式选项();
更新输入鸣谢文本();
if (!string.IsNullOrEmpty(_lastN))
延时更新提示语(获取翻译("发现新版本1"));
if (!窗口初始化)
{下拉模式.SelectedIndexChanged -= 更新按钮文本;
下拉模式.SelectedIndexChanged += 更新按钮文本;
SetScalarVariable(false, "空天姬控制窗口I", "1");
RealPlugin.plug.InvokeNamedCallback(
"command",
获取翻译(愚人节或默认文本("原神", "鸣潮", "窗口") + "加载成功默语")
);
窗口初始化 = true;
}
}
void 绘制下拉框(object sender, DrawItemEventArgs e)
{
if (e.Index == -1)
{
e.Graphics.FillRectangle(new SolidBrush(e.BackColor), e.Bounds);
return;
}
e.Graphics.FillRectangle(new SolidBrush(e.BackColor), e.Bounds);
StringFormat stringFormat =
new() { Alignment = StringAlignment.Near, LineAlignment = StringAlignment.Center };
e.Graphics.DrawString(
((ComboBox)sender).Items[e.Index].ToString(),
e.Font,
new SolidBrush(e.ForeColor),
e.Bounds.X,
e.Bounds.Y
);
using Pen borderPen = new(设置主题颜色(输入框深蓝色));
e.Graphics.DrawRectangle(
borderPen,
e.Bounds.X,
e.Bounds.Y,
e.Bounds.Width - 1,
e.Bounds.Height - 1
);
}
void 添加下拉前缀(ComboBox comboBox)
{int selectedIndex = comboBox.SelectedIndex;
List<string> newItems = new();
foreach (var item in comboBox.Items)
{if (item is string strItem)
{
if (!strItem.StartsWith("▶"))
{newItems.Add($"▶{strItem}");
}
else
{newItems.Add(strItem);
}
}
}
comboBox.Items.Clear();
comboBox.Items.AddRange(newItems.ToArray());
if (selectedIndex >= 0 && selectedIndex < comboBox.Items.Count)
{comboBox.SelectedIndex = selectedIndex;
}
else
{comboBox.SelectedIndex = 0;
}
}
void 输入模式(object sender, MouseEventArgs e)
{switch (e.Button)
{case MouseButtons.Left:
{if (下拉模式.SelectedIndex <= 1)
{发送日志("空天姬鱼王启动：" + (下拉模式.SelectedIndex + 1));
}
else if (下拉模式.SelectedIndex <= 6)
{发送日志("空天姬海钓启动：" + (下拉模式.SelectedIndex - 1));
}
else
{发送日志(
"空天姬空岛启动："
+ (下拉模式.SelectedIndex - 6) switch
{1 => 1,
2 => 2,
3 => 3,
4 => 3,
5 => 4,
_ => 1,
}
);
}
SetScalarVariable(true, "空天姬控制主界面选项索引", 下拉模式.SelectedIndex);
}
break;
case MouseButtons.Middle:
if (!string.IsNullOrEmpty(GetScalarVariable(false, "空天姬参数")))
{发送日志("空天姬暂停");
延时更新(获取翻译("已暂停提示"), 1500).ConfigureAwait(false);
}
else
{延时更新(获取翻译("空天姬未启动提示"), 3000).ConfigureAwait(false);
}
break;
case MouseButtons.Right:
发送日志("空天姬切换悬浮窗");
延时更新(获取翻译("已切换提示"), 1500).ConfigureAwait(false);
break;
}
}
