void 输入预设(object sender, MouseEventArgs e)
{if (
(e.Button == MouseButtons.Left && e.Clicks == 2)
|| (e.Button == MouseButtons.Middle && e.Clicks == 1)
)
{if (
!输入鱼名.Text.StartsWith(获取翻译("输入检验标识"))
&& !string.IsNullOrEmpty(输入鱼名.Text)
)
{if (预设名匹配成功)
{RealPlugin.plug.InvokeNamedCallback(
"command",
默语
+ 获取翻译("空天姬指令1")
+ (
(e.Button == MouseButtons.Middle && e.Clicks == 1)
? 获取翻译("空天姬指令参数1") + 预设参数1
: 预设参数1
+ (
!string.IsNullOrEmpty(预设参数2)
? " " + 预设参数2
: ""
)
)
);
延时更新(获取翻译("已发送提示"), 3000).ConfigureAwait(false);
输入鱼名.SelectionLength = 0;
输入鱼名.SelectionStart = 输入鱼名.Text.Length;
}
else
{延时更新(获取翻译("不支持预设发送提示"), 5000).ConfigureAwait(false);
RealPlugin.plug.InvokeNamedCallback(
"command",
默语 + 获取翻译("空天姬指令2") + 预设参数1
);
输入鱼名.SelectionLength = 0;
输入鱼名.SelectionStart = 输入鱼名.Text.Length;
}
}
else
{延时更新(获取翻译("请输入提示"), 1500).ConfigureAwait(false);
}
}
else if (
e.Button == MouseButtons.Left
&& e.Clicks == 1
&& 输入鱼名.Text.StartsWith(获取翻译("输入检验标识"))
)
{输入鱼名.Text = "";
}
}
