async Task 延时更新(string 文本内容, int 延迟毫秒数)
{文本诗句.Text = 文本内容;
await Task.Delay(延迟毫秒数);
if (!所有诗句.Contains(文本诗句.Text))
{更新诗句();
}
}
async Task 刷新主界面(CancellationToken cancellationToken)
{
_lastA = null;
_lastO = null;
_lastP = null;
_lastT = -1;
_isPaused = false;
_lastlang = -1;
_lastN = null;
while (!cancellationToken.IsCancellationRequested)
{
var a = GetScalarVariable(false, "空天姬参数")?.ToString();
var o =
GetScalarVariable(false, "空天姬控制海钓模式")?.ToString()
+ GetScalarVariable(false, "空天姬空岛模式")?.ToString();
var p = GetScalarVariable(false, "空天姬控制暂停")?.ToString();
var 配置字典 = GetDictVariable(true, "空天姬配置") ?? new VariableDictionary();
int t = int.TryParse(配置字典.GetValue("主界面主题").ToString(), out int result)
? result
: 0;
t = 计算主界面主题(t);
var l =
int.TryParse(
GetDictVariable(true, "空天姬配置")?.GetValue("界面语言")?.ToString(),
out var _l
)
&& _l != 0
? _l
: MissFisher.lang;
var n = GetScalarVariable(false, "空天姬控制有新版本I")?.ToString();
if (
a != _lastA
|| o != _lastO
|| p != _lastP
|| t != _lastT
|| l != _lastlang
|| n != _lastN
)
{_lastA = a;
_lastO = o;
_lastP = p;
_lastT = t;
_isPaused = p == "1";
更新界面样式(a, o, _isPaused, t, n, (l != _lastlang ? l : -1));
}
await Task.Delay(1000, cancellationToken);
}
}
void 更新按钮文本(object sender, EventArgs e)
{
var a = GetScalarVariable(false, "空天姬参数")?.ToString();
var o =
GetScalarVariable(false, "空天姬控制海钓模式")?.ToString()
+ GetScalarVariable(false, "空天姬空岛模式")?.ToString();
var p = GetScalarVariable(false, "空天姬控制暂停")?.ToString();
bool isPaused = p == "1";
更新界面样式(a, o, isPaused, 主界面主题);
}
void 更新界面样式(string a, string o, bool isPaused, int t, string n = null, int l = -1)
{bool 传入语言 = l != -1;
if (传入语言)
{_lang = l;
_lastlang = l;
}
if (!string.IsNullOrEmpty(a) || !string.IsNullOrEmpty(o))
{按钮启动.FlatAppearance.BorderColor = isPaused
? 设置主题颜色(按钮发光黄色)
: 设置主题颜色(按钮冰球紫色);
按钮启动.ForeColor = isPaused ? 设置主题颜色(按钮发光黄色) : 设置主题颜色(白色);
按钮启动.Font = 小号加粗;
按钮启动.Text =
(isPaused ? 获取翻译("启动按钮已暂停") : 获取翻译("启动按钮运行中"))
+ "\r\n"
+ 获取翻译("启动按钮切换悬浮窗提示")
+ "\r\n"
+ (isPaused ? 获取翻译("启动按钮继续提示") : 获取翻译("启动按钮功能提示"));
空天姬主界面.Text = isPaused
? 获取翻译(愚人节或默认文本("原神", "鸣潮") + "标题已暂停")
: 获取翻译(愚人节或默认文本("原神", "鸣潮") + "标题运行中");
设置提示(
按钮启动,
isPaused ? 获取翻译("启动按钮暂停中提示") : 获取翻译("启动按钮运行中提示")
);
}
else
{按钮启动.FlatAppearance.BorderColor = 设置主题颜色(按钮黑蓝色);
按钮启动.ForeColor = 设置主题颜色(白色);
string 模式选项 = 下拉模式.SelectedItem?.ToString() ?? string.Empty;
if (模式选项.StartsWith("▶"))
{模式选项 = 模式选项.Substring(1);
}
按钮启动.Font = 加粗;
按钮启动.Text =
获取翻译(愚人节或默认文本("启动原神文本", "启动鸣潮文本", "启动文本"))
+ 模式选项;
空天姬主界面.Text = 获取翻译(愚人节或默认文本("原神", "鸣潮") + "标题空闲中");
设置提示(
按钮启动,
获取翻译("启动文本")
+ $"{模式选项}"
+ "\r\n"
+ 获取翻译("启动按钮切换悬浮窗提示")
);
}
if (t != 主界面主题 || 传入语言)
{主界面主题 = t;
空天姬主界面.BackColor = 设置主题颜色(背景色);
设置控件样式();
更新颜色(空天姬主界面, EventArgs.Empty);
刷新主题诗句();
更新诗句(true);
}
if (n != _lastN)
{_lastN = n;
if (!string.IsNullOrEmpty(n))
{更新输入鸣谢文本();
延时更新提示语(获取翻译("发现新版本1"));
}
else
{更新输入鸣谢文本(true);
更新诗句();
}
}
}
