Color 设置主题颜色(Color c)
{if (主界面主题 == 1)
{return c;
}
if (主题颜色映射表.TryGetValue(主界面主题, out var 颜色映射))
{
if (颜色映射.TryGetValue(c, out Color 映射颜色))
{return 映射颜色;
}
}
return 显眼紫色;
}
string 转换为汉字(int 数字)
{if (数字 <= 10)
{return 数字转汉字[数字];
}
else if (数字 > 10 && 数字 < 20)
{return "拾" + 数字转汉字[数字 - 10];
}
else if (数字 >= 20 && 数字 < 100)
{if (数字 % 10 == 0)
{return 数字转汉字[数字 / 10] + "拾";
}
else
{return 数字转汉字[数字 / 10] + "拾" + 数字转汉字[数字 % 10];
}
}
else if (数字 == 100)
{return "壹佰";
}
else
{return 数字.ToString();
}
}
string GetOrdinalSuffix(int number)
{if (number % 100 >= 11 && number % 100 <= 13)
return "th";
return (number % 10) switch
{1 => "st",
2 => "nd",
3 => "rd",
_ => "th",
};
}
string 愚人节或默认文本(string 原神文本, string 鸣潮文本, string 默认文本 = "")
{bool 愚人节主题 =
(主界面主题 == 7 || 主界面主题 == 9) && 使用节日主题() && IsAprilFoolsDay();
return 愚人节主题 ? (主界面主题 == 7 ? 原神文本 : 鸣潮文本) : 默认文本;
}
string[] 愚人节或默认诗句(string[] 原神数组, string[] 鸣潮数组, string[] 默认数组 = null)
{bool 有效节日主题 =
(主界面主题 == 7 || 主界面主题 == 9) && 使用节日主题() && IsAprilFoolsDay();
return 有效节日主题
? (主界面主题 == 7 ? 原神数组 : 鸣潮数组)
: 默认数组 ?? Array.Empty<string>();
}
bool 使用节日主题()
{var 配置字典 = GetDictVariable(true, "空天姬配置") ?? new VariableDictionary();
string 配置 = 配置字典.GetValue("节日主题")?.ToString() ?? "1";
return 配置 == "1" && IsAprilFoolsDay();
}
void 恢复下拉模式选项()
{
object 变量值 = GetScalarVariable(true, "空天姬控制主界面选项索引");
string 变量文本 = 变量值 != null ? 变量值.ToString() : string.Empty;
bool 解析成功 = int.TryParse(变量文本, out int 索引);
下拉模式.SelectedIndex = 解析成功
? Math.Max(0, Math.Min(索引, 下拉模式.Items.Count - 1))
: 0;
}
void 更新输入鸣谢文本(bool c = false)
{输入鸣谢.Text =
(
!string.IsNullOrEmpty(_lastN) && !c
? 构建并返回更新提示文本()
: 愚人节或默认文本(
获取翻译("原神公告") + Environment.NewLine,
获取翻译("鸣潮公告") + Environment.NewLine
)
) + 赞助.Replace("\\r\\n", Environment.NewLine);
}
string 构建并返回更新提示文本()
{var l = GetListVariable(true, "空天姬版本信息") ?? new VariableList();
string v = l.Peek(1).ToString() ?? "";
string s = l.Peek(2).ToString() ?? "";
string vv = 更新内容(l.Peek(3).ToString() ?? "");
string ss = 更新内容(l.Peek(4).ToString() ?? "");
StringBuilder stringBuilder = new StringBuilder();
stringBuilder.Append(获取翻译("发现新版本3"));
stringBuilder.Append(MissFisher.isSnapshot ? "" : "v");
stringBuilder.Append(MissFisher.isSnapshot ? s : v);
stringBuilder.Append("\r\n");
stringBuilder.Append(MissFisher.isSnapshot ? ss : vv);
stringBuilder.Append("\r\n");
stringBuilder.Append(获取翻译("更新方法1"));
stringBuilder.Append("\r\n\r\n");
return stringBuilder.ToString();
string 更新内容(string s)
{if (!string.IsNullOrEmpty(s))
{string[] 部分 = s.Split('丨');
return 部分.Length > 1 ? 部分[_lang - 1] : "";
}
return "";
}
}
