var 列表更新计时器 = new System.Windows.Forms.Timer { Interval = 60000 };
var 行更新计时器 = new System.Windows.Forms.Timer { Interval = 50 };
int 当前行索引 = 0;
空天姬航线预报.Shown += (sender, e) =>
{((Form)sender).TopMost = true;
System.Threading.Thread.Sleep(10);
((Form)sender).TopMost = false;
};
空天姬航线预报.Activated += (sender, e) =>
{列表更新计时器.Start();
if (!行更新计时器.Enabled)
{当前行索引 = 0;
行更新计时器.Start();
}
};
空天姬航线预报.Deactivate += (sender, e) =>
{列表更新计时器.Stop();
行更新计时器.Stop();
};
列表更新计时器.Tick += (sender, e) =>
{if (!行更新计时器.Enabled)
{当前行索引 = 0;
行更新计时器.Start();
}
};
行更新计时器.Tick += (sender, e) =>
{更新单行航线();
if (当前行索引 >= 表格.RowCount)
{行更新计时器.Stop();
}
};
滚动面板.MouseWheel += (sender, e) =>
{int 滚动量 = e.Delta > 0 ? -8 : 8;
滚动面板.AutoScrollPosition = new Point(
-滚动面板.AutoScrollPosition.X,
Math.Max(0, -滚动面板.AutoScrollPosition.Y + 滚动量)
);
};
void 设置控件关系()
{float scaleFactor;
using (var graphics = Graphics.FromHwnd(IntPtr.Zero))
{scaleFactor = graphics.DpiY / 96f;
空天姬航线预报.Size = new Size(
(int)((_lang != 1 ? 1280 + 14 : 1086 + 14) * scaleFactor),
(int)((_lang != 1 ? 800 + 7 : 679 + 7) * scaleFactor)
);
}
表格.SuspendLayout();
表格.ColumnCount = 4;
表格.RowCount = 72;
for (int i = 0; i < 72; i++)
{表格.RowStyles.Add(new RowStyle(SizeType.Absolute, 48 * scaleFactor));
}
for (int row = 0; row < 72; row++)
{for (int col = 0; col < 4; col++)
{var label = new Label
{Text = "",
Dock = DockStyle.Fill,
AutoSize = true,
TextAlign = col switch
{0 => ContentAlignment.MiddleRight,
1 => ContentAlignment.MiddleLeft,
2 => ContentAlignment.MiddleLeft,
_ => ContentAlignment.MiddleCenter,
},
ForeColor = col switch
{0 => 白色,
1 => 下拉框淡金色,
2 => 暗金色,
_ => 灰色,
},
Font = col switch
{0 => 加粗,
1 => _lang != 1 ? 小号加粗 : 加粗,
2 => 小号加粗,
_ => 小号加粗,
},
};
表格.Controls.Add(label, col, row);
标签列表.Add(label);
}
}
表格.ResumeLayout(true);
}
void 更新单行航线()
{if (当前行索引 >= 表格.RowCount)
return;
long 当前时间戳 = DateTimeOffset.UtcNow.ToUnixTimeSeconds();
int utc小时数 = DateTimeOffset.FromUnixTimeSeconds(当前时间戳).Hour;
if (utc小时数 % 2 == 1)
当前时间戳 += 7200;
long 第一行时间戳 = 当前时间戳 - (当前时间戳 % 7200);
long 当前行时间戳 = 第一行时间戳 + (当前行索引 / 2) * 7200;
int 班次航线序号 = 计算航线序号(当前行时间戳);
int 行索引1 = 班次航线序号 - 1;
int 行索引2 = 行索引1 + 12;
int 行 = 当前行索引;
更新行数据(行, 当前行时间戳, 航线表格[行索引1][1], 航线表格[行索引1][2], 班次航线序号);
当前行索引++;
当前行时间戳 += 7200;
更新行数据(
当前行索引,
当前行时间戳,
航线表格[行索引2][1],
航线表格[行索引2][2],
班次航线序号
);
当前行索引++;
}
void 更新行数据(int row, long timestamp, string name, string desc, int seq)
{if (row >= 表格.RowCount)
return;
标签列表[row * 4].Text = 获取时间戳显示内容(timestamp, row);
标签列表[row * 4 + 1].Text = name;
标签列表[row * 4 + 2].Text = desc;
标签列表[row * 4 + 3].Text = (row % 2 == 0) ? seq.ToString("D2") : "";
}
int 计算航线序号(long 时间戳)
{const long 基准时间戳 = 1715184000;
long 时间差 = 时间戳 - 基准时间戳;
int 当前小时数 = (int)((时间差 % 86400) / 3600);
int 偏移量 = (int)(时间差 / 7200) + (int)(时间差 / 86400) % 12;
int 修正值 = 当前小时数 % 2 == 1 ? (当前小时数 == 15 ? 2 : 1) : 0;
return (偏移量 + 修正值) % 12 + 1;
}
string 获取时间戳显示内容(long 时间戳, int 行)
{DateTime 本地时间 = DateTimeOffset.FromUnixTimeSeconds(时间戳).LocalDateTime;
int 小时数 = 本地时间.Hour;
return 行 switch
{0 => $"{本地时间:MM-dd} {小时数:00}:00",
_ when 行 % 2 == 1 => "",
_ when 小时数 == 0 || 小时数 == 1 => $"{本地时间:MM-dd} {小时数:00}:00",
_ => $"{小时数:00}:00",
};
}
设置控件关系();
RealPlugin.plug.InvokeNamedCallback("command", 获取翻译("航线初始化完成默语"));
空天姬航线预报.ShowDialog();
}
