string GetValueOrDefault(string key, string defaultValue)
{return 字典.GetValue(key)?.ToString() == ""
? defaultValue
: 字典.GetValue(key).ToString();
}
string 界面语言 = GetValueOrDefault("界面语言", lang.ToString());
if (界面语言 == "0")
界面语言 = lang.ToString();
int _lang = int.TryParse(界面语言, out var l) ? l : lang;
string 主界面自启 = GetValueOrDefault("主界面自启", "1");
string 主界面置顶 = GetValueOrDefault("主界面置顶", "1");
string 确定热键 = GetValueOrDefault("确定热键", 获取翻译("未设置提示"));
