表格.SetColumnSpan(文本勾选自启, 3);
表格.Controls.Add(文本勾选置顶, 3, 15);
表格.SetColumnSpan(文本勾选置顶, 3);
表格.Controls.Add(按钮保存, 1, 17);
表格.SetColumnSpan(按钮保存, 4);
表格.SetRowSpan(按钮保存, 2);
}
void 设置提示(Control c, string t)
{if (c.Tag == null || c.Tag is not ToolTip)
{c.Tag = new ToolTip();
}
var 提示 = (ToolTip)c.Tag;
提示.SetToolTip(c, t);
提示.AutoPopDelay = 20000;
提示.InitialDelay = 500;
提示.ReshowDelay = 500;
提示.IsBalloon = true;
}
void 切换文本勾选前缀(object s, EventArgs e)
{Label l = (Label)s;
bool b = l.Text.StartsWith(" ▣");
l.Text = (b ? " ▢ " : " ▣ ") + l.Text.Replace(" ▣ ", "").Replace(" ▢ ", "");
}
void 设置文本勾选前缀(Control c, bool b)
{c.Text = (b ? " ▣ " : " ▢ ") + c.Text.Replace(" ▣ ", "").Replace(" ▢ ", "");
}
void 设置控件样式(bool boo = false)
{foreach (Control c in 表格.Controls)
{c.Font = _lang != 1 ? 小号 : 字体;
c.ForeColor = Color.White;
c.AutoSize = true;
c.Dock = DockStyle.Fill;
if (c is Label l)
{l.TextAlign = ContentAlignment.MiddleLeft;
l.ForeColor = Color.FromArgb(230, 218, 193);
}
else if (c is Button b)
{b.FlatStyle = FlatStyle.Flat;
b.BackColor = Color.FromArgb(50, 81, 155);
b.FlatAppearance.BorderColor = Color.FromArgb(19, 33, 68);
}
else if (c is TextBox t)
{t.BorderStyle = BorderStyle.FixedSingle;
t.BackColor = Color.FromArgb(21, 35, 71);
}
else if (c is CheckBox cb)
{cb.Appearance = Appearance.Button;
cb.ForeColor = Color.FromArgb(234, 222, 195);
}
else if (c is ComboBox cob)
{cob.DropDownStyle = ComboBoxStyle.DropDownList;
cob.DrawMode = DrawMode.OwnerDrawFixed;
cob.BackColor = Color.FromArgb(41, 60, 107);
cob.ForeColor = Color.FromArgb(234, 222, 195);
添加下拉前缀(cob);
cob.DrawItem -= 绘制下拉框;
cob.DrawItem += 绘制下拉框;
}
}
按钮保存.Font = 加粗;
文本标题自动.ForeColor = Color.FromArgb(196, 197, 201);
文本标题工具.ForeColor = Color.FromArgb(196, 197, 201);
文本标题其他.ForeColor = Color.FromArgb(196, 197, 201);
文本界面语言.TextAlign = ContentAlignment.MiddleRight;
文本勾选置顶.TextAlign = ContentAlignment.MiddleRight;
if (boo)
RealPlugin.plug.InvokeNamedCallback("command", 默语 + 获取翻译("读取配置默语"));
}
void 绘制下拉框(object sender, DrawItemEventArgs e)
{
if (e.Index == -1)
{
e.Graphics.FillRectangle(new SolidBrush(e.BackColor), e.Bounds);
return;
}
e.Graphics.FillRectangle(new SolidBrush(e.BackColor), e.Bounds);
StringFormat stringFormat =
new() { Alignment = StringAlignment.Near, LineAlignment = StringAlignment.Center };
e.Graphics.DrawString(
((ComboBox)sender).Items[e.Index].ToString(),
e.Font,
new SolidBrush(e.ForeColor),
e.Bounds.X,
e.Bounds.Y
);
using Pen borderPen = new(Color.FromArgb(41, 60, 107));
e.Graphics.DrawRectangle(
borderPen,
e.Bounds.X,
e.Bounds.Y,
e.Bounds.Width - 1,
e.Bounds.Height - 1
);
}
void 添加下拉前缀(ComboBox comboBox)
{int selectedIndex = comboBox.SelectedIndex;
List<string> newItems = new();
foreach (var item in comboBox.Items)
{if (item is string strItem)
{
if (!strItem.StartsWith("▶"))
{newItems.Add($"▶ {strItem}");
}
else
{newItems.Add(strItem);
}
}
}
comboBox.Items.Clear();
comboBox.Items.AddRange(newItems.ToArray());
if (selectedIndex >= 0 && selectedIndex < comboBox.Items.Count)
{comboBox.SelectedIndex = selectedIndex;
}
else
{comboBox.SelectedIndex = 0;
}
}
