void 读取配置(object s, EventArgs e, bool b = false, int _s = -1)
{if (空天姬配置窗口.InvokeRequired)
{空天姬配置窗口.Invoke(new System.Action(() => 读取配置(s, e)));
return;
}
if (主界面自启 == "0")
{设置文本勾选前缀(文本勾选自启, false);
}
if (主界面置顶 == "0")
{设置文本勾选前缀(文本勾选置顶, false);
}
输入录制2.Text = 确定热键;
if (int.TryParse(语言, out var lingo))
{下拉宏语言.SelectedIndex = lingo - 1;
}
if (_s != -1)
{下拉界面语言.SelectedIndex = _s;
}
else if (int.TryParse(界面语言, out var lang))
{下拉界面语言.SelectedIndex = lang;
}
下拉TTS报竿.SelectedIndex = TTS报竿 == "2" ? 0 : (TTS报竿 == "1" ? 1 : 2);
下拉音效报竿.SelectedIndex = 音效报竿 == "2" ? 0 : (音效报竿 == "1" ? 1 : 2);
下拉竿时.SelectedIndex = 竿时悬浮窗 == "2" ? 0 : (竿时悬浮窗 == "1" ? 1 : 2);
下拉采集力.SelectedIndex = 采集力悬浮窗 == "2" ? 0 : (采集力悬浮窗 == "1" ? 1 : 2);
if (int.TryParse(悬浮窗字号, out var fontSize) && fontSize >= 1 && fontSize <= 4)
{下拉字号.SelectedIndex = fontSize;
}
if (调试模式 == "0")
{设置文本勾选前缀(文本勾选调试, false);
}
if (节日主题 == "0")
{设置文本勾选前缀(文本勾选节日主题, false);
}
if (int.TryParse(主界面主题, out var theme))
{下拉主题.SelectedIndex = theme;
}
switch (公共频道)
{case "p":
下拉触幻.SelectedIndex = 1;
break;
case "y":
下拉触幻.SelectedIndex = 2;
break;
case "sh":
下拉触幻.SelectedIndex = 3;
break;
}
switch (幻海频道)
{case "p":
下拉时长.SelectedIndex = 1;
break;
case "y":
下拉时长.SelectedIndex = 2;
break;
case "sh":
下拉时长.SelectedIndex = 3;
break;
}
switch (成就频道)
{case "p":
下拉成就.SelectedIndex = 1;
break;
case "y":
下拉成就.SelectedIndex = 2;
break;
case "sh":
下拉成就.SelectedIndex = 3;
break;
}
if (关闭系统音 == "0")
{设置文本勾选前缀(文本勾选系统音, false);
}
if (int.TryParse(系统音, out var intValue) && intValue >= 0 && intValue <= 100)
{输入恢复.Text = intValue.ToString();
}
if (
double.TryParse(延迟时间, out var intValueyc)
&& intValueyc >= 0
&& intValueyc <= 1000
)
{输入延迟.Text = intValueyc.ToString();
}
if (b)
RealPlugin.plug.InvokeNamedCallback("command", 默语 + 获取翻译("配置读取成功默语"));
}
void 保存配置(object sender, EventArgs e)
{if (isSavingConfig)
{return;
}
isSavingConfig = true;
try
{if (空天姬配置窗口.InvokeRequired)
{空天姬配置窗口.Invoke(new System.Action(() => 保存配置(sender, e)));
return;
}
字典.SetValue("主界面自启", 文本勾选自启.Text.StartsWith(" ▣") ? 1 : 0);
字典.SetValue("主界面置顶", 文本勾选置顶.Text.StartsWith(" ▣") ? 1 : 0);
字典.SetValue("节日主题", 文本勾选节日主题.Text.StartsWith(" ▣") ? 1 : 0);
字典.SetValue("确定热键", (检查数字(输入录制2.Text)) ? 输入录制2.Text : "");
字典.SetValue("语言", 下拉宏语言.SelectedIndex + 1);
字典.SetValue("界面语言", 下拉界面语言.SelectedIndex);
字典.SetValue(
"TTS报竿",
下拉TTS报竿.SelectedIndex == 0 ? 2 : (下拉TTS报竿.SelectedIndex == 1 ? 1 : 0)
);
字典.SetValue(
"竿时悬浮窗",
下拉竿时.SelectedIndex == 0 ? 2 : (下拉竿时.SelectedIndex == 1 ? 1 : 0)
);
字典.SetValue(
"采集力悬浮窗",
下拉采集力.SelectedIndex == 0 ? 2 : (下拉采集力.SelectedIndex == 1 ? 1 : 0)
);
字典.SetValue(
"音效报竿",
下拉音效报竿.SelectedIndex == 0 ? 2 : (下拉音效报竿.SelectedIndex == 1 ? 1 : 0)
);
字典.SetValue("悬浮窗字号", 下拉字号.SelectedIndex);
字典.SetValue("调试模式", 文本勾选调试.Text.StartsWith(" ▣") ? 1 : 0);
字典.SetValue("主界面主题", 下拉主题.SelectedIndex);
string chuHuan = 下拉触幻.SelectedIndex switch
{0 => "e",
1 => "p",
2 => "y",
3 => "sh",
_ => "e",
};
字典.SetValue("公共频道", chuHuan);
string huanHai = 下拉时长.SelectedIndex switch
{0 => "e",
1 => "p",
2 => "y",
3 => "sh",
_ => "e",
};
字典.SetValue("幻海频道", huanHai);
string chengJiu = 下拉成就.SelectedIndex switch
{0 => "e",
1 => "p",
2 => "y",
3 => "sh",
_ => "e",
};
字典.SetValue("成就频道", chengJiu);
字典.SetValue("关闭系统音", 文本勾选系统音.Text.StartsWith(" ▣") ? 1 : 0);
字典.SetValue("系统音", (检查数字(输入恢复.Text)) ? 输入恢复.Text : 系统音);
字典.SetValue(
"延迟时间",
(检查数字(输入延迟.Text))
? 转换秒数(输入延迟.Text)
: 转换秒数(延迟时间).ToString()
);
SetDictVariable(true, "空天姬配置", 字典);
RealPlugin.plug.InvokeNamedCallback("command", 默语 + 获取翻译("配置已保存默语"));
发送日志("空天姬配置");
空天姬配置窗口.Close();
}
catch (Exception ex)
{Log(2, $"{ex}");
}
finally
{isSavingConfig = false;
}
}
void 窗口关闭保存配置(object sender, FormClosingEventArgs e)
{if (!isClosing && e.CloseReason == CloseReason.UserClosing)
{isClosing = true;
if (空天姬配置窗口.InvokeRequired)
{空天姬配置窗口.Invoke(new System.Action(() => 保存配置(sender, e)));
}
else
{保存配置(sender, e);
}
isClosing = false;
}
}
void 按键录制(KeyEventArgs e, TextBox recordingInput)
{if (e.Control || e.Alt || e.Shift)
{recordingInput.Text = 获取翻译("不接受组合键提示");
}
else
{if (e.KeyCode == Keys.Back || e.KeyCode == Keys.Escape || e.KeyCode == Keys.Delete)
{recordingInput.Text = 获取翻译("未设置提示");
}
else
{recordingInput.Text = e.KeyValue.ToString();
}
}
}
void 录制2(object sender, KeyEventArgs e)
{按键录制(e, 输入录制2);
}
bool 检查数字(string input)
{if (int.TryParse(input, out int temp))
{return temp >= 0;
}
return false;
}
string 转换秒数(string input)
{if (double.TryParse(input, out double number))
{double result = number / 1000;
if (result == 0)
{return "0";
}
return result.ToString();
}
else
{return "0";
}
}
string 转换毫秒(string input)
{if (double.TryParse(input, out double number))
{double result = number * 1000;
if (result == 0)
{return "0";
}
return result.ToString();
}
else
{return "0";
}
}
void 全选(object sender, MouseEventArgs e)
{if (sender is TextBox textBox)
{if (e.Button == MouseButtons.Left)
{textBox.SelectAll();
}
}
}
}
public static void Handle(object _1, string val)
{AssignValues(val);
static void AssignValues(string input)
{int _lang =
int.TryParse(
GetDictVariable(true, "空天姬配置")?.GetValue("界面语言")?.ToString(),
out var l
)
&& l != 0
? l
: MissFisher.lang;
string 获取翻译(string s)
{return I18n.GetTranslationByIndex(s, _lang);
}
int 单语言空间 = int.Parse("${env:单语言空间}");
int 单语言空间乘二 = 单语言空间 * 2;
int 非水产品空间 = int.Parse("${env:非水产品空间}");
int 非水产品空间乘二 = 非水产品空间 * 2;
string[] values = input.Split(',');
if (values.Length < 49)
{return;
}
string 空天姬目标 = values[0];
string 空天姬拍最小区间非撒饵 = values[1];
string 空天姬拍最小区间 = values[2];
string 空天姬拍最大区间非撒饵 = values[3];
string 空天姬拍最大区间 = values[4];
string 空天姬大鱼最小区间非撒饵1 = values[5];
string 空天姬大鱼最小区间1 = values[6];
string 空天姬大鱼最大区间非撒饵1 = values[7];
string 空天姬大鱼最大区间1 = values[8];
string 空天姬大鱼最小区间非撒饵2 = values[9];
string 空天姬大鱼最小区间2 = values[10];
string 空天姬大鱼最大区间非撒饵2 = values[11];
string 空天姬大鱼最大区间2 = values[12];
string 空天姬大鱼最小区间非撒饵3 = values[13];
string 空天姬大鱼最小区间3 = values[14];
