空天姬生成器窗口.Shown += (sender, e) =>
{
当前分页 = "控制";
切换分页("控制");
((Form)sender).TopMost = true;
System.Threading.Thread.Sleep(10);
((Form)sender).TopMost = false;
};
按钮固定控制.Click += (s, e) =>
{var now = DateTime.Now;
if ((now - _lastClickTime) < _doubleClickThreshold)
{
输入表达式();
_lastClickTime = DateTime.MinValue;
return;
}
_lastClickTime = now;
if (当前分页 != "控制")
{当前分页 = "控制";
切换分页("控制");
}
};
按钮固定模式.Click += (s, e) =>
{if (当前分页 != "模式")
{当前分页 = "模式";
切换分页("模式");
}
};
按钮固定阶段1.Click += (s, e) =>
{if (当前分页 != "阶段1")
{当前分页 = "阶段1";
