按钮固定转换.Click += (s, e) =>
{if (!string.IsNullOrWhiteSpace(输入固定表达式.Text) && !参数变动)
{解析();
if (当前分页 != "阶段1")
{当前分页 = "阶段1";
切换分页("阶段1");
}
}
else if (参数变动)
{生成();
}
};
按钮控制获取并解析.Click += (s, e) =>
{获取预设();
};
按钮控制启动.Click += (s, e) =>
{if (按钮控制启动.ForeColor == 激活金)
{MouseEventArgs doubleClickEventArgs = new(MouseButtons.Left, 2, 0, 0, 0);
输入预设(s, doubleClickEventArgs);
}
};
