{
MouseEventArgs doubleClickEventArgs = new(MouseButtons.Left, 2, 0, 0, 0);
输入预设(s, doubleClickEventArgs);
};
输入控制预设名.MouseDown += (sender, e) =>
{if (e.Button == MouseButtons.Middle && e.Clicks == 1)
{输入预设(sender, e);
}
};
输入控制预设名.MouseClick += (sender, e) =>
{输入预设(sender, e as MouseEventArgs);
};
