按钮控制重置.Click += (s, e) =>
{if (!获取勾选状态(文本控制勾选确认重置))
return;
else
{状态(文本控制勾选确认重置, 0, 1);
刷新(按钮控制重置);
解析("平钓！！！");
参数变动 = false;
刷新(按钮固定转换);
当前分页 = "阶段1";
切换分页("阶段1");
}
};
