空天姬生成器窗口.ShowDialog();
void 设置控件关系()
{空天姬生成器窗口.Controls.Add(表格);
表格.ColumnCount = 6;
表格.RowCount = 11;
控制面板.ColumnCount = 6;
控制面板.RowCount = 5;
模式面板.ColumnCount = 6;
模式面板.RowCount = 5;
阶段1面板.ColumnCount = 6;
阶段1面板.RowCount = 5;
阶段2面板.ColumnCount = 6;
阶段2面板.RowCount = 5;
阶段3面板.ColumnCount = 6;
阶段3面板.RowCount = 5;
阶段4面板.ColumnCount = 6;
阶段4面板.RowCount = 5;
手法面板.ColumnCount = 6;
手法面板.RowCount = 5;
拍水面板.ColumnCount = 6;
拍水面板.RowCount = 5;
专一面板.ColumnCount = 6;
专一面板.RowCount = 5;
窗口期面板.ColumnCount = 6;
窗口期面板.RowCount = 5;
目标面板.ColumnCount = 6;
目标面板.RowCount = 5;
Graphics graphics = Graphics.FromHwnd(IntPtr.Zero);
float dpiX = graphics.DpiX;
float dpiY = graphics.DpiY;
graphics.Dispose();
float scaleFactor = dpiY / 96f;
int 行高像素 = (int)((32) * scaleFactor);
int 列宽像素 = (int)((77) * scaleFactor);
for (int i = 0; i < 表格.RowCount; i++)
{表格.RowStyles.Add(new RowStyle(SizeType.Absolute, 行高像素));
控制面板.RowStyles.Add(new RowStyle(SizeType.Absolute, 行高像素));
模式面板.RowStyles.Add(new RowStyle(SizeType.Absolute, 行高像素));
阶段1面板.RowStyles.Add(new RowStyle(SizeType.Absolute, 行高像素));
阶段2面板.RowStyles.Add(new RowStyle(SizeType.Absolute, 行高像素));
阶段3面板.RowStyles.Add(new RowStyle(SizeType.Absolute, 行高像素));
阶段4面板.RowStyles.Add(new RowStyle(SizeType.Absolute, 行高像素));
手法面板.RowStyles.Add(new RowStyle(SizeType.Absolute, 行高像素));
拍水面板.RowStyles.Add(new RowStyle(SizeType.Absolute, 行高像素));
专一面板.RowStyles.Add(new RowStyle(SizeType.Absolute, 行高像素));
窗口期面板.RowStyles.Add(new RowStyle(SizeType.Absolute, 行高像素));
目标面板.RowStyles.Add(new RowStyle(SizeType.Absolute, 行高像素));
}
for (int j = 0; j < 表格.ColumnCount; j++)
{表格.ColumnStyles.Add(new ColumnStyle(SizeType.Absolute, 列宽像素));
}
for (int j = 0; j < 控制面板.ColumnCount; j++)
{控制面板.ColumnStyles.Add(new ColumnStyle(SizeType.Absolute, 列宽像素));
模式面板.ColumnStyles.Add(new ColumnStyle(SizeType.Absolute, 列宽像素));
阶段1面板.ColumnStyles.Add(new ColumnStyle(SizeType.Absolute, 列宽像素));
阶段2面板.ColumnStyles.Add(new ColumnStyle(SizeType.Absolute, 列宽像素));
阶段3面板.ColumnStyles.Add(new ColumnStyle(SizeType.Absolute, 列宽像素));
阶段4面板.ColumnStyles.Add(new ColumnStyle(SizeType.Absolute, 列宽像素));
手法面板.ColumnStyles.Add(new ColumnStyle(SizeType.Absolute, 列宽像素));
拍水面板.ColumnStyles.Add(new ColumnStyle(SizeType.Absolute, 列宽像素));
专一面板.ColumnStyles.Add(new ColumnStyle(SizeType.Absolute, 列宽像素));
窗口期面板.ColumnStyles.Add(new ColumnStyle(SizeType.Absolute, 列宽像素));
目标面板.ColumnStyles.Add(new ColumnStyle(SizeType.Absolute, 列宽像素));
}
