void 设置通用样式(Control c)
{c.Font = 小号;
c.AutoSize = true;
c.Dock = DockStyle.Fill;
c.Margin = new Padding(1);
c.Padding = new Padding(0);
if (c is Label l)
{l.TextAlign = ContentAlignment.MiddleCenter;
}
else if (c is Button b)
{b.FlatStyle = FlatStyle.Flat;
b.FlatAppearance.BorderColor = 按钮黑蓝色;
}
else if (c is TextBox t)
{t.TextAlign = HorizontalAlignment.Center;
t.BorderStyle = BorderStyle.FixedSingle;
}
else if (c is CheckBox cb)
{cb.Appearance = Appearance.Button;
}
else if (c is ComboBox cob)
{cob.DropDownStyle = ComboBoxStyle.DropDownList;
cob.DrawMode = DrawMode.OwnerDrawFixed;
cob.BackColor = Color.FromArgb(41, 60, 107);
cob.ForeColor = Color.FromArgb(234, 222, 195);
添加下拉前缀(cob);
cob.DrawItem -= 绘制下拉框;
cob.DrawItem += 绘制下拉框;
}
输入固定表达式.BorderStyle = BorderStyle.None;
输入固定表达式.Multiline = true;
输入固定表达式.TextAlign = HorizontalAlignment.Left;
文本固定报错1.TextAlign = ContentAlignment.MiddleRight;
文本固定报错2.TextAlign = ContentAlignment.MiddleLeft;
文本固定提示.TextAlign = ContentAlignment.MiddleLeft;
文本手法勾选不撒饵.TextAlign = ContentAlignment.MiddleLeft;
文本手法勾选收藏品.TextAlign = ContentAlignment.MiddleLeft;
文本手法勾选大鱼知识.TextAlign = ContentAlignment.MiddleLeft;
文本手法勾选不收集.TextAlign = ContentAlignment.MiddleLeft;
文本手法勾选钓组.TextAlign = ContentAlignment.MiddleLeft;
文本手法勾选鱼眼.TextAlign = ContentAlignment.MiddleLeft;
输入阶段1熟练妙招.TextAlign = HorizontalAlignment.Left;
输入阶段2熟练妙招.TextAlign = HorizontalAlignment.Left;
输入阶段3熟练妙招.TextAlign = HorizontalAlignment.Left;
文本拍水熟练妙招.TextAlign = ContentAlignment.MiddleLeft;
文本专一鱼名.TextAlign = ContentAlignment.MiddleRight;
文本专一提钩.TextAlign = ContentAlignment.MiddleRight;
文本窗口期天气名.TextAlign = ContentAlignment.MiddleRight;
文本窗口期时间段.TextAlign = ContentAlignment.MiddleRight;
文本目标目标计数.TextAlign = ContentAlignment.MiddleRight;
文本目标鱼名.TextAlign = ContentAlignment.MiddleRight;
}
void 切换分页(string s)
{空天姬生成器窗口.SuspendLayout();
刷新(按钮固定控制);
刷新(按钮固定模式);
刷新(按钮固定阶段1);
刷新(按钮固定阶段2);
刷新(按钮固定阶段3);
刷新(按钮固定阶段4);
刷新(按钮固定手法);
刷新(按钮固定拍水);
刷新(按钮固定专一);
刷新(按钮固定窗口期);
刷新(按钮固定目标);
表格.Controls.Remove(控制面板);
表格.Controls.Remove(模式面板);
表格.Controls.Remove(阶段1面板);
表格.Controls.Remove(阶段2面板);
表格.Controls.Remove(阶段3面板);
表格.Controls.Remove(阶段4面板);
表格.Controls.Remove(手法面板);
表格.Controls.Remove(拍水面板);
表格.Controls.Remove(专一面板);
表格.Controls.Remove(窗口期面板);
表格.Controls.Remove(目标面板);
if (s == "控制")
{刷新(按钮固定控制, 1);
放置(表格, 控制面板, 0, 5, 6, 5);
}
else if (s == "模式")
{刷新(按钮固定模式, 1);
放置(表格, 模式面板, 0, 5, 6, 5);
}
else if (s == "阶段1")
{刷新(按钮固定阶段1, 1);
放置(表格, 阶段1面板, 0, 5, 6, 5);
}
else if (s == "阶段2")
{刷新(按钮固定阶段2, 1);
放置(表格, 阶段2面板, 0, 5, 6, 5);
}
else if (s == "阶段3")
{刷新(按钮固定阶段3, 1);
放置(表格, 阶段3面板, 0, 5, 6, 5);
}
else if (s == "阶段4")
{刷新(按钮固定阶段4, 1);
放置(表格, 阶段4面板, 0, 5, 6, 5);
}
else if (s == "手法")
{刷新(按钮固定手法, 1);
放置(表格, 手法面板, 0, 5, 6, 5);
}
else if (s == "拍水")
{刷新(按钮固定拍水, 1);
放置(表格, 拍水面板, 0, 5, 6, 5);
}
else if (s == "专一")
{刷新(按钮固定专一, 1);
放置(表格, 专一面板, 0, 5, 6, 5);
}
else if (s == "窗口期")
{刷新(按钮固定窗口期, 1);
放置(表格, 窗口期面板, 0, 5, 6, 5);
}
else if (s == "目标")
{刷新(按钮固定目标, 1);
放置(表格, 目标面板, 0, 5, 6, 5);
}
空天姬生成器窗口.ResumeLayout(true);
}
void 刷新(Control c, int i = -1)
{switch (c.Name)
{
case "按钮固定控制":
{状态(按钮固定控制, 2, i);
按钮固定控制.Text = (i == -1 ? "控制\r\n面板" : "双击输入\r\n到游戏");
}
break;
case "按钮固定转换":
{状态(按钮固定转换, 3);
按钮固定转换.Text = (参数变动 ? "生成" : "解析");
}
break;
case "按钮固定模式":
{按钮固定模式.Text =
下拉模式模式.SelectedIndex switch
{1 => "平钓",
2 => "大鱼",
3 => "耐心",
_ => "",
} + "模式";
状态(按钮固定模式, 2, i);
}
break;
case "按钮固定阶段1":
状态(按钮固定阶段1, 2, i);
break;
case "按钮固定阶段2":
状态(按钮固定阶段2, 下拉阶段2竿型.SelectedIndex != 0 ? 2 : 1, i);
break;
case "按钮固定阶段3":
状态(
按钮固定阶段3,
下拉阶段2竿型.SelectedIndex != 0
? (下拉阶段3竿型.SelectedIndex != 0 ? 2 : 1)
: 0,
i
);
break;
case "按钮固定阶段4":
状态(
按钮固定阶段4,
下拉阶段2竿型.SelectedIndex != 0 && 下拉阶段3竿型.SelectedIndex != 0
? (下拉阶段4竿型.SelectedIndex != 0 ? 2 : 1)
: 0,
i
);
break;
case "按钮固定全局手法":
状态(
按钮固定手法,
(
获取勾选状态(文本手法勾选不撒饵)
|| 获取勾选状态(文本手法勾选不收集)
|| 获取勾选状态(文本手法勾选大鱼知识)
|| 获取勾选状态(文本手法勾选收藏品)
|| 获取勾选状态(文本手法勾选钓组)
|| 获取勾选状态(文本手法勾选鱼眼)
|| 下拉手法引诱.SelectedIndex + 下拉手法特殊.SelectedIndex != 0
)
? 2
: 1,
i
);
break;
case "按钮固定拍水":
状态(按钮固定拍水, 检查输入拍水鱼名 == 1 ? 2 : 1, i);
break;
case "按钮固定专一":
状态(按钮固定专一, 检查输入专一鱼名 == 1 ? 2 : 1, i);
break;
case "按钮固定窗口期":
状态(
按钮固定窗口期,
检查输入窗口期时间段 == 1 || 检查输入窗口期天气名 == 1 ? 2 : 1,
i
);
break;
case "按钮固定目标":
状态(按钮固定目标, 检查输入目标鱼名 == 1 || 检查输入目标计数 == 1 ? 2 : 1, i);
break;
case "输入固定表达式":
状态(输入固定表达式, 1);
break;
case "文本固定报错1":
状态(文本固定报错1);
break;
case "文本固定报错2":
状态(文本固定报错2);
break;
case "文本固定提示":
状态(文本固定提示);
break;
case "文本控制标题":
状态(文本控制标题, 2);
break;
case "文本控制预设名":
状态(文本控制预设名, 检查输入控制预设名 == 1 ? 1 : 0);
break;
case "文本控制提示":
状态(文本控制提示, 0);
break;
case "按钮控制启动":
状态(按钮控制启动, 检查输入控制预设名 == 1 ? 3 : 1);
break;
case "按钮控制获取并解析":
状态(按钮控制获取并解析, 3);
break;
case "按钮控制重置":
状态(按钮控制重置, 获取勾选状态(文本控制勾选确认重置) ? 3 : 1);
break;
case "输入控制预设名":
状态(输入控制预设名, 1);
break;
case "文本控制勾选确认重置":
状态(文本控制勾选确认重置, 0, 1);
break;
case "文本模式标题":
状态(文本模式标题, 2);
break;
case "文本模式模式":
状态(文本模式模式, 2);
break;
case "下拉模式模式":
状态(下拉模式模式, 1);
break;
case "文本手法标题":
状态(文本手法标题, 2);
break;
case "文本手法引诱":
状态(文本手法引诱, 下拉手法引诱.SelectedIndex != 0 ? 1 : 0);
break;
case "文本手法特殊":
状态(文本手法特殊, 下拉手法特殊.SelectedIndex != 0 ? 1 : 0);
break;
case "下拉手法引诱":
状态(下拉手法引诱, 下拉手法引诱.SelectedIndex != 0 ? 1 : 0);
break;
case "下拉手法特殊":
状态(下拉手法特殊, 下拉手法特殊.SelectedIndex != 0 ? 1 : 0);
break;
case "文本手法勾选不撒饵":
状态(文本手法勾选不撒饵, 0, 1);
break;
case "文本手法勾选收藏品":
状态(文本手法勾选收藏品, 0, 1);
break;
case "文本手法勾选大鱼知识":
