break;
case "文本阶段1竿时区间":
状态(文本阶段1竿时区间, 2);
break;
case "文本阶段1最短":
状态(文本阶段1最短, 1);
break;
case "文本阶段1最长":
状态(文本阶段1最长, 1);
break;
case "文本阶段1撒饵":
