void 状态(Control c, int x = 0, int y = -1)
{if (c is TextBox t)
{if (y != -1)
{t.ForeColor = x switch
{0 => 警示蓝,
1 => 警示黄,
2 => 警示红,
_ => 灰色,
};
}
else
{t.ForeColor = x switch
{0 => 灰色,
1 => 白色,
_ => 灰色,
};
t.BackColor = x switch
{0 => 输入框灰蓝色,
1 => 输入框深蓝色,
_ => 输入框灰蓝色,
};
}
}
else if (c is ComboBox cb)
{cb.ForeColor = (cb.SelectedIndex == 0) ? 灰色 : 激活金;
}
else if (c is Label l)
{l.ForeColor = x switch
{0 => 灰色,
1 => 激活金,
2 => 标题蓝灰,
_ => 灰色,
};
if (y != -1)
l.Text = (x == 1 ? "▣ " : "▢ ") + c.Text.Replace("▣ ", "").Replace("▢ ", "");
}
else if (c is Button b)
{b.ForeColor = x switch
{0 => 灰色,
1 => 灰色,
2 => 白色,
3 => 激活金,
_ => 灰色,
};
b.BackColor = x switch
{0 => 深灰色,
>= 1 => y != -1 ? 按钮蓝色按下 : 按钮蓝色,
_ => 深灰色,
};
}
}
