void 处理撒饵竿时(TextBox 撒饵框, TextBox 非撒饵框, string 撒饵, string 非撒饵)
{if (double.TryParse(撒饵, out double 撒饵转换))
{double d = (
double.TryParse(非撒饵, out double 非撒饵时间) && 非撒饵时间 != 0
? 撒饵转换
: Math.Round(撒饵转换 * 0.6, 1)
);
撒饵框.Text = d.ToString();
}
else
撒饵框.Text = "";
if (double.TryParse(撒饵, out double 撒饵时间))
{double d = (
double.TryParse(非撒饵, out double 非撒饵时间) && 非撒饵时间 != 0
? 非撒饵时间
: 撒饵时间
);
非撒饵框.Text = d.ToString();
}
else
非撒饵框.Text = "";
}
void 处理竿型(ComboBox c, string h)
{
string processedStr = (h ?? "")
.Replace('！', '!')
.Replace('＋', '+')
.ToLowerInvariant();
int offset = c == 下拉阶段1竿型 || c.Name.Contains("额外") ? 0 : 1;
const int AllIndex = 0;
const int SingleExclamation = 1;
const int DoubleExclamation = 2;
const int TripleExclamation = 3;
const int SinglePlusDouble = 4;
const int SinglePlusTriple = 5;
const int DoublePlusTriple = 6;
if (processedStr == "全部" || processedStr == "all")
{c.SelectedIndex = AllIndex + offset;
return;
}
var counts = processedStr
.Split('+')
.Select(p => p.Count(ch => ch == '!'))
.OrderBy(n => n)
.ToList();
if (counts.Count == 3 && counts[0] == 1 && counts[1] == 2 && counts[2] == 3)
{c.SelectedIndex = AllIndex + offset;
return;
}
if (counts.Count == 1)
{switch (counts[0])
{case 1:
c.SelectedIndex = SingleExclamation + offset;
return;
case 2:
c.SelectedIndex = DoubleExclamation + offset;
return;
case 3:
c.SelectedIndex = TripleExclamation + offset;
return;
}
}
else if (counts.Count == 2)
{if (counts[0] == 1 && counts[1] == 2)
{c.SelectedIndex = SinglePlusDouble + offset;
return;
}
else if (counts[0] == 1 && counts[1] == 3)
{c.SelectedIndex = SinglePlusTriple + offset;
return;
}
else if (counts[0] == 2 && counts[1] == 3)
{c.SelectedIndex = DoublePlusTriple + offset;
return;
}
}
c.SelectedIndex = 0;
}
void 处理技巧提钩(ComboBox c, string h)
{if (c != 下拉专一提钩)
c.SelectedIndex = h switch
{"精准" => 1,
"双重" or "双提" => 2,
"三重" or "三提" => 3,
_ => 0,
};
else
c.SelectedIndex = h switch
{"双重" or "双提" => 1,
"三重" or "三提" => 2,
_ => 0,
};
}
void 处理手法()
{状态(文本手法勾选不撒饵, 空天姬目标.Contains("不撒饵") ? 1 : 0, 1);
状态(文本手法勾选收藏品, 空天姬目标.Contains("收藏品") ? 1 : 0, 1);
状态(
文本手法勾选大鱼知识,
空天姬目标.Contains("收藏品") && 空天姬目标.Contains("知识") ? 1 : 0,
1
);
状态(
文本手法勾选不收集,
空天姬目标.Contains("收藏品") && 空天姬目标.Contains("不收集") ? 1 : 0,
1
);
状态(文本手法勾选鱼眼, 空天姬目标.Contains("鱼眼") ? 1 : 0, 1);
状态(文本手法勾选钓组, 空天姬目标.Contains("钓组") ? 1 : 0, 1);
if (空天姬目标.Contains("引诱"))
下拉手法引诱.SelectedIndex = 1;
else if (空天姬目标.Contains("鱼影"))
下拉手法引诱.SelectedIndex = 2;
else if (空天姬目标.Contains("重随"))
下拉手法引诱.SelectedIndex = 3;
else
下拉手法引诱.SelectedIndex = 0;
if (空天姬目标.Contains("攒鱼计"))
下拉手法特殊.SelectedIndex = 1;
else if (空天姬目标.Contains("套娃"))
下拉手法特殊.SelectedIndex = 2;
else if (空天姬目标.Contains("多提"))
下拉手法特殊.SelectedIndex = 3;
else
下拉手法特殊.SelectedIndex = 0;
}
string 处理目标(string s)
{Match 结果 = 鱼名匹配.Match(s);
string ss = 结果.Groups[1].Value;
if (string.IsNullOrEmpty(ss))
return "";
if (ss == "《" || ss == "<")
{return "《";
}
string[] 手法词组 =
{"不撒饵",
"收藏",
"攒鱼计",
"套娃",
"知识",
"引诱",
"重随",
"鱼眼",
"鱼影",
"钓组",
"不收集",
"多提",
};
if (手法词组.Any(k => ss.Contains(k)))
{return "";
}
return ss;
}
}
void 生成()
{StringBuilder sb = new();
if (下拉模式模式.SelectedIndex == 0)
{string mode = "";
if (按钮固定阶段2.ForeColor == 白色)
{mode = "大鱼";
if (按钮固定阶段3.ForeColor == 白色)
{mode = "耐心";
}
}
else
{mode = "平钓";
}
sb.Append(mode);
}
else
sb.Append(
下拉模式模式.SelectedIndex switch
{1 => "平钓",
2 => "大鱼",
3 => "耐心",
_ => "平钓",
}
);
if (!string.IsNullOrWhiteSpace(输入阶段1钓饵.Text))
{sb.Append("（");
sb.Append(输入阶段1钓饵.Text);
sb.Append("）");
}
添加额外竿型(下拉阶段1额外竿型);
if (!string.IsNullOrWhiteSpace(输入拍水鱼名.Text))
{sb.Append("拍 ");
if (
!string.IsNullOrWhiteSpace(输入拍水非撒饵最短.Text)
|| !string.IsNullOrWhiteSpace(输入拍水非撒饵最长.Text)
)
{sb.Append(
!string.IsNullOrWhiteSpace(输入拍水非撒饵最短.Text)
? (
(
!string.IsNullOrWhiteSpace(输入拍水撒饵最短.Text)
? 输入拍水撒饵最短.Text + "+"
: ""
) + 输入拍水非撒饵最短.Text
)
: ""
);
sb.Append("~");
sb.Append(
!string.IsNullOrWhiteSpace(输入拍水非撒饵最长.Text)
? (
(
!string.IsNullOrWhiteSpace(输入拍水撒饵最长.Text)
? 输入拍水撒饵最长.Text + "+"
: ""
) + 输入拍水非撒饵最长.Text
)
: ""
);
}
添加竿型(下拉拍水竿型, 下拉拍水提钩);
处理鱼名(输入拍水鱼名);
if (文本拍水勾选鱼篓.ForeColor == 激活金)
