}
}
处理鱼名(输入目标鱼名);
var segments = new List<string>();
if (获取勾选状态(文本手法勾选不撒饵))
segments.Add("不撒饵");
if (获取勾选状态(文本手法勾选收藏品))
segments.Add("收藏品");
if (获取勾选状态(文本手法勾选大鱼知识))
segments.Add("知识");
if (获取勾选状态(文本手法勾选不收集))
segments.Add("不收集");
if (获取勾选状态(文本手法勾选鱼眼))
segments.Add("鱼眼");
if (获取勾选状态(文本手法勾选钓组))
segments.Add("钓组");
if (下拉手法引诱.SelectedIndex != 0)
{var value = 下拉手法引诱.SelectedIndex switch
{1 => "引诱",
2 => "鱼影",
3 => "引诱",
_ => null,
};
if (value != null)
segments.Add(value);
}
if (segments.Count > 0)
{sb.Append(string.Join("、", segments));
}
var specialValue = 下拉手法特殊.SelectedIndex switch
{1 => "攒鱼计",
2 => "套娃",
3 => "多提",
_ => null,
};
if (specialValue != null)
{
sb.Append(sb.Length > 0 ? "、" : "");
sb.Append(specialValue);
}
}
if (!string.IsNullOrWhiteSpace(输入专一鱼名.Text))
{sb.Append("=专一");
处理鱼名(输入专一鱼名);
if (下拉专一提钩.SelectedIndex != 0)
{sb.Append("=");
添加技巧(下拉专一提钩);
}
}
输入固定表达式.Text = sb.ToString().TrimEnd('、');
输入固定表达式.SelectionStart = 0;
void 添加竿型(ComboBox c, ComboBox cc = null)
{if (c.SelectedIndex == 0 && c != 下拉阶段1竿型)
return;
else
{int i =
c.SelectedIndex + (c == 下拉阶段1竿型 || c.Name.Contains("额外") ? 1 : 0);
sb.Append(
i switch
{1 => "全部",
2 => "！",
3 => "！！",
4 => "！！！",
5 => "！+！！",
6 => "！+！！！",
7 => "！！+！！！",
_ => "全部",
}
);
if (cc != null)
添加技巧(cc);
}
}
void 添加额外竿型(ComboBox c)
{if (c.SelectedIndex == 0)
return;
else
{sb.Append(
(c.SelectedIndex + 1) switch
{2 => "（！）",
3 => "（！！）",
4 => "（！！！）",
_ => "",
}
);
}
}
void 添加技巧(ComboBox c)
{sb.Append(
(c.SelectedIndex + (c == 下拉专一提钩 ? 1 : 0)) switch
{1 => "精准",
2 => "双提",
3 => "三提",
_ => "",
}
);
}
void 处理鱼名(TextBox t)
{string tt = t.Text.Trim('、').Trim('【').Trim('】');
if (string.IsNullOrWhiteSpace(tt))
return;
else
{sb.Append("【");
sb.Append(鱼名匹配.Match(tt).ToString());
sb.Append("】");
}
}
void 处理鱼篓(TextBox t)
{if (!string.IsNullOrWhiteSpace(t.Text))
sb.Append("《");
处理鱼名(t);
}
bool 检查ET(TextBox t)
{
string input = t.Text.Trim();
if (input.Length != 4)
return false;
if (!Regex.IsMatch(input, @"^\d{4}$"))
return false;
if (!int.TryParse(input, out int numericValue))
return false;
return numericValue > -1 && numericValue < 2401;
}
}
void 输入预设(object sender, MouseEventArgs e)
{string p = 输入控制预设名.Text;
if (
(e.Button == MouseButtons.Left && e.Clicks == 2)
|| (e.Button == MouseButtons.Middle && e.Clicks == 1)
)
{RealPlugin.plug.InvokeNamedCallback(
"command",
默语
+ "空天姬预设 "
+ ((e.Button == MouseButtons.Middle && e.Clicks == 1) ? "查看 " : "")
+ p
);
}
}
void 输入鱼名_KeyDown(object sender, KeyEventArgs e)
{if (e.KeyCode == Keys.Enter)
{
MouseEventArgs leftDoubleClickEventArgs = new(MouseButtons.Left, 2, 0, 0, 0);
输入预设(sender, leftDoubleClickEventArgs);
}
}
void 输入表达式()
{输入固定表达式.Text = 输入固定表达式.Text.Trim().Replace("/e ", "");
RealPlugin.plug.InvokeNamedCallback("command", 默语 + 输入固定表达式.Text);
}
void 绘制下拉框(object sender, DrawItemEventArgs e)
{
if (e.Index == -1)
{
e.Graphics.FillRectangle(new SolidBrush(e.BackColor), e.Bounds);
return;
}
e.Graphics.FillRectangle(new SolidBrush(e.BackColor), e.Bounds);
StringFormat stringFormat =
new() { Alignment = StringAlignment.Near, LineAlignment = StringAlignment.Center };
e.Graphics.DrawString(
((ComboBox)sender).Items[e.Index].ToString(),
e.Font,
new SolidBrush(e.ForeColor),
e.Bounds.X,
e.Bounds.Y
);
using Pen borderPen = new(Color.FromArgb(41, 60, 107));
e.Graphics.DrawRectangle(
borderPen,
e.Bounds.X,
e.Bounds.Y,
e.Bounds.Width - 1,
e.Bounds.Height - 1
);
}
void 添加下拉前缀(ComboBox comboBox)
{int selectedIndex = comboBox.SelectedIndex;
List<string> newItems = new();
foreach (var item in comboBox.Items)
{if (item is string strItem)
{
if (!strItem.StartsWith("▶"))
{newItems.Add($"▶ {strItem}");
}
else
{newItems.Add(strItem);
}
}
}
comboBox.Items.Clear();
comboBox.Items.AddRange(newItems.ToArray());
if (selectedIndex >= 0 && selectedIndex < comboBox.Items.Count)
{comboBox.SelectedIndex = selectedIndex;
}
else
{comboBox.SelectedIndex = 0;
}
}
void 放置(TableLayoutPanel t, Control c, int x, int y, int w = 1, int h = 1)
{设置通用样式(c);
t.Controls.Add(c, x, t == 表格 ? y : y - 5);
t.SetColumnSpan(c, w);
t.SetRowSpan(c, h);
}
void 获取预设()
{if (字典.ContainsKey(输入控制预设名.Text))
{输入固定表达式.Text = 字典.GetValue(输入控制预设名.Text).ToString();
解析();
当前分页 = "阶段1";
切换分页("阶段1");
}
else
{输入固定表达式.Text = "预设名不存在";
}
}
bool 获取勾选状态(Label l)
{return l.Text.StartsWith("▣");
}
void 切换勾选状态(Label l)
{状态(l, 获取勾选状态(l) ? 0 : 1, 1);
}
}
public static void 发送日志(string text)
{var tri = new Trigger();
fakectx.trig = tri;
RealPlugin.plug.QueueAction(
fakectx,
fakectx.trig,
m: null,
new Triggernometry.Action
{ActionType = Triggernometry.Action.ActionTypeEnum.LogMessage.ToString(),
LogMessageText = $"{text}",
LogProcess = "true",
},
DateTime.Now,
releaseMutex: true
);
}
