bool initialized = false;
for (int i = 1; i <= 5; i++)
{try
{MissFisherExtension.Init();
initialized = true;
break;
}
catch
{MissFisherExtension.Log(
RealPlugin.DebugLevelEnum.Warning,
$"空天姬扩展初始化失败，正在重试第 {i} / 5 次……"
);
if (i == 5)
throw;
}
Thread.Sleep(i * 1000);}
public static class MissFisherExtension
{private static readonly string Name = "MissFisherExtension";
public static PostNamazu.PostNamazu NamazuPlugin =>
(PostNamazu.PostNamazu)RealPlugin.InstanceHook(null, "PostNamazu.PostNamazu").pluginObj;
public static ExternalProcessMemory Memory => NamazuPlugin?.Memory;
public static SigScanner Scanner => NamazuPlugin?.SigScanner;
private static PostNamazuUi _postNamazuUi;
public static PostNamazuUi PostNamazuUi
{get
{if (_postNamazuUi?.Parent as TabPage == null)
{_postNamazuUi = GetPostNamazuUi();
}
return _postNamazuUi;
}
}
public static bool IsReady =>
Triggernometry.Interpreter.StaticHelpers.XivProcess != null && Memory != null;
public static bool IsValidPtr(IntPtr? ptr) => ptr.HasValue && ptr.Value != IntPtr.Zero;
private static PostNamazuUi GetPostNamazuUi()
{TabPage tp = RealPlugin.InstanceHook(null, "PostNamazu.PostNamazu").TabPage;
return tp.Controls.OfType<PostNamazuUi>().FirstOrDefault();
}
public static void OnXivProcUpdate()
{NamazuUiLog($"空天姬初始化：开始扫描内存~");
ScanSignatures();
}
public static void RegisterCallback(
string callBackName,
Action<string> callBackAction,
params IntPtr?[] ptrs
)
{if (ptrs == null || ptrs.Length == 0 || ptrs.Any(p => !IsValidPtr(p)))
{Log(
RealPlugin.DebugLevelEnum.Error,
$"注册回调 {callBackName} 失败：指针无效 (ptr == IntPtr.Zero)"
);
return;
}
Triggernometry.RealPlugin.plug.RegisterNamedCallback(
callBackName,
new Action<object, string>((_, cmd) => callBackAction(cmd)),
null,
registrant: Name
);
}
public static void Init()
{OnXivProcUpdate();
Triggernometry.Interpreter.StaticHelpers.RegisterXivProcessUpdatedAction(
Name,
new System.Action(OnXivProcUpdate)
);
RegisterCallback("KTJChangeBaitAuto", FishingHelper.ChangeBaitAuto, ExecuteCommandPtr);
RegisterCallback("KTJChangeBaitById", FishingHelper.ChangeBaitById, ExecuteCommandPtr);
RegisterCallback(
"KTJChangeSwimBaitByIndex",
FishingHelper.ChangeSwimBaitByIndex,
ExecuteCommandPtr
);
RegisterCallback(
"KTJGetCurrentBait",
FishingHelper.GetCurrentBait,
PlayerStatePtr,
FishManagerPtr
);
RegisterCallback(
"KTJGetCurrentWeather",
FishingHelper.GetCurrentWeather,
WeatherManagerPtr
);
RegisterCallback("KTJUseItem", FishingHelper.UseItem, ActionManagerPtr, UseActionPtr);
RegisterCallback(
"KTJObtainOceanFishing",
FishingHelper.ObtainOceanFishing,
EventFrameworkPtr,
GetInstanceContentDirectorPtr
);
if (FishingHelper.CanChangeBait())
{Echo($"Activated: MissFisherMemoryComponents\r\n{Name} v0.13 By Nyy");
}
else
{Echo($"");
Echo($"{Name} 初始化失败！");
}
}
public static void CheckBeforeExecution()
{if (!IsReady)
throw new Exception("KTJCheckBeforeExecution 没有对应的 FFXIV 进程");
}
public static IntPtr ExecuteCommandPtr;
public static IntPtr PlayerStatePtr;
public static IntPtr WeatherManagerPtr;
public static IntPtr ActionManagerPtr;
public static IntPtr UseActionPtr;
public static IntPtr EventFrameworkPtr;
public static IntPtr FishManagerPtr;
public static IntPtr GetInstanceContentDirectorPtr;
public static void ScanSignatures()
{ExecuteCommandPtr = ScanSignatureWithFallback(
nameof(ExecuteCommandPtr),
"E8 * * * * 48 83 C4 ?? C3 CC CC CC CC CC CC CC CC CC CC CC CC 48 83 EC ?? 45 0F B6 C0",
"E8 * * * * 48 8B 06 48 8B CE FF 50 ? E9 ? ? ? ? 49 8B CC"
);
PlayerStatePtr = Scanner.ScanText(
"48 8D 0D * * * * E8 ?? ?? ?? ?? 84 C0 75 06 F6 43 18 02",
nameof(PlayerStatePtr)
);
WeatherManagerPtr = Scanner.ScanText(
"48 8D 0D * * * * 44 0F B7 45",
nameof(WeatherManagerPtr)
);
var eventFrameworkPtrPtr = ScanSignatureWithFallback(
nameof(EventFrameworkPtr),
"48 8B 35 * * * * 0F B6 EA 4C 8B F1", 
"4C 39 2D * * * * 74 14" 
);
EventFrameworkPtr = Memory.Read<IntPtr>(eventFrameworkPtrPtr);
FishManagerPtr = Memory.Read<IntPtr>(EventFrameworkPtr + 0x70);
GetInstanceContentDirectorPtr = ScanSignatureWithFallback(
nameof(GetInstanceContentDirectorPtr),
"E8 * * * * 0F B6 98"
);
ActionManagerPtr = ScanSignatureWithFallback(
nameof(ActionManagerPtr),
"48 8D 0D * * * * F3 0F 10 13",
"48 8D 0D * * * * 0F B7 46 18 "
);
UseActionPtr = ScanSignatureWithFallback(
nameof(UseActionPtr),
"E8 * * * * 41 89 9E ?? ?? ?? ?? EB 0B",
"E8 * * * * E9 ?? ?? ?? ?? 4C 89 64 24 ?? 49 8D 4F 20",
"E8 * * * * 33 C0 48 8B 5C 24 ?? 48 8B 6C 24 ?? 48 8B 74 24 ?? 48 8B 7C 24 ?? 48 83 C4 50"
);
}
private static IntPtr ScanSignatureWithFallback(string name, params string[] patterns)
{foreach (var pattern in patterns)
{try
{return Scanner.ScanText(pattern, name);
}
catch { }
}
Log(
RealPlugin.DebugLevelEnum.Warning,
$"ScanSignatureWithFallback: `{name}` 提供的 {patterns.Length} 条备用Sig均扫描失败/返回空指针。"
);
return IntPtr.Zero;
}
public static class FishingHelper
{public static bool CanChangeBait()
{return !(ExecuteCommandPtr == null || ExecuteCommandPtr == IntPtr.Zero);
}
public static void ChangeBaitAuto(string cmd)
{CheckBeforeExecution();
if (uint.TryParse(cmd, out var swimIndex) && swimIndex is >= 0 and < 3)
{ChangeSwimBaitByIndex(cmd);
}
else
{ChangeBaitById(cmd);
}
}
public static void ChangeBaitById(string cmd)
{CheckBeforeExecution();
if (uint.TryParse(cmd, out var baitId))
{NamazuUiLog($"KTJChangeBait: baitId = {baitId}");
Memory.CallInjected64<IntPtr>(ExecuteCommandPtr, 701, 4, (int)baitId, 0, 0);
}
}
public static void ChangeSwimBaitByIndex(string cmd)
{CheckBeforeExecution();
if (uint.TryParse(cmd, out var swimIndex) && swimIndex is >= 0 and < 3)
{NamazuUiLog($"KTJChangeSwimBait: swimIndex = {swimIndex}");
Memory.CallInjected64<IntPtr>(ExecuteCommandPtr, 701, 25, (int)swimIndex, 0, 0);
}
}
public static void GetCurrentBait(string cmd)
{CheckBeforeExecution();
var currentBaitId = Memory.Read<uint>(PlayerStatePtr + 0x4A4);
var currentSelectedSwimIndex = Memory.Read<byte>(FishManagerPtr + 0x234);
var swimBaits = new[]
{Memory.Read<uint>(FishManagerPtr + 0x238),
Memory.Read<uint>(FishManagerPtr + 0x23C),
Memory.Read<uint>(FishManagerPtr + 0x240),
};
if (currentSelectedSwimIndex < 3)
{var switBait = swimBaits[currentSelectedSwimIndex];
if (switBait != 0)
{currentBaitId = switBait;
}
}
Triggernometry.Interpreter.StaticHelpers.SetScalarVariable(
isPersistent: false,
"空天姬控制钓饵",
currentBaitId
);
}
public static void GetCurrentWeather(string cmd)
{CheckBeforeExecution();
var currentWeather = Memory.Read<byte>(WeatherManagerPtr + 0x64);
Triggernometry.Interpreter.StaticHelpers.SetScalarVariable(
isPersistent: false,
"空天姬天气ID",
currentWeather
);
}
public static void UseItem(string cmd)
{CheckBeforeExecution();
if (!uint.TryParse(cmd, out var itemId))
return;
var assemblyLock = Memory.Executor.AssemblyLock;
var flag = false;
try
{Monitor.Enter(assemblyLock, ref flag);
if (
Memory.CallInjected64<bool>(
UseActionPtr,
ActionManagerPtr,
(int)2,
itemId,
(ulong)0xE000_0000,
(uint)0xFFFF,
(int)0,
(uint)0,
0
)
)
{NamazuUiLog($"KTJUseItem: itemId = {itemId}");
}
}
finally
{if (flag)
Monitor.Exit(assemblyLock);
}
}
public static void ObtainOceanFishing(string cmd)
{CheckBeforeExecution();
var directorPtr = Memory.CallInjected64<IntPtr>(
GetInstanceContentDirectorPtr,
EventFrameworkPtr
);
if (directorPtr == IntPtr.Zero)
{MissFisherExtension.Log(
RealPlugin.DebugLevelEnum.Error,
$"{Name}: ContentDirector获取失败，现在真在副本中吗？"
);
return;
}
var directorType1 = Memory.Read<ushort>(directorPtr + 0xDCE); 
var directorType2 = Memory.Read<ushort>(directorPtr + 0xE1E); 
if (directorType1 != 0x10 && directorType2 != 0x10)
{MissFisherExtension.Log(
RealPlugin.DebugLevelEnum.Error,
$"{Name}: 现在真在海钓吗？({directorType1}|{directorType2})"
);
return;
}
if (directorType1 == 0x10)
{
var status = Memory.Read<uint>(directorPtr + 0x1DBC);
var currentRoute = Memory.Read<uint>(directorPtr + 0x1DB8);
var currentZone = Memory.Read<uint>(directorPtr + 0x1DC0);
var spectralCurrentActive = Memory.Read<bool>(directorPtr + 0x1DD0);
var weatherId = Memory.Read<uint>(directorPtr + 0x1DCC);
var contentTimeLeft = Memory.Read<float>(directorPtr + 0xCF0);
var timeOffset = Memory.Read<uint>(directorPtr + 0x1DC8);
var timeLeft = contentTimeLeft - timeOffset; 
var entsAt = DateTimeOffset.UtcNow.ToUnixTimeMilliseconds() + (int)timeLeft * 1000;
var playerAllPoint = Memory.Read<ushort>(directorPtr + 0x220C + 0x4);
Triggernometry.Interpreter.StaticHelpers.SetScalarVariable(
isPersistent: false,
"空天姬控制海钓状态",
status
); 
Triggernometry.Interpreter.StaticHelpers.SetScalarVariable(
isPersistent: false,
"空天姬控制海钓当前路线",
currentRoute
); 
Triggernometry.Interpreter.StaticHelpers.SetScalarVariable(
isPersistent: false,
"空天姬控制海钓当前海域",
currentZone
); 
Triggernometry.Interpreter.StaticHelpers.SetScalarVariable(
isPersistent: false,
"空天姬控制海钓是否幻海中",
spectralCurrentActive
);
Triggernometry.Interpreter.StaticHelpers.SetScalarVariable(
isPersistent: false,
"空天姬控制海钓当前天气ID",
weatherId
);
Triggernometry.Interpreter.StaticHelpers.SetScalarVariable(
isPersistent: false,
"空天姬控制海钓当前海域结束时间",
entsAt
); 
Triggernometry.Interpreter.StaticHelpers.SetScalarVariable(
isPersistent: false,
"空天姬控制海钓结束时总分数",
playerAllPoint
); 
return;
}
if (directorType2 == 0x10)
{var status = Memory.Read<uint>(directorPtr + 0x1E0C);
var currentRoute = Memory.Read<uint>(directorPtr + 0x1E08);
var currentZone = Memory.Read<uint>(directorPtr + 0x1E10);
var spectralCurrentActive = Memory.Read<bool>(directorPtr + 0x1E20);
var weatherId = Memory.Read<uint>(directorPtr + 0x1E1C);
var contentTimeLeft = Memory.Read<float>(directorPtr + 0xD40);
var timeOffset = Memory.Read<uint>(directorPtr + 0x1E18);
var timeLeft = contentTimeLeft - timeOffset; 
var entsAt = DateTimeOffset.UtcNow.ToUnixTimeMilliseconds() + (int)timeLeft * 1000;
var playerAllPoint = Memory.Read<ushort>(directorPtr + 0x225C + 0x4);
Triggernometry.Interpreter.StaticHelpers.SetScalarVariable(
isPersistent: false,
"空天姬控制海钓状态",
status
); 
Triggernometry.Interpreter.StaticHelpers.SetScalarVariable(
isPersistent: false,
"空天姬控制海钓当前路线",
currentRoute
); 
Triggernometry.Interpreter.StaticHelpers.SetScalarVariable(
isPersistent: false,
"空天姬控制海钓当前海域",
currentZone
); 
Triggernometry.Interpreter.StaticHelpers.SetScalarVariable(
isPersistent: false,
"空天姬控制海钓是否幻海中",
spectralCurrentActive
);
Triggernometry.Interpreter.StaticHelpers.SetScalarVariable(
isPersistent: false,
"空天姬控制海钓当前天气ID",
weatherId
);
Triggernometry.Interpreter.StaticHelpers.SetScalarVariable(
isPersistent: false,
"空天姬控制海钓当前海域结束时间",
entsAt
); 
Triggernometry.Interpreter.StaticHelpers.SetScalarVariable(
isPersistent: false,
"空天姬控制海钓结束时总分数",
playerAllPoint
); 
return;
}
}
}
public static void Echo(string msg) =>
RealPlugin.plug.InvokeNamedCallback("command", $"/e {msg}");
public static void Log(RealPlugin.DebugLevelEnum level, string msg) =>
Interpreter.StaticHelpers.Log(level, msg);
public static void NamazuUiLog(string msg) => PostNamazuUi?.Log($"{Name}: {msg}");}

