void 刷新主题诗句()
{DateTime 周年起始日期 = new(2024, 3, 27);
DateTime 国家公祭日 = new(2014, 3, 27);
var 当前日期 = DateTime.Now;
bool 是国家公祭日 =
当前日期.Month == 国家公祭日.Month && 当前日期.Day == 国家公祭日.Day && _lang == 1;
int 周年数 = 当前日期.Year - 周年起始日期.Year;
if (当前日期 < 周年起始日期.AddYears(周年数))
{周年数--; 
}
string 周年 = 转换为汉字(周年数);
if (当前日期.Month == 周年起始日期.Month && 当前日期.Day == 周年起始日期.Day)
{if (_lang == 1)
延时更新首次提示语($"空 天 姬 {周年} 周 年 啦 ！");
if (_lang == 3)
延时更新首次提示语($"「MissFisher」の{周年数}周年記念！");
else
延时更新首次提示语(
$"MissFisher's {周年数}{GetOrdinalSuffix(周年数)} Anniversary!"
);
}
所有诗句.Clear();
if (_lang == 1)
{if (是国家公祭日)
{延时更新首次提示语(
"昭 昭 前 事  惕 惕 后 人\r\n1 2 . 1 3\r\n中 国 国 家 公 祭 日"
);
