int 计算主界面主题(int 自定义主题 = 0)
{var 配置字典 = GetDictVariable(true, "空天姬配置") ?? new VariableDictionary();
string 主题 = 配置字典.GetValue("主界面主题")?.ToString() ?? "0";
if (自定义主题 != 0)
{return 自定义主题;
}
if (主题 != "0" && 主题 != "")
{return int.Parse(主题);
}
DateTime 当前日期 = DateTime.Today;
int 当前年份 = 当前日期.Year;
var 日期范围列表 = new List<(DateTime 起始日期, DateTime 结束日期, int 主题)>
{(new DateTime(当前年份, 1, 1), new DateTime(当前年份, 3, 5), 5), 
(new DateTime(当前年份, 3, 27), new DateTime(当前年份, 3, 27), 8), 
(new DateTime(当前年份, 12, 13), new DateTime(当前年份, 12, 13), 2), 
(new DateTime(当前年份, 12, 31), new DateTime(当前年份, 12, 31), 5), 
};
foreach (var 范围 in 日期范围列表)
{if (当前日期 >= 范围.起始日期 && 当前日期 <= 范围.结束日期)
{if (_lang != 1 && 范围.主题 == 2)
return 1;
else
return 范围.主题;
}
}
return 1;
}
