void 初始化预设关联控件内容()
{字典 = GetDictVariable(true, "空天姬预设");
说明 = 字典.GetValue("说明")?.ToString() ?? "";
赞助 = 字典.GetValue("赞助")?.ToString() ?? "";
预设 = 字典
.Values.Keys.Where(item =>
!string.IsNullOrEmpty(item)
&& !char.IsDigit(item[0])
&& !item.StartsWith("空天姬")
)
.ToList();
设置提示(
输入鱼名,
获取翻译("输入提示")
+ Environment.NewLine
+ 说明.Replace("\\r\\n", Environment.NewLine)
);
输入鸣谢.Text = 赞助.Replace("\\r\\n", Environment.NewLine);
}
void 设置控件关系()
{ 
空天姬主界面.Controls.Add(表格);
表格.ColumnCount = 4; 
表格.RowCount = 7; 
Graphics graphics = Graphics.FromHwnd(IntPtr.Zero);
float dpiX = graphics.DpiX;
float dpiY = graphics.DpiY;
graphics.Dispose();
float scaleFactor = dpiY / 96f;
int 行高像素 = (int)(38 * scaleFactor);
int 列宽像素 = (int)(76 * scaleFactor);
for (int i = 0; i < 表格.RowCount; i++)
{表格.RowStyles.Add(new RowStyle(SizeType.Absolute, 行高像素));
}
for (int j = 0; j < 表格.ColumnCount; j++)
{表格.ColumnStyles.Add(new ColumnStyle(SizeType.Absolute, 列宽像素));
}
表格.Controls.Add(按钮启动, 0, 3);
表格.SetRowSpan(按钮启动, 2);
表格.SetColumnSpan(按钮启动, 2);
表格.Controls.Add(下拉海钓, 2, 3);
表格.SetColumnSpan(下拉海钓, 2);
表格.Controls.Add(按钮配置, 3, 1);
表格.Controls.Add(按钮同步, 3, 0);
表格.Controls.Add(按钮航线, 3, 2);
表格.Controls.Add(输入鸣谢, 0, 0);
表格.SetColumnSpan(输入鸣谢, 3);
表格.SetRowSpan(输入鸣谢, 3);
表格.Controls.Add(输入鱼名, 2, 4);
表格.SetColumnSpan(输入鱼名, 2);
表格.Controls.Add(文本诗句, 0, 5);
表格.SetColumnSpan(文本诗句, 4);
表格.SetRowSpan(文本诗句, 2);
}
void 设置控件样式()
{ 
foreach (Control c in 表格.Controls)
{ 
c.Font = 字体;
c.ForeColor = 设置主题颜色(白色);
c.AutoSize = true;
c.Dock = DockStyle.Fill;
文本诗句.Font =
_lang == 1 ? 楷体
: _lang == 3 ? 明朝体
: 罗马体;
文本诗句.ForeColor = 设置主题颜色(标签暗金色);
文本诗句.TextAlign = ContentAlignment.MiddleCenter;
if (c is Button b)
{b.Font = 加粗;
b.FlatStyle = FlatStyle.Flat;
b.BackColor = 设置主题颜色(按钮蓝色);
b.FlatAppearance.BorderColor = 设置主题颜色(按钮黑蓝色);
}
