按钮同步.ForeColor = 设置主题颜色(按钮字体黄色);
按钮航线.BackColor = 设置主题颜色(灰色);
按钮航线.ForeColor = 设置主题颜色(深灰色);
if (c is TextBox t)
{t.Font = 加粗;
t.BorderStyle = BorderStyle.FixedSingle;
t.BackColor = 设置主题颜色(输入框灰蓝色);
}
输入鸣谢.Font = 小号加粗;
输入鸣谢.ForeColor = 设置主题颜色(暗金色);
输入鸣谢.BackColor = 设置主题颜色(输入框深蓝色);
输入鱼名.Font = 字体;
输入鱼名.ForeColor = 设置主题颜色(灰色);
下拉海钓.DropDownStyle = ComboBoxStyle.DropDownList;
下拉海钓.DrawMode = DrawMode.OwnerDrawFixed;
if (下拉海钓.SelectedIndex == -1)
{下拉海钓.SelectedIndex = 0;
}
下拉海钓.BackColor = 设置主题颜色(输入框深蓝色); 
下拉海钓.ForeColor = 设置主题颜色(下拉框淡金色);
下拉海钓.Items.Clear(); 
下拉海钓.Items.AddRange(
new[]
{获取翻译("模式文本1"),
获取翻译("模式文本2"),
获取翻译("模式文本3"),
获取翻译("模式文本4"),
获取翻译("模式文本5"),
}
);
按钮同步.Text = 获取翻译("同步按钮文本");
设置提示(按钮同步, 获取翻译("同步按钮提示"));
按钮配置.Text = 获取翻译("配置按钮文本");
设置提示(按钮配置, 获取翻译("配置按钮提示"));
按钮航线.Text = 获取翻译("航线按钮文本");
设置提示(按钮航线, 获取翻译("航线按钮提示"));
按钮启动.Text = 获取翻译("启动按钮初始文本");
设置提示(按钮启动, 获取翻译("启动按钮初始文本") + "\r\n右键切换悬浮窗状态");
if (输入鱼名.Text == 输入提示)
{var nr = 获取翻译("输入初始提示");
输入鱼名.Text = nr;
输入提示 = nr;
}
设置提示(
输入鱼名,
获取翻译("输入提示")
+ Environment.NewLine
+ 说明.Replace("\\r\\n", Environment.NewLine)
);
设置提示(输入鸣谢, 获取翻译("赞助窗口提示"));
添加下拉前缀(下拉海钓); 
下拉海钓.DrawItem -= 绘制下拉框;
下拉海钓.DrawItem += 绘制下拉框;
}
if (!窗口初始化)
{下拉海钓.SelectedIndexChanged -= 更新按钮文本;
下拉海钓.SelectedIndexChanged += 更新按钮文本;
SetScalarVariable(false, "空天姬控制窗口I", "1");
RealPlugin.plug.InvokeNamedCallback("command", 获取翻译("窗口加载成功默语"));
窗口初始化 = true;
}
}
void 绘制下拉框(object sender, DrawItemEventArgs e)
{
if (e.Index == -1)
{
e.Graphics.FillRectangle(new SolidBrush(e.BackColor), e.Bounds);
return;
}
e.Graphics.FillRectangle(new SolidBrush(e.BackColor), e.Bounds);
StringFormat stringFormat =
new() { Alignment = StringAlignment.Near, LineAlignment = StringAlignment.Center };
e.Graphics.DrawString(
((ComboBox)sender).Items[e.Index].ToString(),
e.Font,
new SolidBrush(e.ForeColor),
e.Bounds.X,
e.Bounds.Y
);
using Pen borderPen = new(设置主题颜色(输入框深蓝色));
e.Graphics.DrawRectangle(
borderPen,
e.Bounds.X,
e.Bounds.Y,
e.Bounds.Width - 1,
e.Bounds.Height - 1
);
}
void 添加下拉前缀(ComboBox comboBox)
{int selectedIndex = comboBox.SelectedIndex;
List<string> newItems = new();
foreach (var item in comboBox.Items)
{if (item is string strItem)
{
if (!strItem.StartsWith("▶"))
{newItems.Add($"▶{strItem}");
}
else
{newItems.Add(strItem); 
}
}
}
comboBox.Items.Clear();
comboBox.Items.AddRange(newItems.ToArray());
if (selectedIndex >= 0 && selectedIndex < comboBox.Items.Count)
{comboBox.SelectedIndex = selectedIndex;
}
else
{comboBox.SelectedIndex = 0;
}
}
void 输入海钓(object sender, MouseEventArgs e)
{switch (e.Button)
{case MouseButtons.Left:
发送日志("空天姬海钓启动：" + (下拉海钓.SelectedIndex + 1));
break;
case MouseButtons.Middle:
if (!string.IsNullOrEmpty(GetScalarVariable(false, "空天姬参数")))
{发送日志("空天姬暂停");
延时更新(获取翻译("已暂停提示"), 1500).ConfigureAwait(false);
}
else
{延时更新(获取翻译("空天姬未启动提示"), 3000).ConfigureAwait(false);
}
break;
case MouseButtons.Right:
发送日志("空天姬切换悬浮窗");
延时更新(获取翻译("已切换提示"), 1500).ConfigureAwait(false);
break;
}
}
void 输入鱼名_KeyDown(object sender, KeyEventArgs e)
{if (e.KeyCode == Keys.Enter)
{ 
MouseEventArgs leftDoubleClickEventArgs = new(MouseButtons.Left, 2, 0, 0, 0);
输入预设(sender, leftDoubleClickEventArgs);
}
}
void 更新颜色(object sender, EventArgs e)
{string[] 参数 = 输入鱼名.Text.Split(
new[] { ' ' },
StringSplitOptions.RemoveEmptyEntries
);
if (
参数.Length > 0
&& 预设.Contains(参数[0])
&& (参数.Length == 1 || (参数.Length == 2 && int.TryParse(参数[1], out _)))
)
{输入鱼名.Font = 字体;
输入鱼名.ForeColor = 设置主题颜色(暗金色);
输入鱼名.BackColor = 设置主题颜色(输入框深蓝色); 
延时更新(
获取翻译("输入验证成功提示")
+ (
参数.Length == 1
? 获取翻译("预设文本提示")
: 获取翻译("定时文本提示")
)
+ 获取翻译("输入验证成功提示2"),
10000
)
.ConfigureAwait(false);
}
else
{输入鱼名.Font = 字体;
输入鱼名.BackColor = 设置主题颜色(输入框灰蓝色); 
输入鱼名.ForeColor = 设置主题颜色(灰色); 
}
}
void 提示(object sender, EventArgs e)
{if (输入鱼名.Text == "")
{输入鱼名.Text = 输入提示;
输入鱼名.SelectionLength = 0;
输入鱼名.SelectionStart = 输入鱼名.Text.Length;
}
}
void 输入预设(object sender, MouseEventArgs e)
{string[] 参数 = 输入鱼名.Text.Split(
new[] { ' ' },
StringSplitOptions.RemoveEmptyEntries
);
if (
(e.Button == MouseButtons.Left && e.Clicks == 2)
|| (e.Button == MouseButtons.Middle && e.Clicks == 1)
)
{if (
!输入鱼名.Text.StartsWith(获取翻译("输入检验标识"))
&& !string.IsNullOrEmpty(输入鱼名.Text)
)
{if (
参数.Length > 0
&& 预设.Contains(参数[0])
&& (参数.Length == 1 || (参数.Length == 2 && int.TryParse(参数[1], out _)))
)
{RealPlugin.plug.InvokeNamedCallback(
"command",
默语
+ 获取翻译("空天姬指令1")
+ (
(e.Button == MouseButtons.Middle && e.Clicks == 1)
? 获取翻译("空天姬指令参数1") + 参数[0]
: 参数[0] + (参数.Length == 2 ? " " + 参数[1] : "")
)
);
延时更新(获取翻译("已发送提示"), 3000).ConfigureAwait(false);
输入鱼名.SelectionLength = 0;
输入鱼名.SelectionStart = 输入鱼名.Text.Length;
}
else
{延时更新(获取翻译("不支持预设发送提示"), 5000).ConfigureAwait(false);
RealPlugin.plug.InvokeNamedCallback(
"command",
默语 + 获取翻译("空天姬指令2") + 参数[0]
);
输入鱼名.SelectionLength = 0;
输入鱼名.SelectionStart = 输入鱼名.Text.Length;
}
}
else
{延时更新(获取翻译("请输入提示"), 1500).ConfigureAwait(false);
}
}
else if (
e.Button == MouseButtons.Left
&& e.Clicks == 1
&& 输入鱼名.Text.StartsWith(获取翻译("输入检验标识"))
)
{输入鱼名.Text = "";
}
}
void 输入航线(object sender, EventArgs e)
{发送日志("空天姬查询航线");
}
async Task 延时更新(string 文本内容, int 延迟毫秒数)
{文本诗句.Text = 文本内容;
await Task.Delay(延迟毫秒数);
if (!所有诗句.Contains(文本诗句.Text))
{更新诗句();
}
}
async Task 刷新主界面(CancellationToken cancellationToken)
{
_lastA = null;
_lastO = null;
_lastP = null;
_lastT = -1; 
_isPaused = false;
_lastlang = -1;
while (!cancellationToken.IsCancellationRequested)
{
var a = GetScalarVariable(false, "空天姬参数")?.ToString();
var o = GetScalarVariable(false, "空天姬控制海钓模式")?.ToString();
var p = GetScalarVariable(false, "空天姬控制暂停")?.ToString();
var 配置字典 = GetDictVariable(true, "空天姬配置") ?? new VariableDictionary();
int t = int.TryParse(配置字典.GetValue("主界面主题").ToString(), out int result)
? result
: 0;
t = 计算主界面主题(t);
var l = int.TryParse(
GetDictVariable(true, "空天姬配置").GetValue("界面语言").ToString(),
out var _l
)
? _l == 0
? lang
: _l
: lang;
if (a != _lastA || o != _lastO || p != _lastP || t != _lastT || l != _lastlang)
{_lastA = a;
_lastO = o;
_lastP = p;
_lastT = t;
_isPaused = p == "1";
_lastlang = l;
更新界面样式(a, o, _isPaused, t, l);
}
await Task.Delay(1000, cancellationToken);
}
}
void 更新按钮文本(object sender, EventArgs e)
{
var a = GetScalarVariable(false, "空天姬参数")?.ToString();
var o = GetScalarVariable(false, "空天姬控制海钓模式")?.ToString();
var p = GetScalarVariable(false, "空天姬控制暂停")?.ToString();
bool isPaused = p == "1";
更新界面样式(a, o, isPaused, 主界面主题);
}
void 更新界面样式(string a, string o, bool isPaused, int t, int l = -1)
{if (l != -1)
_lang = l;
if (!string.IsNullOrEmpty(a) || !string.IsNullOrEmpty(o))
{按钮启动.FlatAppearance.BorderColor = isPaused
? 设置主题颜色(按钮发光黄色)
: 设置主题颜色(按钮冰球紫色);
按钮启动.ForeColor = isPaused ? 设置主题颜色(按钮发光黄色) : 设置主题颜色(白色);
按钮启动.Font = 小号加粗;
按钮启动.Text =
(isPaused ? 获取翻译("启动按钮已暂停") : 获取翻译("启动按钮运行中"))
+ "\r\n"
+ 获取翻译("启动按钮切换悬浮窗提示")
+ "\r\n"
+ (isPaused ? 获取翻译("启动按钮继续提示") : 获取翻译("启动按钮功能提示"));
空天姬主界面.Text = isPaused ? 获取翻译("标题已暂停") : 获取翻译("标题运行中");
设置提示(
按钮启动,
isPaused ? 获取翻译("启动按钮暂停中提示") : 获取翻译("启动按钮运行中提示")
);
}
else
{按钮启动.FlatAppearance.BorderColor = 设置主题颜色(按钮黑蓝色);
按钮启动.ForeColor = 设置主题颜色(白色);
string 海钓选项 = 下拉海钓.SelectedItem?.ToString() ?? string.Empty;
if (海钓选项.StartsWith("▶"))
{海钓选项 = 海钓选项.Substring(1);
}
按钮启动.Font = 加粗;
按钮启动.Text = 获取翻译("启动文本") + 海钓选项;
空天姬主界面.Text = 获取翻译("标题空闲中");
设置提示(
按钮启动,
获取翻译("启动文本")
+ $"{海钓选项}"
+ "\r\n"
+ 获取翻译("启动按钮切换悬浮窗提示")
);
}
if (t != 主界面主题 || l != -1)
{主界面主题 = t;
空天姬主界面.BackColor = 设置主题颜色(背景色);
设置控件样式();
更新颜色(空天姬主界面, EventArgs.Empty);
刷新主题诗句();
更新诗句(true);
}
}
