void 更新诗句(bool 强制更新 = false)
{if (可选诗句.Count == 0 || 强制更新)
可选诗句 = new List<string>(所有诗句);
int 随机索引 = 随机数.Next(可选诗句.Count);
string 诗 = 可选诗句[随机索引];
已显示诗句.Add(诗);
文本诗句.Text = 诗;
if (可选诗句.Count == 0)
{已显示诗句.Clear();
更新诗句();
}
可选诗句.RemoveAt(随机索引);
}
