await Task.Run(() =>
{文本诗句.Text = s;
});
}
Color 设置主题颜色(Color c)
{if (主界面主题 == 1)
{return c;
}
if (主题颜色映射表.TryGetValue(主界面主题, out var 颜色映射))
{
if (颜色映射.TryGetValue(c, out Color 映射颜色))
{return 映射颜色;
}
}
return 显眼紫色;
}
string 转换为汉字(int 数字)
{if (数字 <= 10)
{return 数字转汉字[数字];
}
else if (数字 > 10 && 数字 < 20)
{return "拾" + 数字转汉字[数字 - 10];
}
else if (数字 >= 20 && 数字 < 100)
{if (数字 % 10 == 0)
{return 数字转汉字[数字 / 10] + "拾";
}
else
{return 数字转汉字[数字 / 10] + "拾" + 数字转汉字[数字 % 10];
}
}
else if (数字 == 100)
{return "壹佰";
}
else
{return 数字.ToString(); 
}
}
string GetOrdinalSuffix(int number)
{if (number % 100 >= 11 && number % 100 <= 13)
return "th";
return (number % 10) switch
{1 => "st",
2 => "nd",
3 => "rd",
_ => "th",
};
}
}
public static void Route(object o, string val)
{
var fontCache = new Dictionary<string, Font>();
Font GetFont(string name, float size, FontStyle style = FontStyle.Regular)
{var key = $"{name}|{size}|{(int)style}";
if (!fontCache.TryGetValue(key, out var font))
{font = new Font(name, size, style);
fontCache[key] = font;
}
return font;
}
var 字体 = GetFont("Microsoft YaHei UI", 14.25F);
var 斜体 = GetFont("Microsoft YaHei UI", 14.25F, FontStyle.Italic);
var 小号 = GetFont("Microsoft YaHei UI", 10.5F);
var 楷体 = GetFont("楷体", 15.75F);
var 小号加粗 = GetFont("Microsoft YaHei UI", 10.5F, FontStyle.Bold);
var 加粗 = GetFont("Microsoft YaHei UI", 14.25F, FontStyle.Bold);
var 加粗重点 = GetFont("Microsoft YaHei UI", 14.25F, FontStyle.Bold | FontStyle.Underline);
var 加粗斜体 = GetFont("Microsoft YaHei UI", 14.25F, FontStyle.Bold | FontStyle.Italic);
int _lang = int.TryParse(
GetDictVariable(true, "空天姬配置").GetValue("界面语言").ToString(),
out var l
)
? l == 0
? lang
: l
: lang;
string 获取翻译(string s)
{return I18n.GetTranslationByIndex(s, _lang);
}
var 白色 = Color.FromArgb(255, 255, 255);
