string 系统音 = GetValueOrDefault("系统音", "100");
string 延迟时间 = GetValueOrDefault("延迟时间", "0");
string TTS报竿 = GetValueOrDefault("TTS报竿", "2");
string 音效报竿 = GetValueOrDefault("自定义报竿", "2");
string 竿时悬浮窗 = GetValueOrDefault("竿时悬浮窗", "2");
