string 处理撒饵竿时(string 撒饵, string 非撒饵)
{if (double.TryParse(撒饵, out double 撒饵转换))
{double d =
(
double.TryParse(非撒饵, out double 非撒饵时间) && 非撒饵时间 != 0
? 撒饵转换
: (撒饵转换 / 2)
) + 延迟时间;
return d.ToString();
}
return "";
}
string 处理非撒饵竿时(string 非撒饵, string 撒饵)
{if (double.TryParse(撒饵, out double 撒饵时间))
{double d =
(
double.TryParse(非撒饵, out double 非撒饵时间) && 非撒饵时间 != 0
? 非撒饵时间
: 撒饵时间
) + 延迟时间;
return d.ToString();
}
return "";
}
string 处理竿型(string h)
{if (string.IsNullOrEmpty(h))
{return "";
}
if (h == "全部" || h.Equals("all", StringComparison.OrdinalIgnoreCase))
{return "123";
}
string[] _h = h.Split('+');
bool 是否有效 = _h.All(c => c.All(ch => ch == '!' || ch == '！'));
if (是否有效)
{
return string.Concat(_h.Select(c => c.Length.ToString()));
}
return "";
}
string 处理技巧提钩(string h)
{return h switch
{"精准" => "1",
"强力" => "2",
"三重" or "三提" => "3",
"双重" or "双提" => "4",
_ => "", 
};
}
void 处理目标表格()
{VariableTable 已有表格 = GetTableVariable(false, "空天姬目标");
DataTable 表格 = new();
表格.Columns.AddRange(
new[]
{new DataColumn("指定数量", typeof(string)),
new DataColumn("鱼名", typeof(string)),
new DataColumn("现有数量", typeof(string)),
}
);
表格.Rows.Add(空天姬目标计数1, 处理目标("空天姬计数目标1", 空天姬计数目标1));
表格.Rows.Add(空天姬目标计数2, 处理目标("空天姬计数目标2", 空天姬计数目标2));
表格.Rows.Add(空天姬目标计数3, 处理目标("空天姬计数目标3", 空天姬计数目标3));
VariableTable 变量表格 = 转换表格(表格);
if (!表格内容一致(已有表格, 变量表格))
{foreach (DataRow row in 表格.Rows)
row[2] = "0";
VariableTable 处理后表格 = 转换表格(表格);
SetTableVariable(false, "空天姬目标", 处理后表格);
}
}
VariableTable 转换表格(DataTable 表格)
{return new VariableTable
{Rows = 表格
.Rows.Cast<DataRow>()
.Select(row => new VariableTable.VariableTableRow
{Values = row
.ItemArray.Select(item =>
(Variable)new VariableScalar { Value = item.ToString() }
) 
.ToList(),
})
.ToList(),
};
}
bool 表格内容一致(VariableTable 已有表格, VariableTable 新表格)
{
if (已有表格?.Rows?.Any() != true)
return false;
return 已有表格.Rows.Count == 新表格.Rows.Count
&& 已有表格.Rows.Zip(
新表格.Rows,
(r1, r2) =>
r1.Values[0].ToString() == r2.Values[0].ToString()
&& r1.Values[1].ToString() == r2.Values[1].ToString()
)
.All(x => x);
}
string 处理目标(string 参数a, string 参数b)
{if (string.IsNullOrEmpty(参数b))
return "";
if (参数b == "《" || 参数b == "<")
{return "《";
}
string[] 手法词组 =
{"不撒饵",
"收藏",
"攒鱼计",
"套娃",
"知识",
"引诱",
"重随",
"鱼眼",
"鱼影",
"钓组",
"不收集",
"多提",
};
var 匹配 = 正则提取.Match(参数b);
var 目标 = 匹配.Success ? 匹配.Groups[1].Value : 参数b;
if (手法词组.Any(k => 目标.Contains(k)))
{return 目标;
}
if (
目标.Equals("任何", StringComparison.OrdinalIgnoreCase)
|| 目标.Equals("any", StringComparison.OrdinalIgnoreCase)
)
{return "|any|";
}
else if (
目标.Equals("拍", StringComparison.OrdinalIgnoreCase)
|| 目标.Equals("占位", StringComparison.OrdinalIgnoreCase)
)
return "?|any|";
string[] 部分 = 目标.Split('、');
List<string> 结果列表 = new();
foreach (string 部分值 in 部分)
{if (int.TryParse(部分值, out int 数字))
{if (
空天姬钓饵ID.Values.Any(值 => 值.ToString() == 数字.ToString())
|| 空天姬非水产品ID.Values.Any(值 => 值.ToString() == 数字.ToString())
)
{结果列表.Add(数字.ToString());
}
else
{错误信息.AppendLine(
获取翻译("鱼名错误提示1")
.Replace("{参数a}", 参数a)
.Replace("{参数b}", 参数b)
.Replace("{数字}", 数字.ToString())
);
return "";
}
}
else
{int 索引 = 空天姬钓饵名.IndexOf(部分值);
if (索引 > 0)
{if (索引 > 单语言空间 && 索引 < 单语言空间乘二)
{索引 -= 单语言空间;
}
else if (索引 > 单语言空间乘二)
{索引 -= 单语言空间乘二;
}
Variable 值 = 空天姬钓饵ID.Peek(索引);
结果列表.Add(值.ToString());
}
else
{索引 = 空天姬非水产品名.IndexOf(部分值);
if (索引 > 0)
{if (索引 > 非水产品空间 && 索引 < 非水产品空间乘二)
{索引 -= 非水产品空间;
}
else if (索引 > 非水产品空间乘二)
{索引 -= 非水产品空间乘二;
}
Variable 值 = 空天姬非水产品ID.Peek(索引);
结果列表.Add(值.ToString());
}
else
{错误信息.AppendLine(
获取翻译("鱼名错误提示2")
.Replace("{参数a}", 参数a)
.Replace("{参数b}", 参数b)
.Replace("{部分值}", 部分值)
);
return "";
}
}
}
}
return (正则鱼名否定符.Match(参数b).Success ? "?" : "")
+ $"|{string.Join("|", 结果列表)}|";
}
void 处理手法()
{foreach (var 映射 in 关键字映射)
{if (空天姬目标.Contains(映射.Key))
{设置标量(映射.Value, "1");
}
}
}
void 设置标量(string n, string v)
{if (string.IsNullOrEmpty(v))
{return;
}
else
{SetScalarVariable(false, n, v);
}
}
}
}
public static void Report(object o, string val)
{int _lang = int.TryParse(
GetDictVariable(true, "空天姬配置").GetValue("界面语言").ToString(),
out var l
)
? l == 0
? lang
: l
: lang;
string 获取翻译(string s)
{return I18n.GetTranslationByIndex(s, _lang);
}
int 单语言空间 = int.Parse("${env:单语言空间}");
int 非水产品空间 = int.Parse("${env:非水产品空间}");
int 水产品名起始索引 = 单语言空间 * (_lang - 1);
int 非水产品名起始索引 = 非水产品空间 * (_lang - 1);
string[] values = val.Split(',');
if (values.Length < 6)
{return;
}
string 空天姬最大ET = values[0];
string 空天姬最小ET = values[1];
string 空天姬计数目标1 = values[2];
string 版本 = values[3];
string 快照 = values[4];
string _triggerpath = values[5];
VariableList 钓饵名 = GetListVariable(true, "空天姬钓饵名") ?? new VariableList();
VariableList 钓饵ID = GetListVariable(true, "空天姬钓饵ID") ?? new VariableList();
VariableList 非水产品名 = GetListVariable(true, "空天姬非水产品名") ?? new VariableList();
VariableList 非水产品ID = GetListVariable(true, "空天姬非水产品ID") ?? new VariableList();
var sb = new StringBuilder();
bool 存在海钓模式 = 存在标量("空天姬控制海钓模式");
处理抬头(sb, 存在海钓模式);
处理模式(sb);
处理手法(sb);
处理窗口期(sb);
处理目标竿(sb);
处理其他目标(sb);
处理注脚(sb);
RealPlugin.plug.InvokeNamedCallback("command", sb.ToString());
void 处理抬头(StringBuilder sb, bool seaMode)
{sb.Append(获取翻译("空天姬前缀默语") + (seaMode ? "" : "<se.7>"));
}
void 处理模式(StringBuilder sb)
{string mode = GetModeText(读取标量值("空天姬参数"));
sb.Append($"\n{mode}" + 获取翻译("模式文本"));
}
void 处理手法(StringBuilder sb)
{var 手法标识 = new StringBuilder(_lang != 2 ? "（" : "(");
if (存在标量("空天姬手法不撒饵"))
手法标识.Append(获取翻译("手法标签不撒饵"));
if (存在标量("空天姬手法收藏品"))
手法标识.Append(获取翻译("手法标签收藏品手法"));
if (存在标量("空天姬手法不收集"))
手法标识.Append(获取翻译("手法标签不收集"));
if (存在标量("空天姬手法知识"))
手法标识.Append(获取翻译("手法标签大鱼知识"));
if (存在标量("空天姬手法攒鱼计"))
手法标识.Append(获取翻译("手法标签攒鱼计"));
if (存在标量("空天姬手法套娃"))
手法标识.Append(获取翻译("手法标签套娃优化"));
if (存在标量("空天姬手法引诱"))
手法标识.Append(获取翻译("手法标签引诱"));
if (存在标量("空天姬手法鱼影"))
手法标识.Append(获取翻译("手法标签鱼影"));
if (存在标量("空天姬手法重随"))
手法标识.Append(获取翻译("手法标签重随手法"));
if (存在标量("空天姬手法多提"))
手法标识.Append(获取翻译("手法标签多提优先"));
if (存在标量("空天姬手法鱼眼"))
手法标识.Append(获取翻译("手法标签鱼眼预设"));
if (存在标量("空天姬鱼识参数"))
手法标识.Append(获取翻译("手法标签鱼识预设"));
if (存在标量("空天姬拍水后预设"))
手法标识.Append(获取翻译("手法标签拍水预设"));
if (存在标量("空天姬阶段参数"))
手法标识.Append(获取翻译("手法标签阶段条件"));
if (存在标量("空天姬手法钓组"))
手法标识.Append(获取翻译("手法标签使用钓组"));
if (手法标识.Length > 1)
{手法标识.Append(_lang != 2 ? "）" : ")");
sb.Append(手法标识);
}
}
void 处理窗口期(StringBuilder sb)
{bool 存在ET = 空天姬最大ET != "";
bool 存在天气 = 存在标量("空天姬天气");
if (存在ET || 存在天气)
sb.Append('\n');
if (存在ET)
{double ET最小毫秒 = double.Parse(读取标量值("空天姬最小ET"));
string ET最小 = 空天姬最小ET;
string ET最大 = 空天姬最大ET;
sb.Append(
获取翻译("窗口期标签").Replace("{ET最小}", ET最小).Replace("{ET最大}", ET最大)
+ $"{Math.Round(ET最小毫秒 / 60000)}"
+ (_lang == 2 ? "" : "")
);
}
if (存在天气)
{sb.Append("  " + 获取翻译("天气标签") + 处理天气(读取标量值("空天姬天气")));
}
}
void 处理目标竿(StringBuilder sb)
{BuildFishingSection(sb, 1);
BuildFishingSection(sb, 2);
BuildFishingSection(sb, 3);
BuildFishingSection(sb, 4);
}
void BuildFishingSection(StringBuilder sb, int 阶段)
{
bool 是否存在竿型 = 存在标量($"空天姬大鱼竿型{阶段}");
if (!是否存在竿型)
{return; 
}
string 阶段标识 = 阶段 switch
{1 => "",
2 => "",
3 => "",
4 => "",
_ => "",
};
sb.Append($"\n{阶段标识}");
string 区间 = 处理竿时(阶段);
if (!string.IsNullOrWhiteSpace(区间))
{sb.Append($" {区间}");
}
string 竿型 = 处理竿型(阶段);
if (!string.IsNullOrEmpty(竿型))
{sb.Append($" {竿型}");
}
string 技巧提钩 = 处理技巧提钩(阶段 + 1);
if (!string.IsNullOrEmpty(技巧提钩))
{sb.Append($" {技巧提钩}");
}
string 鱼篓 = 处理鱼篓(阶段 + 1);
if (!string.IsNullOrEmpty(鱼篓))
{sb.Append($" {鱼篓}");
}
string 目标 = 读取标量值($"空天姬大鱼目标{阶段}");
if (!string.IsNullOrEmpty(目标) && !存在标量("空天姬大鱼竿型" + (阶段 + 1)))
{sb.Append(" " + 处理鱼名(目标));
}
else if (!存在标量("空天姬大鱼竿型" + (阶段 + 1)))
{sb.Append(" " + 处理鱼名(读取标量值("空天姬目标")));
}
}
void 处理其他目标(StringBuilder sb)
{
if (存在标量("空天姬拍目标"))
{sb.Append($"\n{(存在标量("空天姬拍占位") ? "" : 获取翻译("拍水标签"))}");
string 竿时拍 = 处理竿时("拍");
if (!string.IsNullOrWhiteSpace(竿时拍))
sb.Append($" {竿时拍}");
string 竿型拍 = 处理竿型("拍");
if (!string.IsNullOrEmpty(竿型拍))
sb.Append($" {竿型拍}");
string 技巧提钩拍 = 处理技巧提钩(1);
if (!string.IsNullOrEmpty(技巧提钩拍))
sb.Append($" {技巧提钩拍}");
if (存在标量("空天姬大鱼鱼篓1"))
sb.Append(获取翻译("鱼篓标签"));
sb.Append(
!存在标量("空天姬拍占位")
? (_lang != 2 ? " " : ", ") + 处理鱼名(读取标量值("空天姬拍目标"))
: ""
);
}
if (存在标量("空天姬专一目标"))
{sb.Append(获取翻译("专一标签") + 处理鱼名(读取标量值("空天姬专一目标")));
if (存在标量("空天姬专一多提"))
sb.Append($" {处理技巧提钩(6)}");
}
bool 存在计数参数 = 空天姬计数目标1 != "";
if (存在计数参数)
{sb.Append(获取翻译("计数标签") + 构建计数目标());
}
}
void 处理注脚(StringBuilder sb)
{if (存在海钓模式)
sb.Append(获取翻译("海钓标签"));
if (存在标量("空天姬预设备注"))
sb.Append(获取翻译("备注标签") + 读取标量值("空天姬预设备注"));
sb.Append(
$"\n {(_triggerpath.Contains("Snapshot") ? $"Snapshot {快照}" : $"v{版本}")}"
);
sb.Append(获取翻译("指令标签"));
}
static string 读取标量值(string key)
{return GetScalarVariable(false, key)?.ToString() ?? string.Empty;
}
bool 存在标量(string key) => 读取标量值(key) != "";
string GetModeText(string param) =>
int.Parse(param) switch
{1 => 获取翻译("平钓标签"),
2 => 获取翻译("大鱼标签"),
3 => 获取翻译("耐心标签"),
_ => 获取翻译("未知模式标签"),
};
string 处理天气(string w)
{string[] 部分 = w.Split('|');
List<string> 结果列表 = new();
foreach (string 部分值 in 部分)
{结果列表.Add(获取翻译($"{部分值}").ToString());
}
return string.Join(_lang != 2 ? "、" : ", ", 结果列表);
}
string 处理竿型(object identifier)
{string value = identifier is int num
? 读取标量值($"空天姬大鱼竿型{num}")
: 读取标量值("空天姬拍竿型");
if (string.IsNullOrEmpty(value))
return "";
return value switch
{"123" => 获取翻译("全提文本"),
"3" => 获取翻译("鱼王竿文本"),
"2" => 获取翻译("中竿文本"),
"1" => 获取翻译("轻竿文本"),
"12" => 获取翻译("轻竿和中竿文本"),
"13" => 获取翻译("轻竿和鱼王竿文本"),
"23" => 获取翻译("中竿和鱼王竿文本"),
_ => 获取翻译("未知竿型文本"),
};
}
string 处理竿时(object 标识)
{string 前缀 = 标识 is int i ? $"大鱼" : "拍";
string 阶段 = 标识 is int ii ? $"{ii}" : "";
bool 存在最小区间 = 存在标量($"空天姬{前缀}最小区间{阶段}");
bool 存在最大区间 = 存在标量($"空天姬{前缀}最大区间{阶段}");
string 最小 = 读取标量值($"空天姬{前缀}最小区间{阶段}");
string 最小非撒饵 = 读取标量值($"空天姬{前缀}最小区间非撒饵{阶段}");
string 最大 = 读取标量值($"空天姬{前缀}最大区间{阶段}");
string 最大非撒饵 = 读取标量值($"空天姬{前缀}最大区间非撒饵{阶段}");
string 最小区间播报 = string.Empty;
if (存在最小区间 && 最小 != "0")
{最小区间播报 = $"{最小}+{最小非撒饵} ";
最小区间播报 += 存在最大区间 ? 获取翻译("秒到文本") : 获取翻译("秒以上文本");
}
string 最大区间播报 = string.Empty;
if (存在最大区间 && 最大 != "0")
{最大区间播报 = $"{最大}+{最大非撒饵} ";
最大区间播报 += 存在最小区间 ? 获取翻译("秒文本") : 获取翻译("秒以下文本");
}
return $"{最小区间播报}{最大区间播报}";
}
string 处理技巧提钩(int 技巧提钩阶段)
{
string 技巧提钩 =
技巧提钩阶段 != 6
? 读取标量值($"空天姬大鱼技巧提钩{技巧提钩阶段}")
: 读取标量值($"空天姬专一多提");
if (int.TryParse(技巧提钩, out int 值))
{return 值 switch
{> 4 => "Unknown Hook",
> 3 => 获取翻译("双提文本"),
> 2 => 获取翻译("三提文本"),
> 1 => 获取翻译("强力文本"),
> 0 => 获取翻译("精准文本"),
_ => $"{值} Hook",
};
}
return string.Empty;
}
string 处理鱼篓(int 阶段)
{if (存在标量($"空天姬大鱼鱼篓{阶段}") && !存在标量("空天姬大鱼竿型" + (阶段 + 1)))
{string 鱼篓 = 读取标量值($"空天姬大鱼鱼篓{阶段}");
return 获取翻译("鱼篓文本") + 处理鱼名($"{鱼篓}");
}
else
{return string.Empty;
}
}
string 构建计数目标()
{var 表格空天姬目标 = GetTableVariable(false, "空天姬目标") ?? new VariableTable();
var sb = new StringBuilder();
for (int i = 1; i <= 3; i++)
{
string 目标 = 表格空天姬目标.Peek(2, i)?.ToString() ?? string.Empty;
string 计数 = 表格空天姬目标.Peek(1, i)?.ToString() ?? "0";
if (!string.IsNullOrEmpty(目标))
{sb.Append(处理鱼名($"{目标}"));
if (计数 != "0")
{sb.Append($"×{计数} ");
}
}
}
return sb.ToString();
}
string 处理鱼名(string n)
{string _n;
if (string.IsNullOrEmpty(n))
return "";
else if (!n.StartsWith("?") && !n.StartsWith("|"))
return n;
else
{_n = n.Trim('?');
_n = _n.Trim('|');
}
if (_n.Equals("any", StringComparison.OrdinalIgnoreCase))
return 获取翻译("任何文本");
string[] 部分 = _n.Split('|');
List<string> 结果列表 = new();
foreach (string 部分值 in 部分)
{if (int.TryParse(部分值, out int 数字))
{int 索引a = 钓饵ID.IndexOf(部分值);
int 索引b = 非水产品ID.IndexOf(部分值);
if (索引a > 0)
{Variable 值 = 钓饵名.Peek(索引a + 水产品名起始索引);
结果列表.Add(值.ToString());
}
else if (索引b > 0)
{Variable 值 = 非水产品名.Peek(索引b + 非水产品名起始索引);
结果列表.Add(值.ToString());
}
else
{return 获取翻译("未找到鱼名文本") + $" {n}";
}
}
else
{return 获取翻译("未找到鱼名文本") + $" {n}";
}
}
return (n.StartsWith("?") ? 获取翻译("非文本") : "")
+ $"{string.Join(_lang != 2 ? "、" : ", ", 结果列表)}";
}
}
public static void 发送日志(string text)
{var tri = new Trigger();
fakectx.trig = tri;
RealPlugin.plug.QueueAction(
fakectx,
fakectx.trig,
m: null,
new Triggernometry.Action
{ActionType = Triggernometry.Action.ActionTypeEnum.LogMessage.ToString(),
LogMessageText = $"{text}",
LogProcess = "true",
},
DateTime.Now,
releaseMutex: true
);
}
public static string 公用获取翻译文本(string key, int index)
{return I18n.GetTranslationByIndex(key, index);
}}
