private static void BuildDictionary()
{_rowDictionary = new Dictionary<string, DataRow>();
foreach (DataRow row in _table.Rows)
{_rowDictionary[row[0].ToString()] = row;
}
}
public static string GetTranslation(string rowKey, string lang = "EN")
{if (!_languageColumns.TryGetValue(lang.ToUpper(), out int columnIndex))
{columnIndex = MissFisher.lang; 
}
return GetTranslationByIndex(rowKey, columnIndex);
}
public static string GetTranslationByIndex(string rowKey, int columnIndex)
{if (!_rowDictionary.TryGetValue(rowKey, out DataRow row))
{Log(RealPlugin.DebugLevelEnum.Error, $"[M.G.B.]MISSING: {rowKey}");
return $"[MISSING:{rowKey}]";
}
if (columnIndex < 0 || columnIndex >= _table.Columns.Count)
{Log(RealPlugin.DebugLevelEnum.Error, $"[M.G.B.]INVALID COL: {columnIndex}");
return $"[INVALID COL:{columnIndex}]";
}
if (columnIndex == 0)
{columnIndex = MissFisher.lang;
}
return row[columnIndex].ToString();
}
public static void Log(RealPlugin.DebugLevelEnum level, string msg) =>
Interpreter.StaticHelpers.Log(level, msg);}

using System;
using System.Windows.Forms;
using Triggernometry;
var p = RealPlugin.plug;
var trv = p.ui.treeView1;
int folderCount = 0; // 计数器
Search(trv.Nodes[1]);
if (folderCount > 1)
{MessageBox.Show($"在远程触发器分组找到 {folderCount} 个空天姬 MissFisher\r\n请取消勾选多余的空天姬 MissFisher 触发器", "错误", MessageBoxButtons.OK, MessageBoxIcon.Error);}
trv.Refresh();
void Search(TreeNode node)
{foreach (TreeNode child in node.Nodes)
{if (child.Tag is Folder f)
{if (f.Name.Contains("空天姬 ") || f.Name.Contains("MissFisher"))
{folderCount++; // 增加计数
}
Search(child);
}
else if (child.Tag is Repository r)
{Search(child);
}
}}

using System;
using System.Windows.Forms;
using Triggernometry;
var p = RealPlugin.plug;
var trv = p.ui.treeView1;
int folderCount = 0; // 计数器
Search(trv.Nodes[0]);
if (folderCount > 1)
{MessageBox.Show($"在本地触发器找到 {folderCount} 个 空天姬 MissFisher 或 风筝猫 Gaelicat\r\n请移除多余的触发器", "错误", MessageBoxButtons.OK, MessageBoxIcon.Error);}
trv.Refresh();
void Search(TreeNode node)
{foreach (TreeNode child in node.Nodes)
{if (child.Tag is Folder f)
{if (f.Name.Contains("风筝猫 ") || f.Name.Contains("空天姬 ") || f.Name.Contains("MissFisher"))
{folderCount++; // 增加计数
}
Search(child);
}
else if (child.Tag is Repository r)
{Search(child);
}
}}

