Action.ExecScriptExpression:
using System;
using System.Windows.Forms;
using Triggernometry;
var p = RealPlugin.plug;
var trv = p.ui.treeView1;
int folderCount = 0; // 计数器
Search(trv.Nodes[1]);
if (folderCount > 1)
{MessageBox.Show($"在远程触发器分组找到 {folderCount} 个空天姬 MissFisher\r\n请取消勾选多余的空天姬 MissFisher 触发器", "错误", MessageBoxButtons.OK, MessageBoxIcon.Error);}
trv.Refresh();
void Search(TreeNode node)
{foreach (TreeNode child in node.Nodes)
{if (child.Tag is Folder f)
{if (f.Name.Contains("空天姬 ") || f.Name.Contains("MissFisher"))
{folderCount++; // 增加计数
}
Search(child);
}
else if (child.Tag is Repository r)
{Search(child);
}
}}

using System;
using System.Windows.Forms;
using Triggernometry;
var p = RealPlugin.plug;
var trv = p.ui.treeView1;
int folderCount = 0; // 计数器
Search(trv.Nodes[0]);
if (folderCount > 1)
{MessageBox.Show($"在本地触发器找到 {folderCount} 个空天姬 MissFisher 或 风筝猫 Gaelicat\r\n请移除多余的触发器", "错误", MessageBoxButtons.OK, MessageBoxIcon.Error);}
trv.Refresh();
void Search(TreeNode node)
{foreach (TreeNode child in node.Nodes)
{if (child.Tag is Folder f)
{if (f.Name.Contains("风筝猫 ") || f.Name.Contains("空天姬 ") || f.Name.Contains("MissFisher"))
{folderCount++; // 增加计数
}
Search(child);
}
else if (child.Tag is Repository r)
{Search(child);
}
}}

