void 设置提示(Control c, string t)
{if (c.Tag == null || !(c.Tag is ToolTip))
{c.Tag = new ToolTip();
}
var 提示 = (ToolTip)c.Tag;
提示.SetToolTip(c, t);
提示.AutoPopDelay = 20000;
提示.InitialDelay = 500;
提示.ReshowDelay = 500;
提示.IsBalloon = true;}
void 切换文本勾选前缀(object s, EventArgs e)
{Label l = (Label)s;
bool b = l.Text.StartsWith(" ▣");
l.Text = (b ? " ▢ " : " ▣ ") + l.Text.Replace(" ▣ ", "").Replace(" ▢ ", "");}
void 设置文本勾选前缀(Control c, bool b)
{c.Text = (b ? " ▣ " : " ▢ ") + c.Text.Replace(" ▣ ", "").Replace(" ▢ ", "");}
void 设置控件样式()
{foreach (Control c in 表格.Controls)
{c.Font = 字体;
c.ForeColor = Color.White;
c.AutoSize = true;
c.Dock = DockStyle.Fill;
按钮保存.Font = 加粗;
if (c is Label l)
{l.TextAlign = ContentAlignment.MiddleLeft;
l.ForeColor = Color.FromArgb(230, 218, 193);
}
文本标题自动.ForeColor = Color.FromArgb(196, 197, 201);
文本标题工具.ForeColor = Color.FromArgb(196, 197, 201);
文本标题其他.ForeColor = Color.FromArgb(196, 197, 201);
if (c is Button b)
{b.FlatStyle = FlatStyle.Flat;
b.BackColor = Color.FromArgb(50, 81, 155);
b.FlatAppearance.BorderColor = Color.FromArgb(19, 33, 68);
}
if (c is TextBox t)
{t.Font = 字体;
t.BorderStyle = BorderStyle.FixedSingle;
t.BackColor = Color.FromArgb(21, 35, 71);
}
if (c is CheckBox cb)
{cb.Appearance = Appearance.Button;
cb.ForeColor = Color.FromArgb(234, 222, 195);
}
if (c is ComboBox cob)
{cob.DropDownStyle = ComboBoxStyle.DropDownList;
cob.DrawMode = DrawMode.OwnerDrawFixed;
cob.BackColor = Color.FromArgb(41, 60, 107);
cob.ForeColor = Color.FromArgb(234, 222, 195);
更新下拉前缀(cob);
cob.DrawItem += new DrawItemEventHandler(绘制下拉框);
}
}
RealPlugin.plug.InvokeNamedCallback("command", 默语 + "正在读取配置……");}
void 绘制下拉框(object sender, DrawItemEventArgs e)
{e.Graphics.FillRectangle(new SolidBrush(e.BackColor), e.Bounds);
StringFormat stringFormat = new StringFormat
{Alignment = StringAlignment.Near,
LineAlignment = StringAlignment.Center,
};
e.Graphics.DrawString(
((ComboBox)sender).Items[e.Index].ToString(),
e.Font,
new SolidBrush(e.ForeColor),
e.Bounds.X,
e.Bounds.Y
);
using (Pen borderPen = new Pen(Color.FromArgb(41, 60, 107)))
{e.Graphics.DrawRectangle(
borderPen,
e.Bounds.X,
e.Bounds.Y,
e.Bounds.Width - 1,
e.Bounds.Height - 1
);
}}
void 更新下拉前缀(ComboBox cb)
{int sli = cb.SelectedIndex;
List<string> newItems = new List<string>();
foreach (var item in cb.Items)
{if (item is string strItem)
{newItems.Add($"▶ {strItem}");
}
}
cb.Items.Clear();
cb.Items.AddRange(newItems.ToArray());
if (sli >= 0 && sli < cb.Items.Count)
{cb.SelectedIndex = sli;
}
else
{cb.SelectedIndex = 0;
}}
void 读取配置(object s, EventArgs e)
{if (空天姬配置窗口.InvokeRequired)
{空天姬配置窗口.Invoke(new System.Action(() => 读取配置(s, e)));
return;
}
if (主界面自启 == "0")
{设置文本勾选前缀(文本勾选自启, false);
}
输入录制2.Text = 确定热键;
if (int.TryParse(语言, out var lingo))
{下拉语言.SelectedIndex = lingo - 1;
}
下拉TTS报竿.SelectedIndex = TTS报竿 == "2" ? 0 : (TTS报竿 == "1" ? 1 : 2);
下拉竿时.SelectedIndex = 竿时悬浮窗 == "2" ? 0 : (竿时悬浮窗 == "1" ? 1 : 2);
下拉采集力.SelectedIndex = 采集力悬浮窗 == "2" ? 0 : (采集力悬浮窗 == "1" ? 1 : 2);
if (int.TryParse(悬浮窗字号, out var fontSize) && fontSize >= 1 && fontSize <= 4)
{下拉字号.SelectedIndex = fontSize;
}
if (调试模式 == "0")
{设置文本勾选前缀(文本勾选调试, false);
}
if (int.TryParse(主界面主题, out var theme))
{下拉主题.SelectedIndex = theme ;
}
switch (公共频道)
{case "p":
下拉触幻.SelectedIndex = 1;
break;
case "y":
下拉触幻.SelectedIndex = 2;
break;
case "sh":
下拉触幻.SelectedIndex = 3;
break;
}
switch (幻海频道)
{case "p":
下拉时长.SelectedIndex = 1;
break;
case "y":
下拉时长.SelectedIndex = 2;
break;
case "sh":
下拉时长.SelectedIndex = 3;
break;
}
switch (成就频道)
{case "p":
下拉成就.SelectedIndex = 1;
break;
case "y":
下拉成就.SelectedIndex = 2;
break;
case "sh":
下拉成就.SelectedIndex = 3;
break;
}
if (关闭系统音 == "0")
{设置文本勾选前缀(文本勾选系统音, false);
}
if (int.TryParse(系统音, out var intValue) && intValue >= 0 && intValue <= 100)
{输入恢复.Text = intValue.ToString();
}
if (int.TryParse(延迟时间, out var intValueyc) && intValueyc >= 0 && intValueyc <= 1000)
{输入延迟.Text = intValueyc.ToString();
}
if (自定义报竿 == "2")
{下拉自定义报竿.SelectedIndex = 1;
}
else if (自定义报竿 == "1")
{下拉自定义报竿.SelectedIndex = 2;
}
if (自定义类型 == "2")
{下拉自定义类型.SelectedIndex = 1;
}
RealPlugin.plug.InvokeNamedCallback("command", 默语 + "配置读取成功！<se.9>");}
void 保存配置(object sender, EventArgs e)
{if (isSavingConfig)
{return;
}
isSavingConfig = true;
try
{if (空天姬配置窗口.InvokeRequired)
{空天姬配置窗口.Invoke(new System.Action(() => 保存配置(sender, e)));
return;
}
字典.SetValue("主界面自启", 文本勾选自启.Text.StartsWith(" ▣") ? 1 : 0);
字典.SetValue("确定热键", (检查数字(输入录制2.Text)) ? 输入录制2.Text : "");
字典.SetValue("语言", 下拉语言.SelectedIndex + 1);
字典.SetValue(
"TTS报竿",
下拉TTS报竿.SelectedIndex == 0 ? 2 : (下拉TTS报竿.SelectedIndex == 1 ? 1 : 2)
);
字典.SetValue(
"竿时悬浮窗",
下拉竿时.SelectedIndex == 0 ? 2 : (下拉竿时.SelectedIndex == 1 ? 1 : 2)
);
字典.SetValue(
"采集力悬浮窗",
下拉采集力.SelectedIndex == 0 ? 2 : (下拉采集力.SelectedIndex == 1 ? 1 : 2)
);
字典.SetValue("悬浮窗字号", 下拉字号.SelectedIndex);
字典.SetValue("调试模式", 文本勾选调试.Text.StartsWith(" ▣") ? 1 : 0);
字典.SetValue(
"主界面主题",
下拉主题.SelectedIndex
);
string chuHuan;
switch (下拉触幻.SelectedIndex)
{case 0:
chuHuan = "e";
break;
case 1:
chuHuan = "p";
break;
case 2:
chuHuan = "y";
break;
case 3:
chuHuan = "sh";
break;
default:
chuHuan = "e";
break;
}
字典.SetValue("公共频道", chuHuan);
string huanHai;
switch (下拉时长.SelectedIndex)
{case 0:
huanHai = "e";
break;
case 1:
huanHai = "p";
break;
case 2:
huanHai = "y";
break;
case 3:
huanHai = "sh";
break;
default:
huanHai = "e";
break;
}
字典.SetValue("幻海频道", huanHai);
string chengJiu;
switch (下拉成就.SelectedIndex)
{case 0:
chengJiu = "e";
break;
case 1:
chengJiu = "p";
break;
case 2:
chengJiu = "y";
break;
case 3:
chengJiu = "sh";
break;
default:
chengJiu = "e";
break;
}
字典.SetValue("成就频道", chengJiu);
字典.SetValue("关闭系统音", 文本勾选系统音.Text.StartsWith(" ▣") ? 1 : 0);
字典.SetValue("系统音", (检查数字(输入恢复.Text)) ? 输入恢复.Text : 系统音);
字典.SetValue("延迟时间", (检查数字(输入延迟.Text)) ? 转换秒数(输入延迟.Text) : 延迟时间);
字典.SetValue(
"自定义报竿",
下拉自定义报竿.SelectedIndex == 0 ? 0 : (下拉自定义报竿.SelectedIndex == 1 ? 2 : 1)
);
字典.SetValue("自定义类型", 下拉自定义类型.SelectedIndex + 1);
StaticHelpers.SetDictVariable(true, "空天姬配置", 字典);
RealPlugin.plug.InvokeNamedCallback(
"command",
默语 + "[空天姬 MissFisher] 配置已保存。<se.10>"
);
发送日志("空天姬配置");
空天姬配置窗口.Close();
}
catch (Exception ex)
{StaticHelpers.Log(2, $"{ex}");
}
finally
{isSavingConfig = false;
}}
void 窗口关闭保存配置(object sender, FormClosingEventArgs e)
{if (!isClosing && e.CloseReason == CloseReason.UserClosing)
{isClosing = true;
if (空天姬配置窗口.InvokeRequired)
{空天姬配置窗口.Invoke(new System.Action(() => 保存配置(sender, e)));
}
else
{保存配置(sender, e);
}
isClosing = false;
}}
void 按键录制(KeyEventArgs e, TextBox recordingInput)
{if (e.Control || e.Alt || e.Shift)
{recordingInput.Text = "不接受组合键";
}
else
{if (e.KeyCode == Keys.Back || e.KeyCode == Keys.Escape)
{recordingInput.Text = "未设置";
}
else
{recordingInput.Text = e.KeyValue.ToString();
}
}}
void 录制2(object sender, KeyEventArgs e)
{按键录制(e, 输入录制2);}
bool 检查数字(string input)
{if (int.TryParse(input, out int temp))
{return temp > 0;
}
return false;}
string 转换秒数(string input)
{if (double.TryParse(input, out double number))
{double result = number / 1000;
if (result == 0)
{return "0";
}
return result.ToString();
}
else
{return "0";
}}
string 转换毫秒(string input)
{if (double.TryParse(input, out double number))
{double result = number * 1000;
if (result == 0)
{return "0";
}
return result.ToString();
}
else
{return "0";
}}
void 全选(object sender, MouseEventArgs e)
{if (sender is TextBox textBox)
{if (e.Button == MouseButtons.Left)
{textBox.SelectAll();
}
}}
void 发送日志(string text)
{var tri = new Trigger();
StaticHelpers.fakectx.trig = tri;
RealPlugin.plug.QueueAction(
StaticHelpers.fakectx,
StaticHelpers.fakectx.trig,
m: null,
new Triggernometry.Action
{ActionType = Triggernometry.Action.ActionTypeEnum.LogMessage.ToString(),
LogMessageText = $"{text}",
LogProcess = "true",
},
DateTime.Now,
releaseMutex: true
);}

