var 空天姬航线预报 = new Form
{Text = "空天姬航线预报（窗口处于前台时才会刷新）",
StartPosition = FormStartPosition.CenterScreen,
FormBorderStyle = FormBorderStyle.FixedSingle,
MaximizeBox = false,
BackColor = 背景色,
TopMost = true,
SizeGripStyle = SizeGripStyle.Hide,};
#endregion
#region 控件事件
空天姬航线预报.Shown += (sender, e) =>
{((Form)sender).TopMost = true;
System.Threading.Thread.Sleep(10);
((Form)sender).TopMost = false;};
var 列表更新计时器 = new Timer { Interval = 60000 };
var 行更新计时器 = new Timer { Interval = 50 };
var 当前行索引 = 0; 
空天姬航线预报.Activated += (sender, e) =>
{列表更新计时器.Start(); 
if (!行更新计时器.Enabled) 
{当前行索引 = 0; 
行更新计时器.Start(); 
}};
空天姬航线预报.Deactivate += (sender, e) =>
{列表更新计时器.Stop(); 
行更新计时器.Stop(); };
列表更新计时器.Tick += (sender, e) =>
{if (!行更新计时器.Enabled) 
{当前行索引 = 0; 
行更新计时器.Start(); 
}};
行更新计时器.Tick += (sender, e) =>
{更新单行航线(); 

if (当前行索引 >= 表格.RowCount)
{行更新计时器.Stop();
}};

var 滚动面板 = new Panel { Dock = DockStyle.Fill, AutoScroll = true };
滚动面板.Controls.Add(表格);

滚动面板.MouseWheel += (sender, e) =>
{int 滚动量 = e.Delta > 0 ? -50 : 50; 
滚动面板.AutoScrollPosition = new Point(
-滚动面板.AutoScrollPosition.X,
Math.Max(0, -滚动面板.AutoScrollPosition.Y + 滚动量)
);};



#endregion

空天姬航线预报.Controls.Add(滚动面板); 
设置控件关系();
设置控件样式();

空天姬航线预报.ShowDialog();
void 设置控件关系()
{RealPlugin.plug.InvokeNamedCallback("command", "/e [空天姬 MissFisher] 请稍后……<se.7>");

表格.ColumnCount = 4; 
表格.RowCount = 72; 

Graphics graphics = Graphics.FromHwnd(IntPtr.Zero);
float dpiX = graphics.DpiX;
float dpiY = graphics.DpiY;
graphics.Dispose();
float scaleFactor = dpiY / 96f; 
int newWidth = (int)Math.Round(1000 * scaleFactor);
int newHeight = (int)Math.Round(664 * scaleFactor);
空天姬航线预报.Size = new Size(newWidth, newHeight);

int 行高像素 = (int)(42 * scaleFactor); 
for (int i = 0; i < 表格.RowCount; i++)
{表格.RowStyles.Add(new RowStyle(SizeType.Absolute, 行高像素));
}
for (int j = 0; j < 表格.ColumnCount; j++)
{表格.ColumnStyles.Add(new ColumnStyle(SizeType.AutoSize)); 
}

初始化表格();}
void 初始化表格()
{for (int row = 0; row < 表格.RowCount; row++)
{for (int col = 0; col < 表格.ColumnCount; col++)
{Label label = new Label
{Text = "", 
Dock = DockStyle.Fill,
TextAlign =
col == 0 ? ContentAlignment.MiddleRight
: col == 1 ? ContentAlignment.MiddleLeft
: col == 2 ? ContentAlignment.MiddleLeft
: ContentAlignment.MiddleCenter,
ForeColor =
col == 0 ? 白色
: col == 1 ? 下拉框淡金色
: col == 2 ? 暗金色
: 灰色,
Font =
col == 0 ? 加粗
: col == 1 ? 加粗重点
: col == 2 ? 小号加粗
: 小号加粗,
};
表格.Controls.Add(label, col, row);
标签列表.Add(label); 
}
}}
void 设置控件样式()
{
foreach (Control c in 表格.Controls)
{
c.AutoSize = true;
c.Dock = DockStyle.Fill;
}}
void 更新单行航线()
{if (当前行索引 >= 表格.RowCount)
{当前行索引 = 0; 
}
//// 暂停布局计算
表格.SuspendLayout();

long 当前时间戳 = DateTimeOffset.UtcNow.ToUnixTimeSeconds();

DateTime utc时间 = DateTimeOffset.FromUnixTimeSeconds(当前时间戳).UtcDateTime;
int utc小时数 = utc时间.Hour;

if (utc小时数 % 2 == 1)
{当前时间戳 += 7200; 
}

long 第一行时间戳 = 当前时间戳 - (当前时间戳 % 7200); 

long 当前行时间戳 = 第一行时间戳 + (当前行索引 / 2) * 7200; 

int 班次航线序号 = 计算航线序号(当前行时间戳);

int 行索引1 = 班次航线序号 - 1;
int 行索引2 = 行索引1 + 12;
string 航线名称1 = 航线表格[行索引1][1];
string 航线描述1 = 航线表格[行索引1][2];
string 航线名称2 = 航线表格[行索引2][1];
string 航线描述2 = 航线表格[行索引2][2];

int 行 = 当前行索引;
标签列表[行 * 4 + 0].Text = 获取时间戳显示内容(当前行时间戳, 行); 
标签列表[行 * 4 + 1].Text = 航线名称1;
标签列表[行 * 4 + 2].Text = 航线描述1;
标签列表[行 * 4 + 3].Text = (行 % 2 == 0 || 行 == 0) ? 班次航线序号.ToString("D2") : ""; 
行++;
当前行时间戳 += 7200; 
标签列表[行 * 4 + 0].Text = 获取时间戳显示内容(当前行时间戳, 行); 
标签列表[行 * 4 + 1].Text = 航线名称2;
标签列表[行 * 4 + 2].Text = 航线描述2;
标签列表[行 * 4 + 3].Text = (行 % 2 == 0 || 行 == 0) ? 班次航线序号.ToString("D2") : ""; 
当前行索引 += 2; 

表格.ResumeLayout();
if (!指令)
{指令 = true;
RealPlugin.plug.InvokeNamedCallback(
"command",
"/e [空天姬 MissFisher] 预报初始化完成。<se.10>"
);
}}
int 计算航线序号(long 时间戳)
{long 基准时间戳 = 1715184000; 
long 时间差 = 时间戳 - 基准时间戳;

int 偏移量 = (int)(时间差 / 7200) + (int)(时间差 / 86400) % 12;

int 当前小时数 = (int)((时间差 % 86400) / 3600);

int 修正值 = (当前小时数 % 2 == 1) ? (当前小时数 == 15 ? 2 : 1) : 0;

int 航班序号 = (偏移量 + 修正值) % 12 + 1;
return 航班序号;}
string 获取时间戳显示内容(long 时间戳, int 行)
{
DateTime 本地时间 = DateTimeOffset.FromUnixTimeSeconds(时间戳).LocalDateTime;
int 小时数 = 本地时间.Hour;
if (行 == 0)
{return $"{本地时间:MM-dd} {小时数:00}:00"; 
}
else if (行 % 2 == 1)
{return ""; 
}
else
{if (小时数 == 0 || 小时数 == 1)
{return $"{本地时间:MM-dd} {小时数:00}:00"; 
}
else
{return $"{小时数:00}:00"; 
}
}}

