using System.Linq;
using System;
using System.Windows.Forms;
using Advanced_Combat_Tracker;
using Triggernometry;

var plugin = ActGlobals.oFormActMain.ActPlugins
            .FirstOrDefault(x => x.pluginObj?.GetType().ToString() == "PostNamazu.PostNamazu")
            ?.pluginObj;

if (plugin == null)
{
    MessageBox.Show("没有在 ACT 中找到鲶鱼精（PostNamazu）插件\n大部分空天姬功能将无法生效",
        "空天姬",
            MessageBoxButtons.OK,
            MessageBoxIcon.Error);
}
else{ TriggernometryHelpers.SetScalarVariable(false,"空天姬控制鲶鱼精检查I", "1"); }

using System.Linq;
using System;
using System.Windows.Forms;
using Advanced_Combat_Tracker;

var plugin = ActGlobals.oFormActMain.ActPlugins
            .FirstOrDefault(x => x.pluginObj?.GetType().ToString() == "Cafe.Matcha.MatchaInit")
            ?.pluginObj;

if (plugin == null)
{
    MessageBox.Show("没有在 ACT 中找到抹茶（Matcha）插件\n建议安装它并勾选抹茶中的“日志及数据共享”模块的“兼容模式”\n可减少提钩延迟和性能消耗",
        "空天姬",
            MessageBoxButtons.OK,
            MessageBoxIcon.Information);
}

using System;
using System.Windows.Forms;
using Triggernometry;

var p = RealPlugin.plug;

var trv = p.ui.treeView1;
int folderCount = 0; // 计数器
Search(trv.Nodes[1]);

if (folderCount > 1)
{
MessageBox.Show($"在远程触发器分组找到 {folderCount} 个空天姬 MissFisher\r\n请取消勾选多余的空天姬 MissFisher 触发器", "错误", MessageBoxButtons.OK, MessageBoxIcon.Error);
}
//else
//{
//    MessageBox.Show("检查完毕，未发现多个空天姬 MissFisher\r\n", "提示", MessageBoxButtons.OK, MessageBoxIcon.Information);
//}

trv.Refresh();

void Search(TreeNode node)
{
    foreach (TreeNode child in node.Nodes)
    {
        if (child.Tag is Folder f)
        {
            if (f.Name.Contains("空天姬 ") || f.Name.Contains("MissFisher"))
            {
                folderCount++; // 增加计数
            }
            Search(child);
        }
        else if (child.Tag is Repository r)
        {
            Search(child);
        }
    }
}

