设置控件关系();
设置控件样式();
读取配置(窗口,EventArgs.Empty);
窗口.ShowDialog();

void 设置控件关系()
{
    // 添加控件关系：主表单
    窗口.Controls.Add(表格);

    表格.ColumnCount = 5;// 列
    表格.RowCount = 22;// 行
    // DPI 适配
    // 获取主屏幕的 DPI
    Graphics graphics = Graphics.FromHwnd(IntPtr.Zero);
    float dpiX = graphics.DpiX;
    float dpiY = graphics.DpiY;
    graphics.Dispose();

    // 计算缩放比例，这里以垂直DPI为例
    float scaleFactor = dpiY / 96f; // 基准DPI为96（100%缩放时的标准DPI）

    // 根据缩放比例调整像素值
    int 行高像素 = (int)(38 * scaleFactor); // 原始38像素，根据DPI调整
    int 列宽像素 = (int)(76 * scaleFactor); // 原始76像素，根据DPI调整

    for (int i = 0; i < 表格.RowCount; i++)
    {
        表格.RowStyles.Add(new RowStyle(SizeType.Absolute, 行高像素));
    }

    for (int j = 0; j < 表格.ColumnCount; j++)
    {
        表格.ColumnStyles.Add(new ColumnStyle(SizeType.Absolute, 列宽像素));
    }


    //表格.CellBorderStyle = TableLayoutPanelCellBorderStyle.Single;// 显示框线

    // 键位设置
    表格.Controls.Add(文本标题键位, 0, 0);
    表格.SetColumnSpan(文本标题键位, 2);

    表格.Controls.Add(文本提示1, 3, 0);
    表格.SetColumnSpan(文本提示1, 2);
    //表格.Controls.Add(文本提示2, 2, 3);
    //表格.SetColumnSpan(文本提示2, 2);

    表格.Controls.Add(文本强心剂, 0, 1);
    表格.SetColumnSpan(文本强心剂, 2);
    表格.Controls.Add(文本确定, 0, 2);
    表格.SetColumnSpan(文本确定, 2);

    表格.Controls.Add(输入录制1, 3, 1);
    表格.SetColumnSpan(输入录制1, 2);
    表格.Controls.Add(输入录制2, 3, 2);
    表格.SetColumnSpan(输入录制2, 2);

    //表格.Controls.Add(按钮录制1, 4, 1);
    //表格.Controls.Add(按钮录制2, 4, 2);

    // 工具功能
    表格.Controls.Add(文本标题功能, 0, 3);
    表格.SetColumnSpan(文本标题功能, 2);
    表格.Controls.Add(文本语言, 2, 3);
    表格.Controls.Add(文本TTS报竿, 0, 4);
    表格.SetColumnSpan(文本TTS报竿, 3);
    表格.Controls.Add(文本竿时, 0, 5);
    表格.SetColumnSpan(文本竿时, 3);
    表格.Controls.Add(文本采集力, 0, 6);
    表格.SetColumnSpan(文本采集力, 3);
    表格.Controls.Add(文本字号, 0, 7);
    表格.SetColumnSpan(文本字号, 2);

    表格.Controls.Add(下拉语言, 3, 3);
    表格.SetColumnSpan(下拉语言, 2);
    表格.Controls.Add(下拉TTS报竿, 3, 4);
    表格.SetColumnSpan(下拉TTS报竿, 2);
    表格.Controls.Add(下拉竿时, 3, 5);
    表格.SetColumnSpan(下拉竿时, 2);
