    表格.SetColumnSpan(文本鱼王竿, 3);

    表格.Controls.Add(输入轻竿, 3, 17);
    表格.SetColumnSpan(输入轻竿, 2);
    表格.Controls.Add(输入中竿, 3, 18);
    表格.SetColumnSpan(输入中竿, 2);
    表格.Controls.Add(输入鱼王竿, 3, 19);
    表格.SetColumnSpan(输入鱼王竿, 2);

    表格.Controls.Add(下拉自定义报竿, 3, 16);
    表格.SetColumnSpan(下拉自定义报竿, 2);

    表格.Controls.Add(按钮保存, 1, 20);
    表格.SetColumnSpan(按钮保存, 3);
    表格.SetRowSpan(按钮保存, 2);
}

void 设置控件样式()
{
    // 主表单
    foreach (Control 控件 in 表格.Controls)
    {
        // 整体设置
        控件.Font = 字体;
        控件.ForeColor = Color.White;
        控件.AutoSize = true;
        控件.Dock = DockStyle.Fill;

        按钮录制1.Font = 加粗;
        按钮录制2.Font = 加粗;
        按钮保存.Font = 加粗;

        文本标题键位.Font = 加粗;
        文本标题自动.Font = 加粗;
        文本标题功能.Font = 加粗;
        文本标题播报.Font = 加粗;
        文本标题自定义报竿.Font = 加粗;


        if (控件 is Label 标签)
        {
            标签.TextAlign = ContentAlignment.MiddleLeft;
            标签.ForeColor = Color.FromArgb(230, 218, 193);
        }
        文本标题键位.ForeColor = Color.FromArgb(196, 172, 123);
        文本标题自动.ForeColor = Color.FromArgb(196, 172, 123);
        文本标题功能.ForeColor = Color.FromArgb(196, 172, 123);
        文本标题播报.ForeColor = Color.FromArgb(196, 172, 123);
        文本标题自定义报竿.ForeColor = Color.FromArgb(196, 172, 123);
        //文本悬浮窗.ForeColor = Color.FromArgb(196, 197, 201);
        文本提示1.TextAlign = ContentAlignment.MiddleRight;
        //文本提示1.ForeColor = Color.FromArgb(196, 197, 201);
        文本调试.TextAlign = ContentAlignment.MiddleRight;
        //文本调试.ForeColor = Color.FromArgb(196, 197, 201);
        文本提示3.ForeColor = Color.FromArgb(196, 197, 201);
        文本恢复.ForeColor = Color.FromArgb(196, 197, 201);
        文本语言.TextAlign = ContentAlignment.MiddleRight;


        if (控件 is Button 按钮)
        {
            按钮.FlatStyle = FlatStyle.Flat;
            按钮.BackColor = Color.FromArgb(50, 81, 155); // 设置背景色
            按钮.FlatAppearance.BorderColor = Color.FromArgb(19, 33, 68); // 设置边框颜色
        }

        if (控件 is TextBox 输入框)
        {
            输入框.Font = 加粗;
            输入框.BorderStyle = BorderStyle.FixedSingle; // 设置边框样式
            输入框.BackColor = Color.FromArgb(49, 67, 103); // 文本颜色背景
        }
        输入录制1.Font = 字体;
        输入录制1.BorderStyle = BorderStyle.FixedSingle;
        输入录制1.BackColor = Color.FromArgb(21, 35, 71);// 设置背景色
        输入录制2.Font = 字体;
        输入录制2.BorderStyle = BorderStyle.FixedSingle;
        输入录制2.BackColor = Color.FromArgb(21, 35, 71);// 设置背景色

        输入轻竿.Font = 字体;
        输入中竿.Font = 字体;
        输入鱼王竿.Font = 字体;

        if (控件 is CheckBox 勾选框)
        {
            勾选框.ForeColor = Color.FromArgb(234, 222, 195);
        }
        //勾选调试.ForeColor = Color.FromArgb(196, 197, 201);

        if (控件 is ComboBox 下拉框)
        {
            下拉框.SelectedIndex = 0;
            下拉框.DropDownStyle = ComboBoxStyle.DropDownList;
            下拉框.DrawMode = DrawMode.OwnerDrawFixed;
            下拉框.BackColor = Color.FromArgb(41, 60, 107);// 设置背景色 41, 60, 107 或 71, 96, 150
            下拉框.ForeColor = Color.FromArgb(234, 222, 195); // 设置前景色
            更新下拉前缀(下拉框, "▶");// ▶ 
            下拉框.DrawItem += new DrawItemEventHandler(绘制下拉框);
        }
    }

    RealPlugin.plug.InvokeNamedCallback("command", 默语 + "正在读取配置……");
}

void 绘制下拉框(object sender, DrawItemEventArgs e)
{
    // 绘制背景
    e.Graphics.FillRectangle(new SolidBrush(e.BackColor), e.Bounds);

    // 创建 StringFormat 对象并设置对齐方式
    StringFormat stringFormat = new StringFormat
    {
        Alignment = StringAlignment.Near,
        LineAlignment = StringAlignment.Center
    };

    // 绘制文本
    e.Graphics.DrawString(((ComboBox)sender).Items[e.Index].ToString(), e.Font, new SolidBrush(e.ForeColor), e.Bounds.X, e.Bounds.Y);

    // 绘制边框
    using (Pen borderPen = new Pen(Color.FromArgb(41, 60, 107))) // 边框颜色 或 41, 60, 107 
    {
        e.Graphics.DrawRectangle(borderPen, e.Bounds.X, e.Bounds.Y, e.Bounds.Width - 1, e.Bounds.Height - 1);
    }
}

void 更新下拉前缀(ComboBox comboBox, string prefix)
{
    int selectedIndex = comboBox.SelectedIndex; // 记录当前选中的索引

    List<string> newItems = new List<string>(); // 创建一个新的列表以存储带有前缀的项

    foreach (var item in comboBox.Items)
    {
        if (item is string strItem)
        {
            newItems.Add($"{prefix}{strItem}"); // 遍历当前的项，添加前缀，并添加到新的列表中
        }
    }

    comboBox.Items.Clear(); // 清空ComboBox的Items集合

    comboBox.Items.AddRange(newItems.ToArray()); // 将带有前缀的新项添加回ComboBox

    if (selectedIndex >= 0 && selectedIndex < comboBox.Items.Count)
    {
        comboBox.SelectedIndex = selectedIndex; // 恢复ComboBox的SelectedIndex
    }
    else
    {
        comboBox.SelectedIndex = 0; // 如果原来的selectedIndex超出范围，则选择第一个项
    }
}

void 读取配置(object sender, EventArgs e)
{
    if (!string.IsNullOrEmpty(强心剂热键))
    {
        输入录制1.Text = 强心剂热键;
    }
    if (!string.IsNullOrEmpty(确定热键))
    {
        输入录制2.Text = 确定热键;
    }
    if (int.TryParse(语言, out var lingo) && lingo >= 2 && lingo <= 3)
    {
        下拉语言.SelectedIndex = lingo - 1;
    }
    if (TTS报竿 == "0")
    {
        下拉TTS报竿.SelectedIndex = 1;
    }
    if (竿时悬浮窗 == "0")
    {
        下拉竿时.SelectedIndex = 1;
    }
    if (禁用采集力悬浮窗 == "1")
    {
        下拉采集力.SelectedIndex = 1;
    }
    if (int.TryParse(悬浮窗字号, out var fontSize) && fontSize >= 1 && fontSize <= 4)
    {
        下拉字号.SelectedIndex = fontSize; 
    }
    if (string.IsNullOrEmpty(调试模式) || 调试模式 == "0")
    {
        勾选调试.Checked = false; 
    }
        switch (公共频道)
    {
        case "p": 下拉触幻.SelectedIndex = 1; break;
        case "y": 下拉触幻.SelectedIndex = 2; break;
        case "sh": 下拉触幻.SelectedIndex = 3; break;
    }
    switch (幻海频道)
    {
        case "p": 下拉时长.SelectedIndex = 1; break;
        case "y": 下拉时长.SelectedIndex = 2; break;
        case "sh": 下拉时长.SelectedIndex = 3; break;
    }
    switch (成就频道)
    {
        case "p": 下拉成就.SelectedIndex = 1; break;
        case "y": 下拉成就.SelectedIndex = 2; break;
        case "sh": 下拉成就.SelectedIndex = 3; break;
    }
    if (关闭报竿 == "0")
    {
        下拉关闭报竿.SelectedIndex = 1;
    }
    if (关闭系统音 == "0")
    {
        下拉系统音.SelectedIndex = 1;
    }
    if (int.TryParse(系统音, out var intValue) && intValue >= 0 && intValue <= 100)
    {
        输入恢复.Text = intValue.ToString();
    }
    if (自定义报竿 == "1")
    {
        下拉自定义报竿.SelectedIndex = 1;
    }
    if (!string.IsNullOrEmpty(轻竿路径))
    {
        输入轻竿.Text = 轻竿路径;
    }
    if (!string.IsNullOrEmpty(中竿路径))
    {
        输入中竿.Text = 中竿路径;
    }
    if (!string.IsNullOrEmpty(鱼王竿路径))
    {
        输入鱼王竿.Text = 鱼王竿路径;
    }
    RealPlugin.plug.InvokeNamedCallback("command", 默语 + "配置读取成功！<se.9>");
}

void 保存配置(object sender, EventArgs e)
{

    string 提示竿型内容 = "";
    if (!string.IsNullOrEmpty(输入轻竿.Text) ||
        !string.IsNullOrEmpty(输入中竿.Text) ||
          !string.IsNullOrEmpty(输入鱼王竿.Text))
    {
        if (!string.IsNullOrEmpty(输入轻竿.Text) &&
            !(输入轻竿.Text.EndsWith(".mp3", StringComparison.OrdinalIgnoreCase) ||
              输入轻竿.Text.EndsWith(".wav", StringComparison.OrdinalIgnoreCase)))
        {
            提示竿型内容 += "轻竿路径应为 .mp3 或 .wav 格式。\n";
        }
        if (!string.IsNullOrEmpty(输入中竿.Text) &&
            !(输入中竿.Text.EndsWith(".mp3", StringComparison.OrdinalIgnoreCase) ||
              输入中竿.Text.EndsWith(".wav", StringComparison.OrdinalIgnoreCase)))
        {
            提示竿型内容 += "中竿路径应为 .mp3 或 .wav 格式。\n";
        }
        if (!string.IsNullOrEmpty(输入鱼王竿.Text) &&
            !(输入鱼王竿.Text.EndsWith(".mp3", StringComparison.OrdinalIgnoreCase) ||
              输入鱼王竿.Text.EndsWith(".wav", StringComparison.OrdinalIgnoreCase)))
        {
            提示竿型内容 += "鱼王竿路径应为 .mp3 或 .wav 格式。\n";
        }
        if (string.IsNullOrEmpty(输入轻竿.Text) ||
             string.IsNullOrEmpty(输入中竿.Text) ||
             string.IsNullOrEmpty(输入鱼王竿.Text))
        {
            提示竿型内容 += "竿型路径不能只填写一部分，要么全部填写，要么全部留空。\n";
        }
    }

    if (检查数字(输入录制1.Text) && string.IsNullOrEmpty(提示竿型内容))
    {
        字典.SetValue("强心剂热键", 输入录制1.Text);
        if (检查数字(输入录制2.Text))
        {
            字典.SetValue("确定热键", 输入录制2.Text);
        }
        else
        {
            字典.SetValue("确定热键", "");
        }
        字典.SetValue("语言", 下拉语言.SelectedIndex + 1 );
        字典.SetValue("TTS报竿", 下拉TTS报竿.SelectedIndex == 0 ? 1 : 0);
        字典.SetValue("竿时悬浮窗", 下拉竿时.SelectedIndex == 0 ? 1 : 0);
        字典.SetValue("禁用采集力悬浮窗", 下拉采集力.SelectedIndex);
        字典.SetValue("悬浮窗字号", 下拉字号.SelectedIndex);
        if (勾选调试.Checked == true)
        { 字典.SetValue("调试模式", 1); }
        else { 字典.SetValue("调试模式", 0); }
        string chuHuan;
        switch (下拉触幻.SelectedIndex)
        {
            case 0: chuHuan = "e"; break;
            case 1: chuHuan = "p"; break;
            case 2: chuHuan = "y"; break;
            case 3: chuHuan = "sh"; break;
            default:
                chuHuan = "e";
                break;
        }
        字典.SetValue("公共频道", chuHuan);
        string huanHai;
        switch (下拉时长.SelectedIndex)
        {
            case 0: huanHai = "e"; break;
            case 1: huanHai = "p"; break;
            case 2: huanHai = "y"; break;
            case 3: huanHai = "sh"; break;
            default:
                huanHai = "e";
                break;
        }
        字典.SetValue("幻海频道", huanHai); 
        string chengJiu;
        switch (下拉成就.SelectedIndex)
        {
            case 0: chengJiu = "e"; break;
            case 1: chengJiu = "p"; break;
            case 2: chengJiu = "y"; break;
            case 3: chengJiu = "sh"; break;
            default:
                chengJiu = "e";
                break;
        }
        字典.SetValue("成就频道", chengJiu);

        字典.SetValue("关闭报竿", 下拉关闭报竿.SelectedIndex == 0 ? 1 : 0);
        字典.SetValue("关闭系统音", 下拉系统音.SelectedIndex == 0 ? 1 : 0);
        if (检查数字(输入恢复.Text))
        {
            字典.SetValue("系统音", 输入恢复.Text);
        }
        if (下拉自定义报竿.SelectedIndex == 1 && !string.IsNullOrEmpty(轻竿路径) && !string.IsNullOrEmpty(中竿路径) && !string.IsNullOrEmpty(鱼王竿路径))
        {
            字典.SetValue("自定义报竿", 下拉自定义报竿.SelectedIndex);
        }
        else{字典.SetValue("自定义报竿", 0);}
        字典.SetValue("轻竿路径", 输入轻竿.Text);
        字典.SetValue("中竿路径", 输入中竿.Text);
        字典.SetValue("鱼王竿路径", 输入鱼王竿.Text);

        if (string.IsNullOrEmpty(空天姬控制强心剂))
        {
            StaticHelpers.SetScalarVariable(false, "空天姬控制强心剂", "1");
        }

        StaticHelpers.SetDictVariable(true, "空天姬配置", 字典);

        //RealPlugin.plug.LogLineQueuer("空天姬配置", "", LogEvent.SourceEnum.Log);
        RealPlugin.plug.InvokeNamedCallback("command", 默语 + "[空天姬 MissFisher] 配置已保存。<se.10>");
        窗口.Close();
    }
    else
    {
        if (!检查数字(输入录制1.Text))
        {

            MessageBox.Show(
                "请录制强心剂热键！",
                "空天姬",
                MessageBoxButtons.OK,
                MessageBoxIcon.Error
            );
        }
        else
        {
            MessageBox.Show(
                提示竿型内容,
                "空天姬",
                MessageBoxButtons.OK,
                MessageBoxIcon.Error
            );
        }
    }
}

void 切换调试(object sender, EventArgs e)
{
    勾选调试.Checked = !勾选调试.Checked;
}

void 开始监听(TextBox recordingInput)
{
    recordingInput.Text = "点击这里后输入";
}

void 按键录制(KeyEventArgs e, TextBox recordingInput)
{
    if (e.Control || e.Alt || e.Shift)
    {
        recordingInput.Text = "不接受组合键";
    }
    else
    {
        if (e.KeyCode == Keys.Back || e.KeyCode == Keys.Escape)
        {
            recordingInput.Text = "";
        }
        else
        {
            recordingInput.Text = e.KeyValue.ToString();
        }
    }
}

void 录制1(object sender, KeyEventArgs e)
{
    按键录制(e, 输入录制1);
}

void 录制2(object sender, KeyEventArgs e)
{
    按键录制(e, 输入录制2);
}

