void 设置控件关系()
{
    // 添加控件关系：主表单
    窗口.Controls.Add(表格);

    表格.ColumnCount = 4;// 列
    表格.RowCount = 7;// 行

    // DPI 适配
    Graphics graphics = Graphics.FromHwnd(IntPtr.Zero);
    float dpiX = graphics.DpiX;
    float dpiY = graphics.DpiY;
    graphics.Dispose();

    float scaleFactor = dpiY / 96f; 

    int 行高像素 = (int)(38 * scaleFactor); 
    int 列宽像素 = (int)(76 * scaleFactor); 

    for (int i = 0; i < 表格.RowCount; i++)
    {
        表格.RowStyles.Add(new RowStyle(SizeType.Absolute, 行高像素));
    }

    for (int j = 0; j < 表格.ColumnCount; j++)
    {
        表格.ColumnStyles.Add(new ColumnStyle(SizeType.Absolute, 列宽像素));
    }


    //表格.CellBorderStyle = TableLayoutPanelCellBorderStyle.Single;// 显示框线

    表格.Controls.Add(按钮海钓, 0, 3);
    表格.SetRowSpan(按钮海钓, 2);
    表格.SetColumnSpan(按钮海钓, 2);
    表格.Controls.Add(下拉海钓, 2, 3);
    表格.SetColumnSpan(下拉海钓, 2);

    表格.Controls.Add(按钮配置, 3, 1);
    表格.Controls.Add(按钮同步, 3, 0);
    表格.Controls.Add(按钮航线, 3, 2);

    表格.Controls.Add(输入鸣谢, 0, 0);
    表格.SetColumnSpan(输入鸣谢, 3);
    表格.SetRowSpan(输入鸣谢, 3);

    表格.Controls.Add(输入鱼名, 2, 4);
    表格.SetColumnSpan(输入鱼名, 2);

    表格.Controls.Add(文本诗句, 0, 5);
    表格.SetColumnSpan(文本诗句, 4);
    表格.SetRowSpan(文本诗句, 2);


}

void 设置控件样式()
{
    // 主表单
    foreach (Control 控件 in 表格.Controls)
    {
        // 整体设置
        控件.Font = 字体;
        控件.ForeColor = Color.White;
        控件.AutoSize = true;
        控件.Dock = DockStyle.Fill;

        if (控件 is Label 标签)
        {
            标签.Font = 加粗;
            标签.ForeColor = Color.FromArgb(196, 172, 123);// 230, 218, 193
            标签.TextAlign = ContentAlignment.MiddleCenter;
        }
        文本签名.Font = 小号加粗;
        文本签名.ForeColor = Color.FromArgb(196, 197, 201); // 196, 197, 201
        文本签名.TextAlign = ContentAlignment.BottomRight;
        文本诗句.Font = 楷体;
        文本诗句.TextAlign = ContentAlignment.MiddleCenter;

        if (控件 is Button 按钮)
        {
            按钮.Font = 加粗;
            按钮.FlatStyle = FlatStyle.Flat;
            按钮.BackColor = Color.FromArgb(50, 81, 155); 
            按钮.FlatAppearance.BorderColor = Color.FromArgb(19, 33, 68); 
        }
        按钮配置.BackColor = 红色;
        按钮配置.ForeColor = 黄色;
        按钮同步.BackColor = 蓝色;
        按钮同步.ForeColor = 黄色;
        按钮航线.BackColor = 灰色;
        按钮航线.ForeColor = 深灰;

        if (控件 is TextBox 输入框)
        {
            输入框.Font = 加粗;
            输入框.BorderStyle = BorderStyle.FixedSingle; 
            输入框.BackColor = Color.FromArgb(49, 67, 103); 
        }
        输入鸣谢.Font = 小号加粗;
        输入鸣谢.ForeColor = Color.FromArgb(196, 172, 123);
        输入鸣谢.BackColor = Color.FromArgb(21, 35, 71);
        输入鱼名.Font = 字体;
        输入鱼名.ForeColor = 灰色;

        if (控件 is CheckBox 勾选框)
        {
            勾选框.ForeColor = Color.FromArgb(234, 222, 195);
        }

        if (控件 is ComboBox 下拉框)
        {
            下拉框.DropDownStyle = ComboBoxStyle.DropDownList;
            下拉框.DrawMode = DrawMode.OwnerDrawFixed;
            下拉框.SelectedIndex = 0;
            下拉框.BackColor = Color.FromArgb(41, 60, 107);// 41, 60, 107 或 71, 96, 150
            下拉框.ForeColor = Color.FromArgb(234, 222, 195); 
            添加下拉前缀(下拉框, "▶");// ▶ 
            下拉框.DrawItem += new DrawItemEventHandler(绘制下拉框);
        }
    }

    StaticHelpers.SetScalarVariable(false, "空天姬控制窗口I", "1");
    RealPlugin.plug.InvokeNamedCallback("command", "/e 窗口加载成功！<se.9>");
}

void 绘制下拉框(object sender, DrawItemEventArgs e)
{
    e.Graphics.FillRectangle(new SolidBrush(e.BackColor), e.Bounds);
    StringFormat stringFormat = new StringFormat
    {
        Alignment = StringAlignment.Near,
        LineAlignment = StringAlignment.Center
    };

    e.Graphics.DrawString(((ComboBox)sender).Items[e.Index].ToString(), e.Font, new SolidBrush(e.ForeColor), e.Bounds.X, e.Bounds.Y);

    using (Pen borderPen = new Pen(Color.FromArgb(41, 60, 107))) 
    {
        e.Graphics.DrawRectangle(borderPen, e.Bounds.X, e.Bounds.Y, e.Bounds.Width - 1, e.Bounds.Height - 1);
    }
}

void 添加下拉前缀(ComboBox comboBox, string prefix)
{
    int selectedIndex = comboBox.SelectedIndex; 

    List<string> newItems = new List<string>(); 

    foreach (var item in comboBox.Items)
    {
        if (item is string strItem)
        {
            newItems.Add($"{prefix}{strItem}"); 
        }
    }

    comboBox.Items.Clear(); 

    comboBox.Items.AddRange(newItems.ToArray()); 

    if (selectedIndex >= 0 && selectedIndex < comboBox.Items.Count)
    {
        comboBox.SelectedIndex = selectedIndex; 
    }
    else
    {
        comboBox.SelectedIndex = 0; 
    }
}

void 更新按钮文本(object sender, EventArgs e)
{
    string 海钓选项 = 下拉海钓.SelectedItem.ToString();
    if (海钓选项.StartsWith("▶"))
    {
        海钓选项 = 海钓选项.Substring(1); 
    }
    按钮海钓.Text = "启动" + 海钓选项;
}

void 输入海钓(object sender, EventArgs e)
{
    string 海钓选项 = 下拉海钓.SelectedItem.ToString();
    if (海钓选项.StartsWith("▶"))
    {
        海钓选项 = 海钓选项.Substring(1); 
    }
    RealPlugin.plug.InvokeNamedCallback("command", 默语 + 海钓选项 + "确定");
}

void 输入鱼名_KeyDown(object sender, KeyEventArgs e)
{
    if (e.KeyCode == Keys.Enter)
    {
        输入预设(sender, EventArgs.Empty);
    }
}

void 更新颜色(object sender, EventArgs e)
{
    if (!输入鱼名.Text.StartsWith("输"))
    {
        if (预设.Contains(输入鱼名.Text))
        {
            输入鱼名.Font = 字体;
            输入鱼名.ForeColor = Color.FromArgb(196, 172, 123);
            输入鱼名.BackColor = Color.FromArgb(21, 35, 71);// 深色
            if (文本诗句.Text != "双 击 或 回 车\r\n启 动 预 设")
            {
                延时更新("双 击 或 回 车\r\n启 动 预 设", 10000).ConfigureAwait(false);
            }
        }
        else
        {
            输入鱼名.Font = 字体;
            输入鱼名.ForeColor = Color.FromArgb(196, 172, 123);
            输入鱼名.BackColor = Color.FromArgb(49, 67, 103); // 浅色
        }
    }
    else
    {
        输入鱼名.Font = 字体;
        输入鱼名.BackColor = Color.FromArgb(49, 67, 103); // 浅色
        输入鱼名.ForeColor = 灰色;// 230, 218, 193
    }
}
void 清空(object sender, EventArgs e)
{
    if (输入鱼名.Text.StartsWith("输"))
    {
        输入鱼名.Text = "";
    }
}

void 提示(object sender, EventArgs e)
{
    if (输入鱼名.Text == "")
    {
        输入鱼名.Text = 输入提示;
        输入鱼名.SelectionLength = 0;
        输入鱼名.SelectionStart = 输入鱼名.Text.Length;
    }
}

void 输入预设(object sender, EventArgs e)
{
    if (!输入鱼名.Text.StartsWith("输") && !string.IsNullOrEmpty(输入鱼名.Text))
    {
        if (预设.Contains(输入鱼名.Text))
        {
            RealPlugin.plug.InvokeNamedCallback("command", 默语 + "空天姬预设 " + 输入鱼名.Text);
            延时更新("已 发 送 至 默 语 频 道", 3000).ConfigureAwait(false);
            输入鱼名.SelectionLength = 0;
            输入鱼名.SelectionStart = 输入鱼名.Text.Length;
        }
        else
        {
            延时更新("未 支 持 此 预 设\r\n暂只支持 2.0 鱼王与宠物", 3000).ConfigureAwait(false);
            输入鱼名.SelectionLength = 0;
            输入鱼名.SelectionStart = 输入鱼名.Text.Length;
        }
    }
    else
    {
        延时更新("请 输 入 鱼 名", 1500).ConfigureAwait(false);
    }
}

void 输入航线(object sender, EventArgs e)
{
    int pid = BridgeFFXIV.GetProcess()?.Id ?? 0;

    if (pid != 0)
    {
        RealPlugin.plug.InvokeNamedCallback("command", 默语 + "航线");
        延时更新("已 发 送 至 默 语 频 道", 3000).ConfigureAwait(false);
    }
    else
    {
        MessageBox.Show(
            "没有找到游戏进程",
            "空天姬",
            MessageBoxButtons.OK,
            MessageBoxIcon.Error
        );
    }
}

async Task 延时更新(string 文本内容, int 延迟毫秒数)
{
    文本诗句.Text = 文本内容;
    await Task.Delay(延迟毫秒数);
    if (!所有诗句.Contains(文本诗句.Text))
    {
        更新诗句();
    }
}

void 复制爱发电(object sender, EventArgs e)
{
    输入鸣谢.SelectionLength = 0;
    延时更新复制("已 复 制 爱 发 电 链 接", 3000, "https://afdian.com/a/bcloli").ConfigureAwait(false);
}

void 复制仓库(object sender, EventArgs e)
{
    延时更新复制("已 复 制 仓 库 链 接", 3000, "https://github.com/BlackCleaverLoli/MissFisher").ConfigureAwait(false);
}

async Task 延时更新复制(string 文本内容, int 延迟毫秒数, string 链接)
{
    await Task.Run(() =>
    {
        Triggernometry.Action.ClipboardSetText(链接);
    });

    文本诗句.Text = 文本内容;
    await Task.Delay(延迟毫秒数);

    if (!所有诗句.Contains(文本诗句.Text))
    {
        更新诗句();
    }
}

void 输入生成器(object sender, EventArgs e)
{
    int pid = BridgeFFXIV.GetProcess()?.Id ?? 0;

    if (pid != 0)
    {
        RealPlugin.plug.InvokeNamedCallback("command", 默语 + "空天姬同步");
    }
    else
    {
        MessageBox.Show(
            "没有找到游戏进程\r\n" +
            "或请手动执行触发器内的“同步内置预设”",
            "空天姬",
            MessageBoxButtons.OK,
            MessageBoxIcon.Error
        );
    }
}

void 输入配置(object sender, EventArgs e)
{
    int pid = BridgeFFXIV.GetProcess()?.Id ?? 0;

    if (pid != 0)
    {
        RealPlugin.plug.InvokeNamedCallback("command", 默语 + "空天姬配置");
    }
    else
    {
        MessageBox.Show(
            "没有找到游戏进程\r\n" +
            "或请手动执行触发器内的“用户配置”",
            "空天姬",
            MessageBoxButtons.OK,
            MessageBoxIcon.Error
        );
    }
}

void 更新诗句()
{
    List<string> 可选诗句 = new List<string>(所有诗句);
    foreach (var 诗 in 已显示诗句)
    {
        可选诗句.Remove(诗);
    }

    if (可选诗句.Count > 0)
    {
        int 随机索引 = 随机数.Next(可选诗句.Count);
        string 新诗句 = 可选诗句[随机索引];
        已显示诗句.Add(新诗句);
        文本诗句.Text = 新诗句;
    }
    else
    {
        已显示诗句.Clear(); 
        更新诗句(); 
    }
}

void 全选(object sender, MouseEventArgs e)
{
    if (e.Button == MouseButtons.Left)
    {
        if (sender is TextBox textBox && e.Button == MouseButtons.Left)
        {
            textBox.SelectAll();
        }
    }
}

