#region 控件事件
窗口.Shown += (sender, e) =>
{
    ((Form)sender).TopMost = true; // 在显示时设置为最前端
    System.Threading.Thread.Sleep(10); // 等待一小段时间以确保窗口已置于顶层
    ((Form)sender).TopMost = false; // 然后取消置顶状态
};
