                if (勾选鱼王杆212.Checked) { if (勾选中杆212.Checked) { 命令 += "+"; } 命令 += "！！！"; }

                if (下拉技巧212.SelectedItem?.ToString() != "特殊")
                {
                    命令 += 下拉技巧212.SelectedItem?.ToString() ?? "";
                }

                命令 += 输入鱼名211.Text;
            }

            // 鱼识第一阶段
            命令 += (!string.IsNullOrEmpty(输入撒饵211.Text) ? 输入撒饵211.Text + "+" : "");
            命令 += (!string.IsNullOrEmpty(输入竿时211.Text) ? 输入竿时211.Text + "~" : "");
            命令 += (!string.IsNullOrEmpty(输入撒饵212.Text) ? "~" + 输入撒饵212.Text + "+" : "");
            命令 += (!string.IsNullOrEmpty(输入竿时212.Text) ? "~" + 输入竿时212.Text : "");

            if (勾选轻杆211.Checked) { 命令 += "！"; }
            if (勾选中杆211.Checked) { if (勾选轻杆211.Checked) { 命令 += "+"; } 命令 += "！！"; }
            if (勾选鱼王杆211.Checked) { if (勾选中杆211.Checked) { 命令 += "+"; } 命令 += "！！！"; }

