                {
                    命令 += (!string.IsNullOrEmpty(输入竿时221.Text) ? 输入竿时221.Text + "~" : "");
                    命令 += (!string.IsNullOrEmpty(输入竿时222.Text) ? "~" + 输入竿时222.Text : "");
