                        if (勾选中杆241.Checked) { if (勾选轻杆241.Checked) { 命令 += "+"; } 命令 += "！！"; }
                        if (勾选鱼王杆241.Checked) { if (勾选轻杆241.Checked || 勾选中杆241.Checked) { 命令 += "+"; } 命令 += "！！！"; }

                        if (下拉技巧241.SelectedItem?.ToString() != "特殊")
                        {
                            命令 += 下拉技巧241.SelectedItem?.ToString() ?? "";
                        }
                    }
                }
            }

            // 鱼识最终目标参数
            if (正则验证ET.IsMatch(输入ET2.Text)) { 命令 += 输入ET2.Text; }

            if (!string.IsNullOrEmpty(输入目标2.Text) ||
                勾选收藏2.Checked || 勾选不撒饵2.Checked )
            {
                命令 += "=";

                命令 += 输入目标2.Text;
                if (勾选收藏2.Checked) { 命令 += "收藏"; }
                if (勾选不撒饵2.Checked) { 命令 += "不撒饵"; }
            }
        }

        // 去除多余符号
        命令 = Regex.Replace(命令, @"~+", "~");
        命令 = Regex.Replace(命令, @"\+~", "+");
        命令 = Regex.Replace(命令, @"\++", "+");

        输入显示.Text = 命令;// 赋值
    }
    else if (!string.IsNullOrEmpty(输入计数11.Text) && int.TryParse(输入计数11.Text, out _) && !string.IsNullOrEmpty(输入计数12.Text) ||
             !string.IsNullOrEmpty(输入计数21.Text) && int.TryParse(输入计数21.Text, out _) && !string.IsNullOrEmpty(输入计数22.Text) ||
             !string.IsNullOrEmpty(输入计数31.Text) && int.TryParse(输入计数31.Text, out _) && !string.IsNullOrEmpty(输入计数32.Text))
    {
        命令 += "计数 ";

        if (int.TryParse(输入计数11.Text, out int 计数1) && !string.IsNullOrEmpty(输入计数12.Text)) // 尝试将输入计数11.Text转换为整数
        {
            命令 += 输入计数11.Text + 输入计数12.Text + "；";
        }
        if (int.TryParse(输入计数21.Text, out int 计数2) && !string.IsNullOrEmpty(输入计数22.Text))
        {
            命令 += 输入计数21.Text + 输入计数22.Text + "；";
        }
        if (int.TryParse(输入计数31.Text, out int 计数3) && !string.IsNullOrEmpty(输入计数32.Text))
        {
            命令 += 输入计数31.Text + 输入计数32.Text + "；";
        }
        输入显示.Text = 命令;// 赋值
    }
    else if (string.IsNullOrEmpty(命令))
    {
        输入显示.Text = "至少勾选阶段 I 的目标竿型";
    }
}

void 重置所有(object sender, EventArgs e)
{
    foreach (Control 控件 in 表格.Controls)
    {
        if (控件 is ComboBox comboBox)
        {
            comboBox.SelectedIndex = 0;
        }
        else if (控件 is TextBox textBox || 控件 is MaskedTextBox maskedTextBox)
        {
            控件.Text = ""; // 清空输入框内容
        }
        else if (控件 is CheckBox checkBox) 
        {
            checkBox.Checked = false; // 取消勾选
        }
        输入显示.Text = "至少勾选阶段 I 的目标竿型";
    }
}

void 复制显示(object sender, EventArgs e)
{
    if (输入显示.Text.StartsWith("至少"))
    {
        MessageBox.Show("至少勾选阶段 I 的目标竿型", "空天姬", MessageBoxButtons.OK, MessageBoxIcon.Error);
    }
    else
    {
        // 将文本复制到剪贴板
        Triggernometry.Action.ClipboardSetText(输入显示.Text);

        RealPlugin.plug.InvokeNamedCallback("command", 默语 + "<se.14>");
    }
}

void 输入参数(object sender, EventArgs e)
{
    if (输入显示.Text.StartsWith("至少"))
    {
        MessageBox.Show("至少勾选阶段 I 的目标竿型", "空天姬", MessageBoxButtons.OK, MessageBoxIcon.Error);
    }
    else
    {
        RealPlugin.plug.InvokeNamedCallback("command", 默语 + 输入显示.Text);
    }
}

void 输入停止(object sender, EventArgs e)
{
    RealPlugin.plug.InvokeNamedCallback("command", 默语 + "停止");
}

void 输入主界面(object sender, EventArgs e)
{
    RealPlugin.plug.InvokeNamedCallback("command", 默语 + "空天姬");
}

void 输入鱼计(object sender, EventArgs e)
{
    输入钓饵1.Text = "万能拟饵";
    勾选轻杆111.Checked = true;
    勾选中杆111.Checked = true;
    勾选鱼王杆111.Checked = true;
    勾选攒鱼计1.Checked = true;
}

