{
    输入钓饵1.Text = "万能拟饵";
    勾选轻杆111.Checked = true;
    勾选中杆111.Checked = true;
    输入撒饵111.Text = "6.4";
    输入竿时111.Text = "11.6";
    输入鱼名111.Text = "魇鱼";
