    输入竿时121.Text = "8.6";
    勾选收藏1.Checked = true;
}

void 输入报错(object sender, EventArgs e)
{
    RealPlugin.plug.InvokeNamedCallback("command", 默语 + "空天姬：功能未完成。<se.11>");
}

void 全选(object sender, MouseEventArgs e)
{
    if (e.Button == MouseButtons.Left)
    {
        if (sender is TextBox textBox && e.Button == MouseButtons.Left)
        {
            textBox.SelectAll();
        }
    }
}

