Action.ExecScriptExpression:
using System;
using static Triggernometry.Interpreter;
using static Triggernometry.Utilities.Memory;

static int WeatherID()
{
    byte[] moduleData = ReadModuleData(XivProc);
    int offset = ScanPoint(moduleData, "48 8D 0D * * * * 44 0F B7 45") ?? throw new Exception("Failed to find signature for Weather.");
    IntPtr address = XivBaseAddress + offset + 0x64;
    byte weather = Read<byte>(XivProcHandle, address);
    return weather;
}
StaticHelpers.SetScalarVariable(isPersistent: false, "空天姬天气ID", WeatherID());

