using System.Windows.Forms;
using Advanced_Combat_Tracker;

var plugin = ActGlobals.oFormActMain.ActPlugins
            .FirstOrDefault(x => x.pluginObj?.GetType().ToString() == "Cafe.Matcha.MatchaInit")
            ?.pluginObj;
        if (plugin == null)
        {
            MessageBox.Show("建议使用抹茶插件并勾选兼容模式可减少提钩延迟和性能消耗");
        }

