设置控件关系();
设置控件样式();
窗口.ShowDialog();

void 设置控件关系()
{
    // 添加控件关系：主表单
    窗口.Controls.Add(表格);

    表格.ColumnCount = 12;// 列
    表格.RowCount = 21;// 行
    // DPI 适配
    // 获取主屏幕的 DPI
    Graphics graphics = Graphics.FromHwnd(IntPtr.Zero);
    float dpiX = graphics.DpiX;
    float dpiY = graphics.DpiY;
    graphics.Dispose();

    // 计算缩放比例，这里以垂直DPI为例
    float scaleFactor = dpiY / 96f; // 基准DPI为96（100%缩放时的标准DPI）

    // 根据缩放比例调整像素值
    int 行高像素 = (int)(38 * scaleFactor); // 原始38像素，根据DPI调整
    int 列宽像素 = (int)(76 * scaleFactor); // 原始76像素，根据DPI调整

    for (int i = 0; i < 表格.RowCount; i++)
    {
        表格.RowStyles.Add(new RowStyle(SizeType.Absolute, 行高像素));
    }

    for (int j = 0; j < 表格.ColumnCount; j++)
    {
        表格.ColumnStyles.Add(new ColumnStyle(SizeType.Absolute, 列宽像素));
    }


    //表格.CellBorderStyle = TableLayoutPanelCellBorderStyle.Single;// 显示框线

    // 中控部分
    表格.Controls.Add(按钮启动, 4, 11);
    表格.SetRowSpan(按钮启动, 2); // 纵向占据 n 格
    表格.SetColumnSpan(按钮启动, 2); // 横向占据 n 格
    表格.Controls.Add(按钮重置, 6, 11);
    表格.SetColumnSpan(按钮重置, 2);
    表格.Controls.Add(按钮复制, 6, 12);
    表格.Controls.Add(按钮停止, 7, 12);

    表格.Controls.Add(按钮航线, 4, 1);
    表格.SetColumnSpan(按钮航线, 2);
    表格.Controls.Add(按钮冲分, 4, 2);
    表格.SetColumnSpan(按钮冲分, 2);
    表格.Controls.Add(按钮成就1, 4, 3);
    表格.Controls.Add(按钮成就2, 5, 3);
    表格.Controls.Add(按钮蓝鱼, 4, 4);
    表格.SetColumnSpan(按钮蓝鱼, 2);

    表格.Controls.Add(按钮调试, 6, 1);
    表格.SetColumnSpan(按钮调试, 2);
    表格.Controls.Add(按钮白票, 6, 2);
    表格.SetColumnSpan(按钮白票, 2);

    表格.Controls.Add(文本目标1, 4, 15);
    表格.SetColumnSpan(文本目标1, 2);
    表格.Controls.Add(文本ET1, 4, 17);
    表格.SetColumnSpan(文本ET1, 2);
    表格.Controls.Add(文本目标参数1, 5, 13);
    表格.Controls.Add(文本目标参数2, 6, 13);
    表格.Controls.Add(文本专一, 4, 7);
    表格.SetColumnSpan(文本专一, 2);
    表格.Controls.Add(文本提示2, 8, 7);// 提示
    表格.SetColumnSpan(文本提示2, 4);
    表格.Controls.Add(文本海钓, 4, 0);
    表格.SetColumnSpan(文本海钓, 2);
    表格.Controls.Add(文本其他, 6, 0);
    表格.SetColumnSpan(文本其他, 2);

    表格.Controls.Add(输入目标1, 4, 16);
    表格.SetColumnSpan(输入目标1, 2);
    表格.Controls.Add(输入ET1, 4, 18);
    表格.SetColumnSpan(输入ET1, 2);
    表格.Controls.Add(输入专一, 4, 8);

    表格.Controls.Add(下拉专一, 5, 8);

