void 设置控件样式()
{
    // 主表单
    foreach (Control 控件 in 表格.Controls)
    {
        // 整体设置
        控件.Font = 字体;
        控件.ForeColor = Color.White;
        控件.AutoSize = true;
        控件.Dock = DockStyle.Fill;

        按钮启动.Font = 加粗;

        文本阶段112.Font = 加粗;
        文本阶段212.Font = 加粗;

        if (控件 is Label 标签)
        {
            标签.TextAlign = ContentAlignment.MiddleCenter;
            标签.ForeColor = Color.FromArgb(230, 218, 193);
        }
        文本阶段112.ForeColor = Color.FromArgb(196, 172, 123);
        文本阶段113.ForeColor = Color.FromArgb(196, 172, 123);
        文本阶段212.ForeColor = Color.FromArgb(196, 172, 123);
        文本阶段213.ForeColor = Color.FromArgb(196, 172, 123);

        if (控件 is Button 按钮)
        {
            按钮.FlatStyle = FlatStyle.Flat;
            按钮.BackColor = Color.FromArgb(50, 81, 155); // 设置背景色
            按钮.FlatAppearance.BorderColor = Color.FromArgb(19, 33, 68); // 设置边框颜色
        }

        if (控件 is TextBox 输入框)
        {
            输入框.Font = 加粗;
            输入框.BorderStyle = BorderStyle.FixedSingle; // 设置边框样式
            输入框.BackColor = Color.FromArgb(49, 67, 103); // 文本颜色背景
        }
        输入显示.BorderStyle = BorderStyle.FixedSingle;
        输入显示.BackColor = Color.FromArgb(21, 35, 71);// 设置背景色

        if (控件 is CheckBox 勾选框)
        {
            勾选框.ForeColor = Color.FromArgb(234, 222, 195);
        }

        if (控件 is ComboBox 下拉框)
        {
            下拉框.DrawMode = DrawMode.OwnerDrawFixed;
            下拉框.BackColor = Color.FromArgb(41, 60, 107);// 设置背景色
            下拉框.ForeColor = Color.FromArgb(234, 222, 195); // 设置前景色
            下拉框.DrawItem += new DrawItemEventHandler(绘制下拉框);
        }
        下拉模式111.Font = 加粗;
        下拉模式211.Font = 加粗;

    }

}

void 绘制下拉框(object sender, DrawItemEventArgs e)
{
    e.Graphics.FillRectangle(new SolidBrush(e.BackColor), e.Bounds); // 绘制背景
    e.Graphics.DrawString(((ComboBox)sender).Items[e.Index].ToString(), e.Font, new SolidBrush(e.ForeColor), e.Bounds.X, e.Bounds.Y); // 绘制文本
    using (Pen borderPen = new Pen(Color.FromArgb(41, 60, 107))) // 边框颜色
    {
        e.Graphics.DrawRectangle(borderPen, e.Bounds.X, e.Bounds.Y, e.Bounds.Width - 1, e.Bounds.Height - 1);
    }
    // 如果需要绘制焦点矩形等，可以在这里添加
}

void 全选(object sender, MouseEventArgs e)
{
    if (e.Button == MouseButtons.Left)
    {
        if (sender is TextBox textBox && e.Button == MouseButtons.Left)
        {
            textBox.SelectAll();
        }
    }
}

void 更新显示(object sender, EventArgs e)
{
    string 命令 = "";

    // 通常参数
    if (勾选轻杆111.Checked || 勾选中杆111.Checked || 勾选鱼王杆111.Checked)
    {
        命令 += (下拉模式111.SelectedItem?.ToString() ?? "") +
            (!string.IsNullOrEmpty(输入钓饵1.Text) ? "" : " ");

        命令 += (!string.IsNullOrEmpty(输入钓饵1.Text) ? "（" + 输入钓饵1.Text + "）" : "");

        // 拍水
        if (!string.IsNullOrEmpty(输入鱼名111.Text))
        {
            命令 += "拍";

            if (勾选轻杆112.Checked || 勾选中杆112.Checked || 勾选鱼王杆112.Checked)
            {
                命令 += (!string.IsNullOrEmpty(输入撒饵113.Text) ? 输入撒饵113.Text + "+" : "");
                命令 += (!string.IsNullOrEmpty(输入竿时113.Text) ? 输入竿时113.Text + "~" : "");
                命令 += (!string.IsNullOrEmpty(输入撒饵114.Text) ? "~" + 输入撒饵114.Text + "+" : "");
                命令 += (!string.IsNullOrEmpty(输入竿时114.Text) ? "~" + 输入竿时114.Text : "");
            }
            if (勾选轻杆112.Checked) { 命令 += "！"; }
            if (勾选中杆112.Checked) { if (勾选轻杆112.Checked) { 命令 += "+"; } 命令 += "！！"; }
            if (勾选鱼王杆112.Checked) { if (勾选中杆112.Checked) { 命令 += "+"; } 命令 += "！！！"; }

            if (下拉技巧112.SelectedItem?.ToString() != "特殊")
            {
                命令 += 下拉技巧112.SelectedItem?.ToString() ?? "";
            }

            命令 += 输入鱼名111.Text;
        }

        // 第一阶段
        命令 += (!string.IsNullOrEmpty(输入撒饵111.Text) ? 输入撒饵111.Text + "+" : "");
        命令 += (!string.IsNullOrEmpty(输入竿时111.Text) ? 输入竿时111.Text + "~" : "");
        命令 += (!string.IsNullOrEmpty(输入撒饵112.Text) ? "~" + 输入撒饵112.Text + "+" : "");
        命令 += (!string.IsNullOrEmpty(输入竿时112.Text) ? "~" + 输入竿时112.Text : "");

        if (勾选轻杆111.Checked) { 命令 += "！"; }
        if (勾选中杆111.Checked) { if (勾选轻杆111.Checked) { 命令 += "+"; } 命令 += "！！"; }
        if (勾选鱼王杆111.Checked) { if (勾选中杆111.Checked) { 命令 += "+"; } 命令 += "！！！"; }

        if (下拉技巧111.SelectedItem?.ToString() != "特殊")
        {
            命令 += 下拉技巧111.SelectedItem?.ToString() ?? "";
        }

        // 第二阶段
        if (勾选轻杆121.Checked || 勾选中杆121.Checked || 勾选鱼王杆121.Checked)
        {
            命令 += 输入鱼名121.Text;
            命令 += "》";

            if (勾选轻杆121.Checked || 勾选中杆121.Checked || 勾选鱼王杆121.Checked)
            {
                命令 += (!string.IsNullOrEmpty(输入竿时121.Text) ? 输入竿时121.Text + "~" : "");
                命令 += (!string.IsNullOrEmpty(输入竿时122.Text) ? "~" + 输入竿时122.Text : "");
            }

            if (勾选轻杆121.Checked) { 命令 += "！"; }
            if (勾选中杆121.Checked) { if (勾选轻杆121.Checked) { 命令 += "+"; } 命令 += "！！"; }
            if (勾选鱼王杆121.Checked) { if (勾选轻杆121.Checked || 勾选中杆121.Checked) { 命令 += "+"; } 命令 += "！！！"; }

            if (下拉技巧121.SelectedItem?.ToString() != "特殊")
            {
                命令 += 下拉技巧121.SelectedItem?.ToString() ?? "";
            }

            // 第三阶段
